import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const t={},i=p('<p>\u95F2\u6765\u65E0\u4E8B\uFF0C\u7A81\u7136\u7FFB\u5230\u4E86\u53BB\u5E74\u627E\u5DE5\u4F5C\u65F6\u5199\u7684\u4E00\u4E9B\u5C0F\u5FC3\u60C5\uFF0C\u611F\u89C9\u86EE\u6709\u610F\u601D\uFF0C\u62FF\u51FA\u6765\u8A8A\u5199\u4E0E\u6B64\uFF0C\u4EE5\u4E3A\u7EAA\u5FF5\u3002</p><p>\u4ECA\u5929\u662F\u6765\u5230\u676D\u5DDE\u7684\u7B2C\u4E09\u5929\u4E86\uFF0C\u6B63\u5F0F\u9762\u8BD5\u7684\u7B2C\u4E8C\u5929\uFF0C\u4E00\u5929\u7684\u60C5\u51B5\u53EF\u4EE5\u8BF4\u975E\u5E38\u5706\u6EE1\u5145\u5B9E\u800C\u4E14\u8F9B\u82E6,\u65E9\u4E0A\u516B\u70B9\u949F\u51FA\u5BB6\u95E8,\u7136\u540E\u5728\u5916\u4E00\u5929\u7684\u5954\u6CE2,\u7ECF\u5386\u5404\u79CD\u4EBA\u60C5\u4E16\u6545,\u6700\u540E\u6447\u6447\u649E\u649E\u56DE\u5230\u5BB6,\u5DF2\u7136\u662F\u665A\u4E0A\u4E03\u70B9\u949F\u4E86\u3002</p><p>\u5148\u8BF4\u8BF4\u9762\u8BD5\u7684\u60C5\u51B5\uFF0C\u4E00\u5171\u9762\u4E86\u4E09\u5BB6\uFF0C\u5176\u5B9E\u539F\u672C\u5B89\u6392\u7684\u662F\u4E24\u5BB6\u7684\uFF0C\u8FD9\u6837\u4E0D\u4F1A\u90A3\u4E48\u8D76\uFF0C\u4E5F\u80FD\u4FDD\u8BC1\u66F4\u597D\u7684\u7CBE\u529B\uFF0C\u4F46\u662F\u4E2D\u5348\u53C8\u5728\u5916\u9762\u63A5\u5230\u7535\u8BDD\uFF0C\u8BF4\u6709\u5B89\u6392\uFF0C\u800C\u4E14\u5C31\u5728\u4E0B\u5348\uFF0C\u56E0\u6B64\u4E0A\u5348\u4E00\u573A\uFF0C\u4E0B\u5348\u4E24\u573A\uFF0C\u8FD8\u662F\u633A\u8D76\u7684\u3002</p><p><img src="http://t.eryajf.net/imgs/2021/09/c954e8d2c8e1cfd8.jpg" alt="image"></p><p>\u4E0A\u5348\u9762\u7684\u90A3\u5BB6\uFF0C\u53EF\u80FD\u662F\u6BD4\u8F83\u6B63\u89C4\u5427\uFF0C\u4F46\u662F\u8FD9\u91CC\u7684\u6B63\u89C4\u6709\u4E00\u70B9\u70B9\u7684\u6B63\u8BDD\u53CD\u8BF4\u7684\u610F\u601D\uFF0C\u5C31\u662F\u5148\u8BA9\u4EBA\u4E8B\u6765\u9762\u7684\uFF0C\u603B\u4E4B\u5C31\u662F\u4E00\u573A\u51E0\u4E4E\u6CA1\u6709\u4EC0\u4E48\u503C\u5F97\u8BF4\u7684\u9762\u8BD5\u3002\u4E5F\u8BA9\u6211\u611F\u89C9\uFF0C\u90A3\u79CD\u5148\u8BA9\u4EBA\u4E8B\u804A\u7684\uFF0C\u90FD\u662F\u778E\u626F\u6DE1\uFF0C\u5BF9\u4E8E\u516C\u53F8\u6765\u8BF4\uFF0C\u56E0\u4E3A\u4EBA\u4E8B\u521D\u59CB\u9762\u8BD5\u57FA\u672C\u4E0A\u4E5F\u90FD\u662F\u6CA1\u6709\u4EC0\u4E48\u76EE\u7684\u6027\u7684\uFF0C\u7136\u540E\u95EE\u4E9B\u4E0D\u75BC\u4E0D\u75D2\u7684\u95EE\u9898\uFF0C\u57FA\u672C\u4E0A\u8DDF\u6280\u672F\u6CA1\u5565\u5173\u7CFB\uFF0C\u4F60\u5728\u56DE\u7B54\u7684\u65F6\u5019\u5462\uFF0C\u53EF\u80FD\u7A0D\u5FAE\u6709\u4E00\u70B9\u70B9\u5B9E\u5728\uFF0C\u5C31\u4F1A\u88AB\u5BF9\u65B9\u5047\u60F3\u6210\u6CA1\u81EA\u4FE1\uFF0C\u7136\u540E\u6700\u540E\u53EF\u80FD\u4ECE\u4ED6\u8FD9\u5C31\u628A\u4F60\u7ED9\u5426\u4E86\uFF0C\u4F60\u53BB\u9762\u6280\u672F\u5DE5\u4F5C\u7684\uFF0C\u7ED3\u679C\u8FDE\u6280\u672F\u7684\u8FB9\u90FD\u6CA1\u6CBE\u5230\u3002\u6240\u4EE5\u611F\u89C9\u51E1\u662F\u5148\u4EBA\u4E8B\u9762\u7684\uFF0C\u90FD\u5F88\u626F\u6DE1\u3002</p><p>\u800C\u4E14\u6211\u611F\u89C9\u4EBA\u4E8B\u662F\u4E00\u4E2A\u975E\u5E38\u795E\u5947\u7684\u5B58\u5728\uFF0C\u597D\u597D\u7684\u4E00\u4E2A\u8C26\u606D\uFF0C\u5F6C\u5F6C\u6709\u793C\u7684\u5C0F\u4F19\u5B50\u9762\u8BD5\uFF0C\u8BF4\u8BDD\u53EF\u80FD\u4E5F\u662F\u6E29\u6587\u5C14\u96C5\u7684\uFF0C\u4F46\u5728\u4EBA\u4E8B\u773C\u91CC\uFF0C\u53EF\u80FD\u4F60\u5C31\u662F\u4E00\u4E2A\u4E0D\u591F\u81EA\u4FE1\uFF0C\u4E0D\u591F\u5927\u80C6\uFF0C\u6CA1\u6709\u4E3B\u89C1\uFF0C\u6CA1\u6709\u4EF7\u503C\u7684\uFF0C\uFF0C\u56E0\u4E3A\u4E00\u4E9B\u4E2A\u5148\u51B3\u6761\u4EF6\u7684\u7EA6\u675F\uFF0C\u5BFC\u81F4\u4F5C\u4E3A\u4EBA\u4E8B\uFF0C\u540E\u671F\u4F1A\u6210\u4E3A\u8FD9\u6837\u4E00\u79CD\u72B6\u6001\uFF0C\u5509\uFF0C\u6211\u53EA\u60F3\u8BF4\uFF0C\u603B\u662F\u770B\u5230\u5BF9\u65B9\u7684\u7F3A\u70B9\uFF0C\u800C\u4E0D\u80FD\u591F\u53D1\u73B0\u5176\u4F18\u70B9\uFF0C\u662F\u5176\u6CE8\u5B9A\u6C38\u8FDC\u505A\u4E0D\u51FA\u66F4\u597D\u6210\u7EE9\u7684\u5BBF\u547D\u3002\u901A\u8FC7\u53D1\u73B0\u6FC0\u52B1\u6C42\u804C\u8005\u7684\u95EA\u5149\u70B9\u4E3A\u516C\u53F8\u521B\u9020\u4EF7\u503C\uFF0C\u8981\u6BD4\u6311\u51E0\u4E2A\u6C42\u804C\u8005\u7684\u6BDB\u75C5\uFF0C\u8981\u4E48\u5426\u5B9A\u4ED6\uFF0C\u8981\u4E48\u5C31\u662F\u5C3D\u91CF\u538B\u4E00\u538B\u5DE5\u8D44\u6765\u62DB\u5230\u8FD9\u6837\u4E00\u4E2A\u4E0D\u75DB\u4E0D\u75D2\u7684\uFF0C\u65E0\u591A\u53EF\u8BF4\u7684\u4EBA\uFF0C\u5F3A\u51FA\u4E0D\u77E5\u9053\u591A\u5C11\u591A\u5C11\u500D\u3002\u53EF\u80FD\u4EBA\u4E8B\u90FD\u662F\u5929\u5E95\u4E0B\u6700\u806A\u660E\u7684\u4EBA\u5427\u3002\u5475\u5475\u4E00\u7B11</p><p>\u4ECA\u513F\u9762\u7684\u7B2C\u4E8C\u5BB6\uFF0C\u8FD8\u633A\u9760\u8C31\u7684\uFF0C\u73AF\u5883\uFF0C\u6C1B\u56F4\u90FD\u633A\u597D\u7684\uFF0C\u5148\u662F\u6280\u672F\u9762\u7684\uFF0C\u611F\u89C9\u804A\u5F97\u8FD8\u4E0D\u9519\uFF0C\u4ED6\u8BF4\u975E\u5E38\u6EE1\u610F\uFF0C\u540E\u6765\u4ED6\u53EB\u4EBA\u4E8B\u8FC7\u6765\uFF0C\u804A\u4E86\u804A\u85AA\u8D44\uFF0C\u8FD8\u6709\u5176\u4ED6\u4E00\u4E9B\u4E8B\u60C5\uFF0C\uFF0C\uFF0C\u5176\u4E2D\u4ED6\u4E5F\u8BF4\u5230\uFF0C\u611F\u89C9\u6211\u5BF9\u8BB8\u591A\u7684\u77E5\u8BC6\u7684\u638C\u63E1\u53EF\u80FD\u505C\u7559\u5728\u7406\u8BBA\u7684\u591A\u4E9B\uFF0C\u7136\u540E\u6709\u4E00\u4E9B\u4E5F\u4E0D\u662F\u5F88\u6DF1\u5165\uFF0C\u4E5F\u8BF4\u5176\u5B9E\u5F53\u521D\u7684\u4ED6\u4E5F\u662F\u8FD9\u6837\u4E00\u4E2A\u72B6\u6001\uFF0C\u4F46\u662F\u56E0\u4E3A\u804A\u5929\u611F\u89C9\u6211\u8FD8\u4E0D\u9519\uFF0C\u6709\u90A3\u79CD\u5411\u4E0A\u7684\u52B2\u513F\uFF0C\u6574\u4E2A\u4EBA\u7684\u5FC3\u6001\u4EE5\u53CA\u4E0E\u4EBA\u6C9F\u901A\u90FD\u8FD8\u633A\u4E0D\u9519\uFF0C\u6240\u4EE5\u86EE\u559C\u6B22\u7684\uFF0C\u8FD9\u4E5F\u662F\u6211\u9762\u8BD5\u4EE5\u6765\uFF0C\u6700\u63A5\u8FD1\u6210\u529F\u70B9\u7684\u4E00\u6B21\u5427\uFF0C\u633A\u5F00\u5FC3\u7684</p><p>\u9762\u8BD5\u4E4B\u524D\u90FD\u6709\u4E00\u4E2A\u586B\u8868\uFF0C\u57FA\u672C\u4E0A\u6BCF\u5BB6\u90FD\u4F1A\u8BA9\u5199\u4E00\u4E0B\u5BB6\u5EAD\u8054\u7CFB\u4EBA\uFF0C\u504F\u504F\u8FD9\u5BB6\u516C\u53F8\u8BA9\u5199\u4E00\u4E0B\u8054\u7CFB\u4EBA\u7684\u5E74\u9F84\uFF0C\u5728\u8FD9\u4E2A\u5730\u65B9\uFF0C\u6211\u5361\u4F4F\u4E86\uFF0C\u5988\u598867\u5E74\uFF0C\u7238\u723869\u5E74\uFF0C\u4ECA\u5E742017\uFF0C\uFF0C\uFF0C\u540E\u8FB9\u7684\uFF0C\u81EA\u5DF1\u90FD\u4E0D\u6562\u7B97\u4E86\uFF0C\u7B97\u4E86\u4E00\u904D\uFF0C\u5403\u60CA\u7684\u4E0D\u6562\u76F8\u4FE1\uFF0C\u6211\u53C8\u7B97\u4E86\u4E00\u904D\uFF0C\u6014\u6014\u7684\u5446\u4F4F\u4E86\uFF0C\u4E0D\u77E5\u6240\u4EE5\uFF0C\u4ED6\u4EEC\u90FD48\u4E86\u4E94\u5341\u591A\u4E86\u554A\uFF0C\u6211\u7684\u5988\u5440\uFF0C\u592A\u51FA\u4E4E\u6211\u7684\u60F3\u8C61\u4E86\uFF0C\u56E0\u4E3A\u5728\u6211\u7684\u5FC3\u91CC\uFF0C\u7238\u5988\u5E94\u8BE5\u4E5F\u8FD8\u662F\u56DB\u5341\u521A\u51FA\u5934\u7684\u6837\u5B50\u7684\uFF0C\u4E8E\u662F\u987F\u4E86\u4E00\u4E0B\u4E4B\u540E\uFF0C\u6211\u5199\u4E0A\u4E86\u7238\u7238\u7684\u5E74\u9F8448\u3002\u5230\u73B0\u5728\u6211\u90FD\u89C9\u5F97\u4E0D\u53EF\u76F8\u4FE1\uFF0C\u8FD9\u6837\u4E00\u4E2A\u73B0\u5B9E\uFF0C\u4F46\u662F\u8FD9\u5C31\u662F\u4E8B\u5B9E\uFF0C\u81EA\u5DF1\u4E5F\u957F\u5927\u5566\uFF0C\u7236\u6BCD\u6E10\u6E10\u8001\u53BB\uFF0C\u662F\u8981\u52A0\u7D27\u594B\u6597\uFF0C\u65E9\u70B9\u6311\u8D77\u5BB6\u5EAD\u7684\u62C5\u5B50\uFF0C\u5C3D\u91CF\u5C11\u7684\u8BA9\u7236\u6BCD\u4E3A\u81EA\u5DF1\u64CD\u5FC3\uFF0C\u52A0\u6CB9\u52AA\u529B\u554A\uFF01\uFF01\uFF01</p><p>\u540E\u6765\u4EBA\u4E8B\u8FC7\u6765\u804A\uFF0C\u4E3B\u8981\u56DE\u7B54\u4E86\u4E00\u4E9B\u516C\u53F8\u7684\u798F\u5229\u5F85\u9047\uFF0C\u4EE5\u53CA\u5DE5\u4F5C\u4E0A\u73ED\u65F6\u95F4\uFF0C\u8FD8\u95EE\u4E86\u4E00\u4E9B\u4E4B\u524D\u5DE5\u4F5C\u7684\u60C5\u51B5\uFF0C\u4EE5\u53CA\u4E3A\u4EC0\u4E48\u9009\u62E9\u4E86\u6765\u676D\u5DDE\uFF0C\u6211\u4E5F\u662F\u505A\u4E86\u4E00\u4E2A\u673A\u667A\u800C\u53C8\u5999\u7684\u56DE\u7B54\uFF1A</p><p>\u6211\u662F\u516B\u6708\u6B63\u5728\u90D1\u5DDE\u8F9E\u804C\uFF0C\u7136\u540E\u5317\u4EAC\u676D\u5DDE\u90FD\u6709\u670B\u53CB\u505A\u6280\u672F\uFF0C\u8DDF\u6211\u8BF4\u4E0A\u4E00\u7EBF\u57CE\u5E02\u66F4\u597D\u7684\u953B\u70BC\uFF0C\u6210\u957F\uFF0C\u5F53\u65F6HR\u95EE\u6211\uFF0C\u90A3\u4F60\u600E\u4E48\u6CA1\u6709\u9009\u62E9\u5317\u4EAC\uFF0C\u9009\u62E9\u4E86\u676D\u5DDE\u5462\uFF1F</p><p>\u56E0\u4E3A\u90A3\u4F1A\u513F\u804A\u5929\u7684\u6C14\u6C1B\u5DF2\u7ECF\u5F88\u8F7B\u677E\u4E86\uFF0C\u6CA1\u6709\u90A3\u4E48\u4E25\u8083\u4E86\uFF0C\u6211\u5C31\u8BF4\uFF0C\u55EF\uFF0C\u8FD9\u4E2A\u6709\u4E00\u5B9A\u7684\u4E2A\u4EBA\u539F\u56E0\u5427\uFF0C\u524D\u6BB5\u65F6\u95F4\u5728\u5BB6\u7684\u65F6\u5019\uFF0C\u6211\u5728\u770B\u6743\u529B\u7684\u6E38\u620F\uFF0C\u91CC\u8FB9\u4E0D\u662F\u4E5F\u7ECF\u5E38\u8BF4\u4E00\u53E5\u8BDD\u561B\uFF0CWinter is coming\u3002\u73B0\u5728\u5317\u4EAC\u5DF2\u7ECF\u5F88\u51B7\u4E86\uFF0C\u800C\u6211\u53C8\u662F\u4E00\u4E2A\u6015\u51B7\u7684\u4EBA\uFF0C\u6240\u4EE5\u5C31\u9009\u62E9\u6765\u676D\u5DDE\u4E86\u3002</p><p>\u5982\u679C\u8FD9\u5BB6\u516C\u53F8\u7ED9\u6211\u6253\u7535\u8BDD\u4E86\uFF0C\u6211\u7EDD\u5BF9\u4F1A\u53BB\uFF0C\u6CA1\u6709\u4EC0\u4E48\u72B9\u8C6B\u7684\uFF0C\u4E3B\u8981\u662F\u73AF\u5883\u633A\u6EE1\u610F\uFF0C\u4E5F\u4E00\u5B9A\u4E00\u5B9A\u80FD\u591F\u5B66\u5230\u4E0D\u5C11\u7684\u4E1C\u897F\uFF0C\u5C24\u5176\u662F\u8DDF\u6280\u672F\uFF08\u65B9\u540C\u5B66\uFF09\u9762\u7684\u65F6\u5019\uFF0C\u804A\u5F97\u5F88\u6295\u673A\u3002 \u8FD9\u5C31\u662F\u4E00\u79CD\u533A\u522B\u5427\uFF0C\u5F53\u516C\u53F8\u503E\u5411\u4E8E\u4F60\u7684\u65F6\u5019\uFF0C\u4EBA\u4E8B\u4E5F\u4F1A\u53D8\u6210\u4E00\u4E2A\u548C\u853C\u53EF\u4EB2\u7684\u670B\u53CB\u3002</p><p><img src="http://t.eryajf.net/imgs/2021/09/eb06e61ae19602dd.jpg" alt="image"></p><p>\u7B2C\u4E09\u5BB6\u9762\u8BD5\uFF0C\u53EF\u5C31\u66F4\u6709\u610F\u601D\u4E86\uFF0C\u516C\u53F8\u89C4\u6A21\u4E5F\u4E0D\u5C0F\uFF0C\u4F30\u8BA1\u80FD\u591F\u5927\u51E0\u5341\u53F7\uFF0C\u7136\u800C\uFF0C\u73B0\u5728\u6CA1\u6709\u4E00\u4E2A\u8FD0\u7EF4\uFF0C\u4E4B\u524D\u7684\u73AF\u5883\u7684\uFF0C\u57FA\u672C\u4E0A\u90FD\u662F\u4E00\u56E2\u7CDF\uFF0C\u6709\u4EE3\u7801\u4E86\uFF0C\u5C31\u5F80\u90A3\u513F\u4E00\u653E\uFF0C\u53CD\u6B63\u73B0\u5728\u7684\u60C5\u51B5\u662F\uFF0C\u4E00\u56E2\u4E71\u9EBB\uFF0C\u6709\u65F6\u5019\u51FA\u4E86\u4E2A\u95EE\u9898\uFF0C\u90FD\u627E\u4E0D\u5230\u5728\u54EA\u513F\uFF0C\uFF0C\uFF0C\u9762\u8BD5\u6211\u7684\u90A3\u4E2A\u4EBA\uFF0C\u4ED6\u4E5F\u662F\u521A\u5230\u8FD9\u5BB6\u516C\u53F8\uFF0C\u5C5E\u4E8E\u7A7A\u964D\u8FC7\u6765\uFF0C\u8FD9\u4E9B\u662F\u4ED6\u8BF4\u7684\uFF0C\u4ED6\u8BF4\u4ED6\u4E5F\u662F\u6765\u64E6\u5C41\u80A1\u7684\uFF0C\uFF0C\uFF0C\u6211\u611F\u89C9\u8FD9\u5C41\u80A1\u554A\uFF0C\u4E0D\u597D\u64E6\uFF0C\u7136\u540E\u540E\u6765\u6211\u4FE9\u90FD\u6CA1\u804A\u9762\u8BD5\u7684\u4E8B\u513F\u4E86\uFF0C\u80AF\u5B9A\u4E0D\u5408\u9002\u4E86\uFF0C\u7136\u540E\u5C31\u804A\u4E86\u4E00\u4E9B\u5173\u4E8E\u516C\u53F8\u4E2D\u7684\u8FD0\u7EF4\u554A\uFF0C\u4E4B\u7C7B\u7684\uFF0C\u4E5F\u6DA8\u4E86\u4E0D\u5C11\u89C1\u8BC6</p><p>\u4ECA\u5929\u516B\u70B9\u4ECE\u5BB6\u51FA\u53D1\uFF0C\u505A286\u4E00\u4E2A\u591A\u5C0F\u65F6\u5230\u8FBE\u7B2C\u4E00\u5BB6\uFF0C\u5341\u70B9\u9762\u8BD5\uFF0C\u4E8C\u5341\u5206\u949F\u5DE6\u53F3\uFF0C\u9762\u5B8C\u51FA\u6765\uFF0C\u4E0D\u8DB3\u5341\u4E00\u70B9\uFF0C\u5403\u4E86\u4EFD\u8106\u76AE\u9E21\uFF0C20\u5757\u94B1\uFF0C\u770B\u5230\u90A3\u5BB6\u751F\u610F\u7279\u522B\u597D\uFF0C\u57FA\u672C\u4E0A\u90FD\u662F\u5916\u5356\u4EBA\u5458\u5728\u6392\u961F\uFF0C\u975E\u5E38\u70ED\u95F9\uFF0C\u770B\u7684\u5374\u89C9\u5F97\u6709\u4E00\u4E1D\u60B2\u54C0\u3002\u5403\u5B8C\u5F80\u7B2C\u4E8C\u5BB6\u8F6C\uFF0C\u672C\u6765\u6253\u7B97\u5230\u897F\u6E56\u8F6C\u8F6C\uFF0C\u51E4\u8D77\u8DEF\u51FA\u53BB\u4E4B\u540E\uFF0C\u53C8\u63A5\u5230\u7535\u8BDD\uFF0C\u7EA6\u4E86\u7B2C\u4E09\u5BB6\uFF0C\u6211\u5C31\u60F3\uFF0C\u897F\u6E56\u4E0D\u53BB\u4E86\uFF0C\u5148\u5230\u7B2C\u4E8C\u5BB6\u90A3\u4E2A\u5730\u65B9\uFF0C\u7B49\u7740\u5427\uFF0C\u5230\u8FBE\u7B2C\u4E8C\u5BB6\u7684\u65F6\u5019\uFF0C\u8FD8\u4E0D\u8DB3\u5341\u4E8C\u70B9\uFF0C\u7136\u540E\u65C1\u8FB9\u6709\u6761\u6CB3\uFF0C\u633A\u597D\u7684\uFF0C\u5728\u6CB3\u8FB9\u67F3\u6811\u4E0B\uFF0C\u5EA6\u8FC7\u4E86\u4E2D\u5348\u7684\u65F6\u5149\uFF0C\u9762\u8BD5\u5927\u6982\u7528\u4E86\u4E00\u4E2A\u534A\u949F\u5934\uFF0C\u51FA\u6765\u4E4B\u540E\u76F4\u63A5\u8DD1\u5230\u5730\u94C1\uFF0C\u6CA1\u6562\u803D\u8BEF\uFF0C\u5230\u8FBE\u516C\u4EA4\u7AD9\u7684\u65F6\u5019\uFF0C\u5E94\u8BE5\u662F\u4E09\u70B9\u4E94\u5341\uFF0C\u7ED9\u7B2C\u4E09\u5BB6\u4EBA\u4E8B\u53D1\u4E86\u77ED\u4FE1\uFF0C\u505A\u4E86\u56DB\u5341\u5206\u949F\u516C\u4EA4\uFF0C\u8D70\u4E86\u5341\u5206\u949F\u5230\u8FBE\u7B2C\u4E09\u5BB6\uFF0C\u5012\u662F\u6CA1\u6709\u804A\u592A\u4E45\u3002\u6BD5\u7ADF\u4E0D\u5408\u9002\uFF0C\u51FA\u6765\u53D1\u73B0\u5929\u5DF2\u7ECF\u6697\u9ED1\u4E86\uFF0C\u4F46\u662F\u4E00\u5B9A\u8981\u56DE\u5BB6\u554A\uFF0C\u53BB\u7B49\u516C\u4EA4\uFF0C\u7B49\u4E86\u4E8C\u5341\u5206\u949F\u4E5F\u4E0D\u89C1\u6765\uFF0C\u540E\u6765\u641C\u4E86\u8981\u5230\u7684\u7AD9\u70B9\uFF0C3.9\u516C\u91CC\uFF0C\u4E8E\u662F\u626B\u4E86\u4E00\u8F86\u8F66\u5B50\u51FA\u53D1\uFF0C\u51FA\u53D1\u5C31\u5DF2\u7ECF\u661F\u70B9\u7684\u6709\u96E8\uFF0C\u98CE\u4E5F\u5927\uFF0C\u6211\u9A91\u7684\u683C\u5916\u7684\u5FEB\uFF0C\u7A81\u7136\u6709\u4E00\u79CD\u8C6A\u58EE\u7684\u611F\u89C9\uFF0C\u5728\u5927\u6982\u8FD8\u6709\u4E00\u5343\u7C73\u7684\u5730\u65B9\uFF0C\u96E8\u7A81\u7136\u4E0B\u5927\uFF0C\u53EA\u5F97\u4E22\u4E0B\u8F66\u5B50\uFF0C\u7136\u540E\u6253\u4F1E\u6B65\u884C\uFF0C\u8D70\u5230\u6700\u540E\u4E00\u8D9F\u8981\u505A\u7684\u516C\u4EA4\u7AD9\uFF0C\u5DF2\u7ECF\u662F\u516D\u70B9\u949F\u4E86\uFF0C\u7B49\u4E86\u5341\u5206\u949F\u5DE6\u53F3\uFF0C\u7EC8\u4E8E\u8FC7\u6765\u4E86\uFF0C\u8FD9\u8F86\u8F66\u5F00\u7684\u98DE\u5FEB\uFF0C\u7A7F\u68AD\u5728\u90CA\u533A\u7684\u4E61\u6751\u91CC\uFF0C\u4E00\u4E2A\u5C0F\u65F6\u4E4B\u540E\uFF0C\u5230\u8FBE\u4E86\u4F4F\u7684\u5730\u65B9\u3002\u8FB9\u8D70\u8FB9\u60F3\uFF0C\u4E00\u5929\u4E5F\u662F\u591F\u62FC\u7684\uFF0C\u4F46\u662F\u611F\u8C22\u4E2D\u95F4\u90A3\u6B21\u9762\u8BD5\u7ED9\u7684\u529B\u91CF\uFF0C\u529F\u592B\u4E0D\u8D1F\u6709\u5FC3\u4EBA\uFF0C\u603B\u7B97\u89C1\u5F97\u5230\u4E86\u4E00\u4E9B\u56DE\u62A5\uFF0C\u4ED8\u51FA\u518D\u591A\uFF0C\u4E5F\u89C9\u5F97\u5F00\u5FC3\u4E86\u3002</p><p><img src="http://t.eryajf.net/imgs/2021/09/72c1c206ec2c9550.jpg" alt="image"></p><p>\u4ECA\u5929\u9762\u8BD5\u9047\u5230\u7684\u4E0D\u4F1A\u7684\u95EE\u9898\uFF1A</p><ul><li>1\uFF0C \u5982\u4F55\u901A\u8FC7\u76D1\u63A7\u8F6F\u4EF6\u5B9E\u73B0\u8BE6\u7EC6\u7684\uFF0C\u6BD4\u5982Web\u670D\u52A1\u5668\u4E00\u5929\u5904\u7406\u4E86\u591A\u5C11\u7684\u8BF7\u6C42\uFF0C\u6709\u54EA\u4E9B\u8BF7\u6C42\u662F\u4E00\u4E9B\u4EC0\u4E48\u60C5\u51B5\uFF0C\uFF0C\uFF0C\u54CE\uFF0C\u95EE\u9898\u6211\u90FD\u8BB0\u4E0D\u5927\u6E05\u4E86\uFF0C\u66F4\u4E0D\u7528\u8BF4\u7B54\u6848\u4E86\u3002</li><li>2\uFF0C \u5173\u4E8EWeb\u4F18\u5316\u7684\uFF0C\u8BF4\uFF0C\u5982\u4F55\u914D\u7F6E\uFF0C\u80FD\u591F\u9650\u5236\u5355\u4E00\u7528\u6237\u5BF9\u670D\u52A1\u5668\u8BF7\u6C42\u7684\u9891\u7387</li><li>3\uFF0C \u5982\u4F55\u83B7\u53D6\u5230\uFF0C\u8BF7\u6C42web\u670D\u52A1\u5668\u6B21\u6570\u6700\u591A\u7684\u5341\u4E2Aip\uFF0C\uFF0C\uFF0C</li></ul><p>\u65F6\u95F4\uFF1A2017\u5E74\u5341\u6708</p>',19);function c(r,a){return i}var l=e(t,[["render",c]]);export{l as default};
