import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const t={},r=p('<p>\u8FD9\u662F2017\u5E74\u8FC7\u5E74\u56DE\u5BB6\u7684\u8DEF\u4E0A\u5728\u8F66\u4E0A\u5199\u4E0B\u7684\u6587\u5B57\uFF0C\u73B0\u5728\u56DE\u5934\u770B\u8D77\u6765\uFF0C\u4F9D\u65E7\u611F\u89C9\u90A3\u4E48\u7684\u7280\u5229\uFF0C\u6709\u5473\u3002</p><p>\u5728\u5199\u5173\u4E8E\u9875\u9762\u7684\u65F6\u5019\u4E5F\u66FE\u8BF4\u8FC7\uFF0C\u4EE5\u540E\u4F1A\u7ECF\u5E38\u5C06\u4EE5\u524D\u5728QQ\u7A7A\u95F4\u53D1\u5E03\u8FC7\u7684\u52A8\u6001\u66F4\u65B0\u5230\u535A\u5BA2\uFF0C\u53EA\u4E0D\u8FC7\u4E00\u76F4\u5FD9\u788C\uFF0C\u4E5F\u6CA1\u6709\u65F6\u95F4\u597D\u597D\u6574\u7406\u8FD9\u4E9B\u4E1C\u897F\uFF0C\u4ECA\u5929\u53D1\u4E00\u7BC7\u5173\u4E8E\u7231\u60C5\u7684\uFF0C\u7B97\u662F\u7B80\u5355\u5F00\u4E2A\u5934\u5427\u3002</p><p><img src="http://t.eryajf.net/imgs/2021/09/aee0e8072d20061c.jpg" alt="image"></p><p>\u5F52\u5BB6\u9014\u4E2D\uFF0C\u5076\u9047\u4E00\u5BF9\u60C5\u4FA3\uFF0C\u4E8C\u4EBA\u5173\u7CFB\u6781\u5FAE\u5999\uFF0C\u4E3E\u4E00\u5C0F\u4F8B\uFF0C\u4E0D\u77E5\u4F55\u4E8B\u800C\u8D77\uFF0C\u7537\u4EBA\u8BF4\u4E86\u4E00\u53E5\u8BDD\uFF0C\u5973\u4EBA\u7B2C\u4E8C\u53E5\u8BDD\u4FBF\u662F\u7834\u53E3\u5927\u9A82\uFF0C\u4F60\u4ED6\u5988\u7684\u4F60\u5C31\u662F\u50BB\u903C\uFF0C\u7537\u4EBA\u56DE\u7EDD\u9053\uFF0C\u6211\u548B\u53C8\u6210\u50BB\u903C\u4E86\u2026\u5982\u6B64\u60C5\u51B5\uFF0C\u540E\u6765\u591A\u6B21\u51FA\u73B0\uFF0C\u5E94\u8BE5\u4E0D\u662F\u5076\u7136\u3002\u6211\u5FC3\u60F3\uFF0C\u5927\u6982\u4E24\u4E2A\u4EBA\u90FD\u4E0D\u66FE\u8C08\u8FC7\u604B\u7231\uFF0C\u4EE5\u81F3\u4E8E\u628A\u7231\u60C5\u6298\u817E\u6210\u8FD9\u4E2A\u6837\u5B50\uFF01</p><p>\u674E\u6556\u66FE\u575A\u6301\u5F3A\u8C03\uFF0C\u7231\u60C5\u662F\u7EAF\u5FEB\u4E50\uFF0C\u4E0D\u662F\u75DB\u82E6\uFF0C\u5047\u5982\u51FA\u73B0\u75DB\u82E6\u7684\u65F6\u5019\uFF0C\u5C31\u5E94\u8BE5\u5206\u5F00\u4E86\u3002\u4ED6\u8BA4\u4E3A\u771F\u6B63\u7684\u7231\u60C5\u53EA\u6709\u4E09\u5230\u516D\u4E2A\u6708\u3002\u6211\u80FD\u8BA4\u540C\u7231\u60C5\u7684\u5FEB\u4E50\uFF0C\u4F46\u4E0D\u80FD\u82DF\u540C\u5BF9\u75DB\u82E6\u7684\u9003\u907F\uFF0C\u56E0\u4E3A\u53EA\u6709\u7ECF\u5386\u8FC7\u75DB\u82E6\uFF0C\u624D\u80FD\u771F\u6B63\u7684\u53D1\u81EA\u5185\u5FC3\u5728\u7231\u60C5\u5F53\u4E2D\u5949\u732E\uFF0C\u5206\u4EAB\uFF0C\u5FEB\u4E50\uFF0C\u73CD\u60DC\u3002</p><p>\u7231\u60C5\u539F\u672C\u4E0D\u662F\u5FEB\u4E50\u4E24\u4E2A\u5B57\u7684\u4EE3\u540D\u8BCD\uFF0C\u5B83\u662F\u5305\u542B\u6240\u6709\u67F4\u7C73\u6CB9\u76D0\u82E6\u8FA3\u9178\u751C\u7684\u4F1F\u5927\u5347\u534E\u3002\u5F88\u591A\u4EBA\u8BA4\u8BC6\u4E0D\u5230\u8FD9\u4E00\u70B9\uFF0C\u6240\u4EE5\uFF0C\u4ED6\u76F2\u76EE\u7684\uFF0C\u6BEB\u65E0\u987E\u5FCC\u7684\u4E00\u5FC3\u6E34\u671B\u628A\u4E16\u4FD7\u751F\u6D3B\u5F53\u4E2D\u7ECF\u5386\u7684\u7E41\u6742\u7CDF\u5FC3\u7ECF\u7531\u7231\u60C5\uFF0C\u4FBF\u53EF\u4EE5\u65E0\u9650\u7D22\u53D6\u5230\u81EA\u5DF1\u6240\u60F3\u8981\u7684\u5FEB\u4E50\uFF0C\u7136\u800C\u771F\u6B63\u7684\u4E8B\u5B9E\u662F\uFF0C\u5373\u4FBF\u518D\u65E0\u79C1\uFF0C\u518D\u9738\u9053\u7684\u7231\u60C5\uFF0C\u4E5F\u90FD\u9075\u5FAA\u201C\u6B32\u8981\u53D6\u4E4B\uFF0C\u5FC5\u5148\u4E88\u4E4B\u201D\uFF0C\u201C\u8D60\u4EBA\u73AB\u7470\uFF0C\u624B\u7559\u4F59\u9999\u201D\u8FD9\u6837\u7684\u57FA\u672C\u4EBA\u6027\u539F\u5219\u3002\u6240\u4EE5\uFF0C\u4E00\u5473\u5730\u7D22\u53D6\uFF0C\u6216\u8005\u662F\u4E00\u5473\u5730\u4ED8\u51FA\uFF0C\u90FD\u4E0D\u662F\u771F\u6B63\u7684\u80FD\u628A\u4E24\u4E2A\u4EBA\u53D8\u5F97\u66F4\u7F8E\u597D\u7684\u7231\u60C5\u7684\u672C\u6765\u6837\u5B50\u3002</p><p>\u662F\u7684\uFF0C\u6211\u575A\u4FE1\uFF0C\u7231\u60C5\u6709\u4E00\u79CD\u9B54\u529B\uFF0C\u5B83\u53EF\u4EE5\u8BA9\u908B\u9062\u7684\u4EBA\u53D8\u7684\u5E72\u51C0\uFF0C\u53EF\u4EE5\u628A\u61D2\u60F0\u7684\u4EBA\u53D8\u5F97\u52E4\u52B3\uFF0C\u66F4\u53EF\u4EE5\u628A\u5E7C\u7A1A\u7684\u4EBA\u513F\u53D8\u5F97\u6210\u719F\u3002\u5F53\u7136\uFF0C\u60F3\u8981\u83B7\u5F97\u8FD9\u4E9B\u6B63\u7684\u80FD\u826F\uFF0C\u9996\u5148\u8981\u6C42\u6211\u4EEC\u81EA\u5DF1\u6709\u4E00\u4E2A\u6B63\u786E\u7684\u7231\u60C5\u89C2\uFF0C\u6216\u8005\u8BF4\uFF0C\u81F3\u5C11\u6709\u4E00\u4E2A\u6B63\u786E\u7684\u4EBA\u60C5\u4E16\u6545\u7684\u8BA4\u8BC6\u3002\u4F60\u7231\u4E0A\u4E00\u4E2A\u4EBA\uFF0C\u4F46\u81EA\u5DF1\u7684\u908B\u9062\u663E\u7136\u662F\u5F88\u5927\u7684\u7F3A\u70B9\uFF0C\u6240\u4EE5\u4F60\u903C\u7740\u81EA\u5DF1\u52E4\u6362\u8863\u670D\u591A\u6D17\u6FA1\uFF1B\u4F60\u6E34\u671B\u4F60\u7231\u7740\u7684\u4EBA\uFF0C\u4ED6\u4E5F\u50CF\u4F60\u7231\u4ED6\u4E00\u6837\u7684\u7231\u4F60\uFF0C\u4EE5\u5F80\u7684\u65F6\u5019\uFF0C\u60F3\u559D\u70ED\u6C34\uFF0C\u56E0\u4E3A\u61D2\u60F0\u5F97\u8FC7\u4E14\u8FC7\u51C9\u6C34\u5428\u5428\u5428\u4E5F\u559D\u4E86\uFF0C\u4F46\u662F\u73B0\u5728\u5973\u670B\u53CB\u4E0D\u80FD\u559D\u51C9\u6C34\uFF0C\u4F60\u4E0D\u80FD\u61D2\u4E86\uFF0C\u4F60\u7231\u5979\uFF0C\u6240\u4EE5\u4F60\u8981\u653E\u4E0B\u624B\u91CC\u7684\u4E8B\u60C5\uFF0C\u53BB\u70E7\u58F6\u70ED\u6C34\uFF0C\u7231\u60C5\u5F53\u4E2D\uFF0C\u5F88\u591A\u4E8B\u60C5\u5E76\u4E0D\u518D\u53EA\u662F\u81EA\u5DF1\u4E00\u4E2A\u4EBA\u53BB\u5B8C\u6210\uFF0C\u5F53\u4F60\u70E7\u6C34\u7684\u65F6\u5019\uFF0C\u4F1A\u53D1\u73B0\uFF0C\u53E6\u4E00\u534A\u5DF2\u7ECF\u5728\u6084\u6084\u51C6\u5907\u676F\u5B50\uFF0C\u5976\u8336\u3002\u8FD9\u4E9B\u5FAE\u5C0F\u7684\u4E8B\u60C5\uFF0C\u6709\u7684\u4EBA\u4E00\u8F88\u5B50\u90FD\u6CA1\u6709\u53BB\u611F\u53D7\u8FC7\uFF1B\u604B\u7231\u4E4B\u524D\u4F60\u4EFB\u4F55\u4E8B\u60C5\u90FD\u662F\u4ECE\u81EA\u5DF1\u7684\u89D2\u5EA6\u53BB\u770B\u53BB\u89E3\u51B3\uFF0C\u4F5C\u4E3A\u534A\u4E2A\u4EBA\uFF0C\u6709\u5F88\u591A\u60C5\u611F\u4F60\u4F53\u4F1A\u4E0D\u5230\uFF0C\u505A\u8D77\u6765\u4E5F\u5C31\u663E\u5F97\u6655\u6655\u4E4E\u4E4E\uFF0C\u5E7C\u7A1A\u53EF\u7231\uFF0C\u4EE5\u8FD9\u6837\u7684\u72B6\u6001\u8FDB\u5165\u5230\u7231\u60C5\uFF0C\u5FC5\u7136\u7684\uFF0C\u4F1A\u906D\u9047\u5230\u79CD\u79CD\u82E6\u75DB\uFF0C\u6298\u78E8\uFF0C\u4E24\u4E2A\u6DF1\u7231\u7740\u5F7C\u6B64\u7684\u4EBA\uFF0C\u5728\u7231\u7684\u540D\u4E49\u4E0B\u5728\u4E00\u5757\u513F\uFF0C\u4E92\u76F8\u4F24\u5BB3\uFF0C\u65E2\u75DB\u82E6\u53C8\u65E0\u53EF\u5948\u4F55\u3002\u5728\u6211\u770B\u6765\uFF0C\u7231\u7684\u75DB\u6B63\u662F\u7231\u7684\u5FEB\u4E50\u4E4B\u524D\u5146\uFF0C\u4F60\u8BF4\u4E00\u53E5\u8BDD\u6211\u4E0D\u6EE1\u610F\uFF0C\u6211\u8BF4\u4E24\u53E5\u8BDD\u4F60\u4E0D\u987A\u5FC3\uFF0C\u4E24\u4E2A\u4EBA\u90FD\u66FE\u5927\u53D1\u96F7\u9706\uFF0C\u65A9\u65AD\u5FEB\u4E50\uFF0C\u5982\u679C\u4E0D\u50BB\uFF0C\u4EE5\u540E\u76F8\u5904\u4E2D\uFF0C\u907F\u7740\u5BF9\u65B9\u7684\u96F7\u70B9\uFF0C\u7D27\u7740\u5BF9\u65B9\u7684\u5FEB\u4E50\u53BB\u76F8\u5904\uFF0C\u4E0D\u4FE1\u8C08\u4E0D\u597D\u4E00\u573A\u604B\u7231\uFF0C\u800C\u80FD\u770B\u5230\u60F3\u5230\u5E76\u505A\u5230\u8FD9\u4E9B\u7684\u4EBA\uFF0C\u4FBF\u5DF2\u6210\u719F\u3002</p><p>\u7231\u60C5\u7684\u9B54\u529B\uFF0C\u4EC5\u6B64\u800C\u5DF2\u3002</p><p>\u6709\u7684\u4EBA\u770B\u4E0D\u5230\uFF0C\u975E\u4F46\u6CA1\u6709\u5728\u7231\u60C5\u4E2D\u627E\u5230\u5FEB\u4E50\uFF0C\u66F4\u56E0\u4E3A\u7231\u60C5\uFF0C\u8BA9\u81EA\u5DF1\u53D8\u5F97\u7CDF\u7CD5\u3002\u8F66\u4E0A\u6240\u9047\u5230\u7684\u60C5\u4FA3\uFF0C\u6216\u8BB8\u4E24\u4E2A\u4EBA\u5728\u81EA\u5DF1\u7684\u5DE5\u4F5C\u751F\u6D3B\u4E2D\u90FD\u662F\u975E\u5E38\u597D\u975E\u5E38\u4F18\u79C0\u7684\u4EBA\uFF0C\u4F46\u56E0\u4E3A\u4E24\u4E2A\u4EBA\u4E0D\u61C2\u5F97\u7231\u60C5\u771F\u8C1B\uFF0C\u7ED3\u5408\u5728\u4E00\u5757\u513F\uFF0C\u597D\u8A00\u4E0D\u8FC7\u4E09\u4E94\u53E5\uFF0C\u4FBF\u9A82\u5BF9\u65B9\uFF0C\u5BB3\u81EA\u5DF1\u8D77\u6765\u3002\u5973\u4EBA\u53D8\u5F97\u8D8A\u6765\u8D8A\u5F3A\u52BF\uFF0C\u7537\u4EBA\u53D8\u5F97\u8D8A\u6765\u8D8A\u5351\u5FAE\uFF0C\u8FD9\u573A\u604B\u7231\u53EF\u4EE5\u5224\u5B9A\u4E3A\u5931\u8D25\u4E86\u3002\u5E76\u975E\u662F\u6211\u81EA\u5DF1\u5927\u7537\u5B50\u4E3B\u4E49\uFF0C\u4E0D\u4FE1\u4F60\u81EA\u5DF1\u53EF\u4EE5\u5230\u516C\u5171\u573A\u5408\u611F\u53D7\u4E00\u4E0B\uFF0C\u4E00\u5BF9\u60C5\u4FA3\uFF0C\u5973\u4EBA\u5BF9\u7537\u4EBA\u53C8\u6253\u53C8\u9A82\uFF0C\u7537\u4EBA\u552F\u552F\u8BFA\u8BFA\u4E0D\u6562\u542D\u58F0\uFF0C\u4F60\u5FC3\u91CC\u4F1A\u600E\u6837\u770B\u5F85\u8FD9\u4E2A\u7537\u4EBA\uFF0C\u5C31\u80FD\u7406\u89E3\u6211\u6240\u8BF4\u7684\uFF0C\u628A\u7537\u4EBA\u53D8\u5F97\u5C0F\u6C14\uFF0C\u5351\u5FAE\uFF0C\u4F4E\u4E09\u4E0B\u56DB\u7684\u4E0D\u662F\u597D\u5973\u4EBA\uFF0C\u628A\u5973\u4EBA\u53D8\u5F97\u9738\u9053\uFF0C\u5F3A\u52BF\uFF0C\u4E0D\u77E5\u7F9E\u803B\u7684\u4E0D\u662F\u597D\u7231\u60C5\u3002\u6709\u4EBA\u8BF4\u4E24\u4E2A\u597D\u4EBA\u4E24\u4E2A\u4F18\u79C0\u7684\u4EBA\u5728\u4E00\u5757\u4E0D\u5E78\u798F\u4E5F\u4E0D\u5FEB\u4E50\uFF0C\u662F\u7231\u60C5\u7684\u9519\uFF0C\u6211\u60F3\u8BF4\uFF0C\u4E0E\u7231\u60C5\u65E0\u5173\uFF0C\u6B64\u65F6\u6B64\u523B\uFF0C\u6709\u751C\u871C\u60C5\u4FA3\u6B63\u5728\u6625\u5BB5\u4E00\u523B\uFF0C\u4E5F\u6709\u4E0D\u5E78\u7684\u60C5\u4FA3\u6B63\u5728\u6C34\u6DF1\u706B\u70ED\uFF0C\u66F4\u6709\u81EA\u79C1\u7684\u60C5\u4FA3\u6B63\u5728\u4E89\u5435\u6253\u95F9\u3002\u6240\u4EE5\uFF0C\u4E0E\u7231\u65E0\u5173\uFF0C\u75DB\u82E6\u7684\u4EBA\uFF0C\u4E00\u5B9A\u662F\u4E0D\u591F\u4F18\u79C0\uFF0C\u5DE5\u4F5C\u7684\u4F18\u79C0\u4E0D\u4EE3\u8868\u7231\u60C5\u7684\u4F18\u79C0\uFF0C\u5E76\u4E0D\u662F\u6BCF\u4E00\u4E2A\u4EBA\u90FD\u80FD\u591F\u7EC6\u5FC3\u8010\u5FC3\u53BB\u4F53\u609F\u7231\u60C5\u4E2D\u7684\u5FAE\u5999\u7684\u3002</p><p>\u552F\u5176\u75DB\u82E6\u8FC7\uFF0C\u6240\u4EE5\u603B\u662F\u5E78\u798F\u7684\u3002</p>',10);function c(a,_){return r}var d=e(t,[["render",c]]);export{d as default};
