import{e as l}from"./app.dbf5d933.js";import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";const p={},o=l('<p>\u4E16\u95F4\u7684\u60B2\u559C\u5E76\u4E0D\u76F8\u901A\uFF0C\u6709\u4EBA\u4F1A\u9677\u5728\u6253\u55DD\u7684\u75DB\u82E6\u4E4B\u4E2D\uFF0C\u51E0\u4E4E\u547D\u4EA4\u4E8E\u6B64\u3002</p><p>\u8FD9\u51E0\u5929\u53D1\u70E7\u4E86\uFF0C\u6211\u5C31\u7ECF\u5386\u4E86\u4E00\u573A\u8FD9\u6837\u7684\u4F53\u9A8C\uFF0C\u53EF\u8C13\u75DB\u82E6\u96BE\u5F53\u3002\u539F\u672C\u53D1\u70E7\u5C31\u5DF2\u7ECF\u8BA9\u6211\u611F\u89C9\u6D51\u8EAB\u9178\u75DB\uFF0C\u5927\u6982\u662F\u5927\u524D\u5929\u665A\u4E0A\u5403\u7684\u591A\u4E86\u4E4B\u7F18\u6545\uFF0C\u8FD9\u4E24\u5929\uFF0C\u5728\u53D1\u70E7\u6B63\u76DB\u7684\u4E24\u5929\u91CC\uFF0C\u5728\u67D0\u4E2A\u4E0D\u77E5\u9053\u5750\u59FF\u4E0D\u5BF9\uFF0C\u8FD8\u662F\u547C\u5438\u4E0D\u5BF9\u7684\u65F6\u673A\u4E0B\uFF0C\u55DD\u5C31\u8D77\u6765\u4E86\uFF0C\u800C\u4E14\u8FD8\u662F\u90A3\u79CD\u987D\u56FA\u6027\u6253\u55DD\uFF0C\u4E00\u5F00\u59CB\u5C31\u505C\u4E0D\u4E0B\u6765\u3002\u6B63\u597D\u6BD4\uFF1A\u55DD\u4E0D\u77E5\u6240\u8D77\uFF0C\u4E00\u6253\u4E0D\u505C\u3002\u6211\u6574\u4E2A\u82E6\u75DB\u7684\u75C5\u4F53\u88AB\u6BCF\u4E00\u4E2A\u55DD\u6643\u52A8\u7740\uFF0C\u4EFF\u4F5B\u5468\u8EAB\u90FD\u53D8\u6210\u4E86\u4E00\u4E2A\u88C5\u6EE1\u6C34\u7684\u5851\u6599\u888B\uFF0C\u51E0\u4E4E\u6574\u4E2A\u7075\u9B42\u90FD\u8DDF\u968F\u7740\u6253\u55DD\u6643\u52A8\u7740\u3002\u8FD9\u79CD\u75DB\u82E6\uFF0C\u975E\u662F\u4EB2\u5386\u4E0D\u53EF\u610F\u4F1A\u3002</p><p>\u767D\u5929\u6253\u55DD\u5C31\u786C\u751F\u751F\u96BE\u53D7\u4E00\u6CE2\uFF0C\u66F4\u96BE\u53D7\u7684\u662F\u665A\u4E0A\uFF0C\u672C\u6253\u7B97\u65E9\u70B9\u4F11\u606F\u7684\u60F3\u6CD5\u4E5F\u4E0D\u80FD\u5982\u613F\uFF0C\u6574\u4E2A\u4EBA\u90FD\u88AB\u55DD\u6643\u52A8\u7740\uFF0C\u65E0\u6CD5\u6210\u7720\uFF0C\u6700\u540E\u6211\u9B3C\u4F7F\u795E\u5DEE\u7FFB\u5F00\u77E5\u4E4E\uFF0C\u5728\u51CC\u6668\u65F6\u5206\uFF0C\u5BFB\u89C5\u5230\u504F\u65B9\uFF0C\u679C\u7136\u5C31\u7ACB\u9A6C\u89C1\u6548\uFF0C\u4E0D\u53EF\u8C13\u4E0D\u795E\u5947\uFF0C\u8D76\u7D27\u8BB0\u5F55\u4E0B\u6765\uFF0C\u4EE5\u5907\u540E\u7528\uFF0C\u5E76\u5206\u4EAB\u51FA\u6765\uFF0C\u4EE5\u5229\u66F4\u591A\u8EAB\u9677\u6B64\u82E6\u7684\u4EBA\u65E9\u65E5\u8131\u79BB\u6B64\u5883\u3002</p><p><img src="http://t.eryajf.net/imgs/2021/09/82c826a045256b3e.jpg" alt="img"></p><p>\u5728\u77E5\u4E4E\u63D0\u4F9B\u7684\u4F17\u591A\u7B54\u6848\u4E2D\uFF0C\u76EE\u524D\u6211\u4E2A\u4EBA\u4EB2\u6D4B\u6709\u6548\u7684\u4E24\u4E2A\uFF0C\u7279\u6B64\u8BB0\u5F55\uFF1A</p><blockquote><ul><li>\u5438\u6C14\uFF0C\u5410\u5C3D\uFF0C\u5DE6\u624B\u634F\u4F4F\u9F3B\u5B50\uFF0C\u7136\u540E\u8FDE\u7740\u559D5~6\u5C0F\u53E3\u6E29\u6C34(\u4E0D\u8981\u559D\u51C9\u6C34)</li></ul></blockquote><p>\u6B64\u65B9\u6CD5\u628A\u6211\u7761\u4E0D\u7740\u4E4B\u524D\u7684\u55DD\u7ACB\u65F6\u6B62\u4F4F\uFF0C\u53EA\u4E0D\u8FC7\u534A\u591C\u4E24\u70B9\u591A\u9192\u6765\u4E4B\u540E\uFF0C\u6B64\u65B9\u6CD5\u53C8\u4E0D\u594F\u6548\u4E86\uFF0C\u4EFF\u4F5B\u6253\u55DD\u4E5F\u5BF9\u6B64\u514D\u75AB\u4E86\u4E00\u6837\u3002\u518D\u6B21\u8FDB\u5165\u7761\u4E0D\u7740\u7684\u5FAA\u73AF\uFF0C\u6211\u4E8E\u662F\u53C8\u627E\u5230\u4E86\u7B2C\u4E8C\u4E2A\u6B62\u55DD\u7684\u65B9\u6CD5\uFF1A</p><blockquote><ul><li>\u6162\u6162\u7684\u7528<strong>\u9F3B\u5B50\u6DF1\u6DF1\u5438\u5165</strong>\u4E00\u53E3\u6C14\uFF0C\u6CE8\u610F\uFF0C\u4E00\u5B9A\u8981\u5E73\u7A33\u7684\u6162\u6162\u5438\u5165\uFF0C\u5DEE\u4E0D\u591A\u752810\u79D2\u7684\u65F6\u95F4\uFF0C\u5438\u5230\u80BA\u90E8\u5145\u6EE1\u65B0\u9C9C\u7684\u7A7A\u6C14\uFF0C\u53EF\u4EE5\u95ED\u4E0A\u773C\u775B\u5E76\u62AC\u5934\u3002</li><li><strong>\u5C4F\u4F4F</strong>\u8FD9\u53E3\u6C14\uFF0C10-20\u79D2\uFF0C\u907F\u514D\u5468\u56F4\u4EBA\u7684\u89E6\u78B0\u6216\u8005\u6253\u6270\uFF0C\u4EE5\u514D\u5C94\u6C14\u3002</li><li>\u6700\u540E\u6162\u6162\u7684\u628A\u8FD9\u53E3\u6C14\u5E73\u7A33\u7F13\u6162\u7684<strong>\u7528\u5634\u5410\u51FA</strong>\uFF0C\u6301\u7EED\u7EA610\u79D2\u5DE6\u53F3\uFF0C\u5410\u5230\u518D\u4E5F\u5410\u4E0D\u51FA\u6C14\u4E3A\u6B62\u3002\u7136\u540E\u7F13\u6162\u6062\u590D\u547C\u5438\u3002</li><li>\u91CD\u590D\u8FD9\u4E2A\u8FC7\u7A0B\u4E24\u5230\u4E09\u6B21\u3002</li></ul></blockquote><p>\u6211\u7684\u7B2C\u4E8C\u6B21\u987D\u56FA\u6253\u55DD\uFF0C\u5C31\u662F\u88AB\u8FD9\u4E2A\u6CD5\u5B50\u7ED9\u6CBB\u4F4F\u4E86\u3002</p><p>\u5F53\u665A\u56E0\u4E3A\u504F\u65B9\u6551\u547D\uFF0C\u7279\u522B\u53D1\u4E86\u670B\u53CB\u5708\u7559\u5FF5\u611F\u8C22\uFF0C\u7B2C\u4E8C\u5929\u6709\u670B\u53CB\u770B\u5230\uFF0C\u53C8\u5206\u4EAB\u4E86\u4E00\u4E2A\u4ED6\u7528\u7684\u65B9\u6CD5\uFF1A</p><blockquote><ul><li>\u559D\u51E0\u5927\u53E3\u6E29\u6C34\uFF0C\u7136\u540E\u5F2F\u8170\u621090\u5EA6\uFF0C\u4E24\u5206\u949F\u5373\u53EF\u89E3\u51B3\u3002</li></ul></blockquote><p>\u8FD9\u4E2A\u6211\u6CA1\u6709\u7528\u5230\uFF0C\u4E0D\u8FC7\u4E5F\u4E00\u5E76\u5728\u6B64\u8BB0\u5F55\u4E86\u3002</p><p>\u4F46\u613F\u4E16\u95F4\u4EBA\u65E0\u75C5\uFF0C\u5E0C\u671B\u6240\u6709\u770B\u5230\u8FD9\u7BC7\u6587\u5B57\u7684\uFF0C\u90FD\u80FD\u88AB\u751F\u6D3B\u5584\u5F85\uFF01</p><ul><li>\u53C2\u8003 <ul><li>https://zhuanlan.zhihu.com/p/165220406</li></ul></li></ul>',14);function e(i,r){return o}var s=t(p,[["render",e]]);export{s as default};
