import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h2 id="_1-\u5750\u4E09\u8F6E" tabindex="-1"><a class="header-anchor" href="#_1-\u5750\u4E09\u8F6E" aria-hidden="true">#</a> 1\uFF0C\u5750\u4E09\u8F6E</h2><p>\u6211\u4ECE\u90D1\u5DDE\u56DE\u6765\uFF0C\u5230\u8FBE\u793E\u65D7\u7684\u65F6\u5019\u662F\u665A\u4E0A\u4E03\u70B9\uFF0C\u6BD5\u7ADF\u5341\u4E8C\u6708\u4EFD\u4E86\uFF0C\u5929\u5DF2\u5F7B\u5E95\u9ED1\u900F\uFF0C\u8FD8\u6CA1\u4E0B\u8F66\u5C31\u770B\u5230\u4E24\u8F86\u4E09\u8F6E\u8F66\uFF0C\u5176\u4E2D\u4E00\u4E2A\u4EBA\u4ECE\u6709\u4E9B\u7834\u65E7\u7684\u4E09\u8F6E\u8F66\u4E0A\u4E0B\u6765\uFF0C\u8EAB\u5B50\u5DE6\u53F3\u6447\u6643\u7740\u8D70\u8FC7\u6765\uFF0C\u6211\u4E00\u773C\u770B\u51FA\uFF0C\u4ED6\u8EAB\u4F53\u6709\u4E9B\u4E0D\u4FBF\uFF0C\u5E74\u7EAA\u4F3C\u4E5F\u4E0D\u5C0F\uFF0C\u8138\u76AE\u5E72\u67AF\uFF0C\u5728\u5BD2\u51B7\u4E2D\uFF0C\u5E0C\u671B\u7740\u7B49\u5F85\u5230\u4E00\u4E2A\u5E0C\u671B\u3002</p><p>\u201C\u5750\u4E09\u8F6E\u4E0D\u5750\uFF1F\u201D</p><p>\u6211\u4E0B\u4E86\u8F66\uFF0C\u4E00\u80A1\u5BD2\u98CE\u5939\u6742\u7740\u6C7D\u8F66\u521A\u505C\u6240\u84C4\u79EF\u7684\u5C3E\u6C14\u5439\u6765\uFF0C\u4F7F\u6211\u4E0D\u7981\u6253\u4E86\u4E2A\u51B7\u6218\uFF0C\u987A\u624B\u628A\u8863\u670D\u62C9\u94FE\u62C9\u4E0A\u53BB\uFF0C\u76F4\u5954\u540E\u8FB9\u53BB\u62FF\u884C\u674E\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5F53\u6211\u5728\u8F66\u4E0A\u770B\u5230\u4ED6\u7684\u6837\u5B50\u65F6\uFF0C\u5C31\u5DF2\u52A8\u4E86\u607B\u9690\u4E4B\u5FC3\uFF0C\u4E8E\u4ED6\u800C\u8A00\u6700\u5927\u7684\u6E29\u6696\u2014\u2014\u6050\u6015\u5C31\u662F\u5149\u987E\u4ED6\u7684\u751F\u610F\u4E86\u3002</p><p>\u201C\u5750\u3002\u201D</p><p>\u201C\u90A3\u60F3\u53BB\u54EA\u513F\uFF1F\u201D</p><p>\u6B64\u65F6\u6211\u5DF2\u628A\u884C\u674E\u62FF\u51FA\uFF0C\u4ED6\u7AD9\u5728\u6211\u7684\u540E\u9762\uFF0C\u4F38\u624B\u5C31\u8981\u53BB\u63D0\uFF0C\u6211\u8FDE\u8BF4\u201C\u4E0D\u7528\u4E86\uFF0C\u6211\u6765\u5C31\u884C\u3002\u201D\u4ED6\u53CD\u800C\u663E\u51FA\u4E00\u4E1D\u96BE\u4E3A\u60C5\u7684\u6837\u5B50\uFF0C\u63A5\u7740\u7ACB\u9A6C\u8DDF\u4E0A\u6765\u3002</p><p>\u6211\u7A0D\u7A0D\u56DE\u5FC6\u4E00\u4E0B\u60F3\u53BB\u7684\u5730\u65B9\u7684\u540D\u5B57\uFF0C\u201C\u53BB\u7F51\u5427\u8857\u5427\uFF01\u201D</p><p>\u201C\u4E2D\uFF0C\u4E0A\u8F66\u5427\u3002\u201D</p><p>\u201C\u54CE\uFF0C\u591A\u5C11\u94B1\u554A\u3002\u201D</p><p>\u201C\u53BB\u7F51\u5427\u8857\u7684\u8BDD\uFF0C\u4E94\u5757\u94B1\uFF0C\u4F60\u770B\u554A\uFF0C\u6211\u62C9\u6D3B\u5C31\u662F\u5B9E\u60E0\uFF0C\u8981\u662F\u522B\u4EBA\u5C11\u516D\u5757\u90FD\u4E0D\u8DD1\u3002\u201D\u4ED6\u8BF4\u5B8C\u4EF7\u94B1\u53C8\u89E3\u91CA\u4E86\u4E00\u53E5\uFF0C\u53EF\u4ED6\u4E0D\u77E5\u9053\uFF0C\u5728\u6211\u5FC3\u91CC\uFF0C\u7B80\u76F4\u60F3\u518D\u7ED9\u4ED6\u6DFB\u4E00\u4E9B\u5462\uFF0C\u5C3D\u7BA1\u6211\u5E76\u4E0D\u662F\u4E00\u4E2A\u6000\u63E3\u591A\u5C11\u94B1\u7684\u4EBA\uFF0C\u4F46\u6211\u6E05\u695A\uFF0C\u5BF9\u4E8E\u4ED6\u800C\u8A00\uFF0C\u5C0F\u94B1\u5C31\u80FD\u6362\u5927\u5E78\u798F\u3002</p><p>\u201C\u884C\uFF0C\u8D70\u5427\u3002\u201D\u6211\u7279\u610F\u56DE\u5E94\u5F97\u5F88\u5E72\u8106\uFF0C\u751F\u6015\u4ED6\u56E0\u4E3A\u6211\u7684\u72B9\u8C6B\u800C\u96BE\u4E3A\u60C5\u3002</p><p>\u4ED6\u5F00\u59CB\u53D1\u52A8\u6469\u6258\u8F66\uFF0C\u8FD8\u7ED9\u6211\u4ECB\u7ECD\u8BF4\uFF1A\u201C\u55EF\uFF0C\u90A3\u91CC\u53EF\u4EE5\u4E0A\u7F51\u3002\u201D</p><p>\u8F66\u5F00\u52A8\u4E86\uFF0C\u8FD9\u65F6\u6211\u624D\u53D1\u73B0\uFF0C\u8FD9\u8F86\u4E09\u8F6E\u786E\u5B9E\u5F88\u65E7\u4E86\uFF0C\u89D2\u94C1\u710A\u7684\u5EA7\u5DF2\u6709\u4E9B\u4E0B\u5782\uFF0C\u4E0A\u9762\u7684\u76AE\u5957\u4E5F\u70C2\u4E86\u4E2A\u7A9F\u7ABF\uFF0C\u7A0D\u7A0D\u5750\u76F4\u4E86\u8EAB\u5B50\uFF0C\u5934\u5C31\u9876\u5230\u4E86\u8F66\u68DA\u3002\u5750\u5728\u8F66\u6597\u91CC\uFF0C\u6211\u5FFD\u7136\u53C8\u95FB\u5230\u4E09\u8F6E\u6469\u6258\u7684\u90A3\u96BE\u95FB\u7684\u6C7D\u6CB9\u5473\uFF0C\u53EA\u597D\u7528\u624B\u906E\u7740\u70B9\u513F\u9F3B\u5B50\u3002\u8F66\u5F00\u7684\u6162\u60A0\u60A0\uFF0C\u8FD9\u5012\u8BA9\u6211\u5FC3\u5B89\u8BB8\u591A\u3002\u6211\u7231\u5750\u8001\u5E74\u4EBA\u7684\u4E09\u8F6E\uFF0C\u5374\u53C8\u5BB3\u6015\u9047\u5230\u5F00\u8F66\u5F88\u5FEB\u7684\u8001\u5E74\u4EBA\uFF0C\u7231\u522B\u4EBA\u56FA\u7136\u91CD\u8981\uFF0C\u4F46\u9996\u5148\u8981\u7231\u62A4\u81EA\u5DF1\u7684\u8EAB\u4F53\u3002</p><p>\u5341\u5206\u949F\u5DE6\u53F3\u8F66\u5230\u4E86\uFF0C\u6211\u5F13\u7740\u8EAB\u5B50\u8DF3\u51FA\u6765\uFF0C\u5728\u5BFB\u627E\u8981\u7ED9\u4ED6\u7684\u94B1\u7684\u65F6\u5019\uFF0C\u4ED6\u4E0A\u53BB\u5C31\u8981\u5E2E\u6211\u63D0\u884C\u674E\uFF0C\u6211\u8D76\u5FD9\u5C06\u4ED6\u62E6\u4E0B\uFF0C\u201C\u54CE\uFF0C\u884C\u674E\u633A\u6C89\u7684\uFF0C\u60A8\u522B\u63D0\uFF0C\u8BA9\u6211\u6765\u3002\u201D\u4ED6\u8D70\u7684\u65F6\u5019\u53C8\u4E00\u6B21\u7ED9\u6211\u4ECB\u7ECD\u8BF4\uFF0C\u8FD9\u91CC\u6709\u7F51\u5427\uFF0C\u53EF\u4EE5\u4E0A\u7F51\u3002\u597D\u50CF\u8FD9\u4E9B\u8BAF\u606F\u5730\u63A8\u4ECB\u662F\u4ED6\u7684\u5206\u5185\u4E4B\u4E8B\u4E00\u6837\u3002</p><p>\u840D\u6C34\u76F8\u9022\uFF0C\u5374\u4F3C\u90BB\u91CC\u4E00\u822C\u3002\u5728\u8FD9\u6837\u4E00\u4E2A\u5927\u65F6\u4EE3\u91CC\uFF0C\u6216\u8BB8\uFF0C\u8FD9\u5C31\u662F\u6211\u7684\u5BB6\u4E61\u2014\u2014\u8FD9\u4E2A\u5C0F\u53BF\u57CE\u7684\u4E00\u80A1\u9B45\u529B\u5427\u3002</p><p><img src="http://t.eryajf.net/imgs/2021/09/9c7f808bc92a7564.jpg" alt="image"></p><h2 id="_2-\u5403\u9762\u6761" tabindex="-1"><a class="header-anchor" href="#_2-\u5403\u9762\u6761" aria-hidden="true">#</a> 2\uFF0C\u5403\u9762\u6761</h2><p>\u6211\u62FF\u7740\u884C\u674E\uFF0C\u8D70\u8FDB\u4E00\u5BB6\u996D\u5E97\uFF0C\u5F88\u6E05\u51C0\uFF0C\u90A3\u8FB9\u662F\u8001\u677F\u4E00\u5BB6\u4E94\u53E3\u5728\u5403\u665A\u996D\uFF0C\u8FD9\u8FB9\u6709\u4E00\u4F4D\u8001\u5927\u7237\u4E5F\u5403\u7740\u996D\u3002</p><p>\u201C\u60F3\u5403\u5565\uFF1F\u201D\u7537\u8001\u677F\u8D77\u8EAB\u63A5\u5F85\u6211\u3002</p><p>\u201C\u6765\u4E00\u4EFD\u513F\u5200\u524A\u9762\u5427\u3002\u201D</p><p>\u201C\u5927\u4EFD\u513F\uFF0C\u4E2D\u4EFD\u513F\uFF1F\u201D</p><p>\u201C\u8981\u4E2D\u4EFD\u513F\u3002\u989D\uFF0C\u6CA1\u6709\u5C0F\u4EFD\u513F\u5417\uFF1F\u201D</p><p>\u201C\u4E2D\u4EFD\u513F\u662F\u4E09\u4E24\u4E5D\u3002\u6709\u5C0F\u4EFD\u513F\uFF0C\u4F60\u770B\u4F60\u8981\u2026\u2026\uFF1F\u201D</p><p>\u201C\u90A3\u5C31\u4E2D\u4EFD\u513F\u5427\uFF0C\u5927\u4EFD\u513F\u6211\u6050\u6015\u5403\u4E0D\u5B8C\u3002\u201D</p><p>\u8D77\u521D\u6211\u4EE5\u4E3A\u8001\u5927\u7237\u4E5F\u662F\u6765\u8FD9\u513F\u5403\u996D\u7684\u5BA2\u4EBA\uFF0C\u5F53\u6211\u5750\u4E0B\u6765\u770B\u5230\u4ED6\u7897\u91CC\u7684\u996D\u548C\u90A3\u4E00\u684C\u4E00\u6837\u65F6\uFF0C\u624D\u77E5\u9053\u539F\u6765\u662F\u4E00\u5BB6\u5B50\u7684\u3002</p><p>\u4E00\u4E2A\u8001\u7237\u5B50\uFF0C\u513F\u5B50\uFF0C\u513F\u5AB3\u5987\uFF0C\u4E24\u4E2A\u5B59\u5973\u513F\uFF0C\u4E00\u4E2A\u5B59\u5B50\u3002\u513F\u5AB3\u5987\u521A\u597D\u80CC\u5BF9\u7740\u8001\u7237\u5B50\u5750\u7684\u684C\u5B50\uFF0C\u8FD9\u662F\u79BB\u4ED6\u6700\u8FD1\u7684\u4F4D\u5B50\uFF0C\u800C\u9762\u5BF9\u4ED6\u7684\u6070\u662F\u4E00\u4E2A\u540E\u80CC\u3002\u8001\u7237\u5B50\u5F88\u5B89\u9759\u5730\u5403\u7740\uFF0C\u89D2\u843D\u91CC\u7535\u89C6\u673A\u5F00\u7740\uFF0C\u4ED6\u5374\u5934\u4E5F\u4E0D\u62AC\u4E00\u4E0B\uFF0C\u56E0\u4E3A\u7535\u89C6\u6B63\u64AD\u653E\u7684\uFF0C\u662F\u52A8\u753B\u7247\u3002\u6211\u6CE8\u610F\u5230\uFF0C\u5F53\u52A8\u753B\u7247\u64AD\u5B8C\uFF0C\u5C0F\u5B59\u5B50\u62FF\u7740\u9065\u63A7\u5668\u5F00\u59CB\u6362\u53F0\uFF0C\u5FFD\u7684\u4E00\u58F0\u5F26\u5B50\u97F3\uFF0C\u8001\u7237\u5B50\u6162\u6162\u62AC\u8D77\u5934\uFF0C\u672A\u53CA\u505A\u4E2A\u8FA8\u8BC6\uFF0C\u5DF2\u88AB\u6362\u6210\u522B\u7684\u9891\u9053\uFF0C\u6211\u5206\u660E\u770B\u5230\u4ED6\u7684\u5634\u5F20\u4E86\u5F20\uFF0C\u4F46\u4EC0\u4E48\u4E5F\u6CA1\u8BF4\uFF0C\u5C31\u53C8\u628A\u5934\u4F4E\u4E0B\u53BB\u3002\u6211\u5FFD\u7136\u60F3\u548C\u8001\u7237\u5B50\u804A\u804A\u5929\uFF0C\u5374\u56E0\u4E3A\u4E0D\u77E5\u9053\u8BF4\u4E9B\u4EC0\u4E48\uFF0C\u53EA\u597D\u4F5C\u7F62\u3002</p><p>\u770B\u7740\u8001\u7237\u5B50\uFF0C\u6211\u4E0D\u7981\u6697\u60F3\uFF1A\u4E5F\u8BB8\u8001\u4F34\u4E1A\u5DF2\u6545\u53BB\uFF0C\u7559\u4ED6\u4E00\u4EBA\u5728\u8FD9\u4E2A\u4E0D\u592A\u5C5E\u4E8E\u4ED6\u7684\u5BB6\uFF0C\u513F\u5B50\u5DF2\u4F5C\u5979\u4EBA\u592B\uFF0C\u5B59\u5B50\u5B59\u5973\u8FD8\u5C0F\u4E0D\u61C2\u4E8B\u3002\u8BF4\u65E5\u5B50\u6CA1\u5473\u9053\u5427\uFF0C\u603B\u4E5F\u7B97\u6709\u5403\u6709\u7A7F\uFF0C\u8BF4\u65E5\u5B50\u4E0D\u9519\u5427\uFF0C\u5374\u603B\u611F\u89C9\u90A3\u4E48\u5B64\u5355\u3002\u4EBA\u8001\u4E86\uFF0C\u603B\u6709\u4E9B\u53EF\u601C\uFF0C\u5374\u8FD8\u8981\u63A9\u76D6\u6240\u6709\u82E6\u75DB\u628A\u8FD9\u53EF\u601C\u53D8\u4F5C\u4E60\u60EF\u3002</p><p>\u6211\u9009\u5750\u7684\u684C\u5B50\u4E0A\u9762\u6709\u4E00\u53EA\u7897\uFF0C\u6CA1\u6709\u4EBA\u52A8\u8EAB\u8981\u60F3\u6536\u62FE\u3002\u8FD9\u65F6\u5019\u8001\u7237\u5B50\u628A\u7897\u7B77\u653E\u4E0B\uFF0C\u8D70\u8FC7\u6765\u6536\u62FE\u6211\u5750\u7684\u684C\u5B50\uFF0C\u7AEF\u7897\uFF0C\u62B9\u684C\u5B50\uFF0C\u52A8\u4F5C\u7F13\u6162\uFF0C\u5374\u4E00\u4E1D\u4E0D\u82DF\uFF0C\u5F88\u8BA4\u771F\u7684\u4E3A\u4ED6\u513F\u5B50\u7684\u4E8B\u4E1A\u5949\u732E\u529B\u91CF\u3002\u6211\u770B\u89C1\u4ED6\u8138\u4E0A\u6CA1\u6709\u4E00\u70B9\u8868\u60C5\uFF0C\u773C\u775B\u5F88\u4E45\u4E0D\u7728\u4E00\u6B21\uFF0C\u5076\u5C14\u54B3\u55FD\u4E24\u58F0\uFF0C\u8D76\u5FD9\u7528\u624B\u906E\u4F4F\u5634\u3002</p><p>\u8001\u677F\u5230\u540E\u8FB9\u5F00\u706B\u505A\u996D\uFF0C\u6211\u5FFD\u7136\u542C\u5230\u90A3\u8FB9\u684C\u5B50\u4E0A\u7684\u58F0\u97F3\uFF0C\u662F\u5927\u5B59\u5973\u5728\u8BB2\u5B66\u6821\u7684\u4E8B\u513F\uFF0C\u5927\u5B59\u5973\u770B\u4E0A\u53BB\u6709\u5341\u4E00\u4E8C\u5C81\u3002</p><p>\u201C\u6211\u4EEC\u5B66\u6821\u73B0\u5728\u6BCF\u5929\u90FD\u8DD1\u64CD\uFF0C\u7136\u540E\u6211\u4EEC\u73ED\u6709\u4E00\u4E9B\u7537\u751F\u5C31\u5728\u540E\u8FB9\u8E29\u5973\u751F\u7684\u978B\uFF0C\u90A3\u5929\u4E00\u4E2A\u7537\u751F\u53BB\u8E29\u4E00\u4E2A\u5973\u751F\uFF0C\u7ED3\u679C\u4E00\u4E0B\u5B50\u628A\u978B\u5E95\u7ED9\u8E29\u6389\u4E86\uFF0C\u5C31\u5269\u4E86\u4E00\u4E2A\u978B\u9762\u5728\u811A\u4E0A\uFF0C\u5F53\u65F6\u6211\u6B63\u5728\u8DD1\uFF0C\u4E5F\u4E0D\u77E5\u9053\u3002\u201D</p><p>\u5979\u7684\u5C0F\u5F1F\u5F1F\uFF0C\u5C0F\u59B9\u59B9\u542C\u7684\u5165\u795E\uFF0C\u5979\u7684\u5988\u5988\u4ECD\u5728\u90A3\u513F\u5939\u83DC\u5403\u7740\uFF0C\u6211\u5728\u4E00\u8FB9\u542C\u7740\u4E5F\u7B11\u4E86\u3002</p><p>\u201C\u540E\u6765\u8BE5\u4E0A\u8BFE\u4E86\uFF0C\u6211\u4EEC\u56F4\u4E86\u4E00\u5806\u4EBA\u5728\u770B\u5979\u7684\u811A\uFF0C\u5316\u5B66\u8001\u5E08\u8FC7\u6765\u4E86\uFF0C\u95EE\u600E\u4E48\u4E86\uFF0C\u5979\u8BF4\u978B\u5E95\u88AB\u8E29\u6389\u4E86\uFF0C\u8001\u5E08\u5C31\u8BF4\uFF0C\u978B\u9762\u4E0D\u662F\u8FD8\u5728\uFF0C\u8D70\uFF0C\u4E0A\u8BFE\u53BB\u3002\u540E\u6765\u8FD9\u4E2A\u5973\u751F\u56DE\u5BB6\u6362\u4E86\u978B\uFF0C\u5979\u7238\u4E5F\u6765\u5B66\u6821\u4E86\uFF0C\u8BF4\u8981\u627E\u90A3\u4E2A\u7537\u751F\uFF0C\u7ED3\u679C\u8FD8\u5413\u5F97\u90A3\u4E2A\u7537\u751F\u8EB2\u5728\u73ED\u91CC\u4E0D\u6562\u51FA\u6765\u4E86\uFF0C\u4ECE\u90A3\u513F\u4EE5\u540E\uFF0C\u518D\u6CA1\u6709\u7537\u751F\u6562\u53BB\u8E29\u5973\u751F\u7684\u978B\u4E86\u3002\u201D</p><p>\u8FD9\u4E2A\u65F6\u5019\uFF0C\u8FD8\u662F\u8001\u7237\u5B50\uFF0C\u4ECE\u540E\u53A8\u7AEF\u7740\u4E00\u5927\u7897\u9762\uFF0C\u5C0F\u6B65\u5C0F\u8D70\uFF0C\u6211\u770B\u89C1\u4E86\uFF0C\u8D76\u7D27\u4E0A\u524D\u4E24\u6B65\u63A5\u4E86\u8FC7\u6765\uFF0C\u6211\u770B\u89C1\u4ED6\u5634\u89D2\u5FAE\u5FAE\u4E0A\u626C\uFF0C\u53C8\u5750\u56DE\u53BB\u4E86\u3002\u800C\u6211\u8FD8\u6C89\u6D78\u5728\u5927\u5B59\u5973\u8BB2\u7684\u4E8B\u513F\u91CC\uFF0C\u6211\u5728\u60F3\uFF0C\u5982\u679C\u6211\u6709\u4E00\u4E2A\u5973\u513F\uFF0C\u5341\u4E8C\u4E09\u5C81\uFF0C\u9752\u6625\u61F5\u61C2\u7684\uFF0C\u5979\u9047\u5230\u8FD9\u6837\u7684\u4E8B\u513F\uFF0C\u6211\u4F1A\u600E\u4E48\u9762\u5BF9\u5462\uFF0C\u8FD8\u6CA1\u60F3\u6211\u5C31\u5148\u7B11\u4E86\uFF0C\u53EA\u662F\u89C9\u5F97\u6709\u610F\u601D\uFF0C\u6211\u60F3\uFF0C\u5927\u6982\u6211\u4E0D\u4F1A\u50CF\u90A3\u4E2A\u5973\u751F\u5BB6\u957F\u90A3\u6837\u53BB\u5B66\u6821\u627E\u90A3\u7537\u751F\u4E8B\u513F\u5427\uFF0C\u53EF\u8FD9\u6837\u7684\u8BDD\uFF0C\u4ED6\u4EEC\u4EE5\u540E\u4F1A\u4E0D\u4F1A\u8FD8\u6B3A\u8D1F\u6211\u7684\u5973\u513F\uFF0C\u6211\u7684\u5973\u513F\u80FD\u4E0D\u80FD\u81EA\u5DF1\u89E3\u5F00\u81EA\u5DF1\u5FC3\u91CC\u7684\u7599\u7629\u5462\u2026\u2026\u60F3\u7740\u6447\u6447\u5934\u7B11\u4E86\uFF0C\u57CB\u5934\u5403\u8D77\u6211\u7684\u9762\u6765\u3002</p><p>\u63A5\u7740\u5927\u5B59\u5973\u53C8\u8BB2\u4E86\u4E00\u4EF6\u4E8B\u513F\uFF0C\u201C\u4FFA\u73ED\u6709\u4E2A\u5973\u751F\u5979\u7238\u662F\u4E2A\u7535\u5DE5\uFF0C\u6709\u6B21\u4F5C\u4E1A\u6CA1\u5199\u5B8C\uFF0C\u8001\u5E08\u95EE\u5979\u4E3A\u5565\uFF0C\u5979\u8BF4\uFF0C\u6628\u665A\u5BB6\u91CC\u505C\u7535\u4E86\uFF0C\u8001\u5E08\u53C8\u8BF4\uFF0C\u54A6\uFF0C\u8C01\u6562\u505C\u6041\u5BB6\u91CC\u7535\u554A\u3002\u201D\u5C0F\u59B9\u59B9\u95EE\uFF1A\u201C\u5565\u662F\u7535\u5DE5\u554A\u3002\u201D\u201C\u7535\u5DE5\u5C31\u662F\u7BA1\u7535\u7684\u3002\u201D\u201C\u90A3\u8001\u5E08\u4E3A\u5565\u90A3\u6837\u8BF4\uFF1F\u201D\u201C\u54CE\u5440\uFF0C\u4F60\u6CA1\u542C\u51FA\u6765\u662F\u789C\uFF08\u63F6\u63C4\uFF09\u5979\u91CC\u3002\u201D</p><p>\u4ECE\u7B2C\u4E00\u53E3\u5C31\u611F\u89C9\u5230\uFF0C\u8FD9\u9762\u5F88\u4E0D\u597D\u5403\uFF0C\u5750\u8F66\u5750\u7684\u96BE\u53D7\uFF0C\u6211\u6709\u70B9\u513F\u540E\u6094\u6CA1\u6709\u8981\u5C0F\u4EFD\u513F\u3002\u4F46\u4E0D\u597D\u5403\u7684\u4E1C\u897F\u5F77\u4F5B\u66F4\u9700\u8981\u628A\u5B83\u5403\u5B8C\u4F3C\u7684\uFF0C\u62FF\u5927\u5B59\u5973\u8BB2\u7684\u4E8B\u513F\u5F53\u4F50\u6599\uFF0C\u6211\u56EB\u56F5\u7740\u5403\u4E0B\u4E86\u4E00\u534A\u513F\uFF0C\u73B0\u5728\u5979\u4E0D\u8BB2\u4E86\uFF0C\u5012\u8BE5\u6211\u80E1\u601D\u4E71\u60F3\u8D77\u6765\u3002</p><p>\u4E00\u4E2A\u5BB6\u5475\uFF0C\u6709\u4E24\u4E2A\u4E16\u754C\uFF0C\u8840\u8109\u4E5F\u8BB8\u662F\u552F\u4E00\u7684\u5173\u8054\uFF0C\u81F3\u4E8E\u9759\u566A\u4E92\u4E0D\u76F8\u6270\uFF0C\u51E0\u4E4E\u662F\u4E00\u4E2A\u5E38\u6001\u3002\u8001\u4EBA\u7684\u5B58\u5728\u53D8\u5F97\u6CA1\u843D\uFF0C\u4ED6\u6240\u6709\u7684\u4E50\u8DA3\uFF0C\u6240\u6709\u7684\u4F24\u75DB\uFF0C\u90FD\u53EA\u80FD\u5728\u81EA\u5DF1\u7684\u5C0F\u4E16\u754C\u91CC\u5C55\u5F00\u3002\u51AC\u6708\u66F4\u6DF1\u4E86\uFF0C\u5BD2\u51B7\u53C8\u5C82\u6B62\u7A97\u5916\u7684\u98CE\u5462\u3002</p><p>\u4E5F\u8BB8\uFF0C\u8001\u4EBA\u5FC3\u91CC\u4EC0\u4E48\u4E5F\u6CA1\u6709\u60F3\uFF0C\u5E72\u67AF\u7684\u751F\u6D3B\u6240\u5E26\u7ED9\u4ED6\u7684\uFF0C\u5DF2\u7136\u628A\u70ED\u5FF1\u7684\u5FC3\u80F8\u51BB\u56FA\uFF0C\u6240\u6709\u4EBA\u6240\u5177\u5907\u7684\u4E03\u60C5\u516D\u6B32\uFF0C\u5728\u4ED6\u770B\u6765\uFF0C\u90FD\u53D8\u5F97\u7D22\u7136\u65E0\u5473\uFF0C\u4E0D\u503C\u4E00\u63D0\u3002</p><p>\u5403\u5B8C\u996D\uFF0C\u6211\u8D70\u51FA\u6765\uFF0C\u4E00\u4E9B\u5546\u94FA\u5DF2\u5F00\u59CB\u5173\u95E8\uFF0C\u4E00\u5929\u7684\u5FD9\u788C\u4E5F\u5C06\u8981\u6B47\u606F\uFF0C\u6211\u987E\u4E0D\u5F97\u611F\u6168\u81EA\u5DF1\uFF0C\u76F4\u5954\u65C5\u5E97\u800C\u53BB\u3002</p><p>\u6587\u7AE0\u5199\u4E8E2015\u5E74\uFF0C\u5230\u4ECA\u5929\u53D1\u5230\u6B64\u5904\uFF0C\u4E5F\u662F\u4E00\u79CD\u51A5\u51A5\u5B89\u6392\u3002\u800C\u4ECA\u91CD\u56DE\u5934\u770B\u81EA\u5DF1\u5F53\u5E74\u5199\u8FC7\u7684\u4E00\u4E9B\u6587\u5B57\uFF0C\u611F\u53F9\u65F6\u5149\u6613\u901D\uFF0C\u81EA\u5DF1\u4E5F\u7F3A\u5C11\u4E86\u5F88\u591A\u5F53\u5E74\u5BF9\u4E8E\u60B2\u5929\u60AF\u4EBA\u7684\u90A3\u79CD\u4EBA\u6587\u60C5\u6000\uFF0C\u8EAB\u4E0A\u66F4\u591A\u7684\u4E5F\u662F\u75B2\u4E8E\u751F\u6D3B\u7684\u5954\u6CE2\uFF0C\u5145\u6EE1\u9E21\u6BDB\u849C\u76AE\u7684\u5C0F\u5FC3\u80F8\uFF0C\u5FF5\u53CA\u6B64\uFF0C\u4E0D\u7981\u6C57\u989C\uFF01</p>',40);function t(c,d){return r}var h=e(a,[["render",t]]);export{h as default};
