import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const t={},c=p('<p>\u6574\u7406\u81EA\u7236\u4EB2\u53D1\u7684\u5FAE\u4FE1\u670B\u53CB\u5708\uFF0C\u5206\u4EAB\u65F6\u95F4\u4E3A\uFF1A<code>10\u670823\u65E5</code></p><blockquote><p>\u5BF9\u8BDD\u868A\u5B50</p><p>\u6709\u4E00\u53EA\u4E0D\u8BC6\u76F8\u7684\u868A\u5B50\uFF0C\u5B83\u6709\u70B9\u7316\u72C2\u8FD8\u6709\u70B9\u56A3\u5F20\uFF0C\u5C31\u8C61\u8521\u82F1\u6587\u4E00\u6837\u62B1\u7740\u7F8E\u56FD\u4F6C\u7684\u5927\u817F\uFF0C\u75AF\u72C2\u5730\u5728\u53EB\u56A3\u53F0\u72EC\uFF0C\u4F3C\u4E4E\u8981\u6293\u4F4F\u9752\u6625\u7684\u5C3E\u5DF4\uFF0C\u5014\u5F3A\u5730\u4E0D\u80AF\u9000\u51FA\u5386\u53F2\u7684\u821E\u53F0\u3002</p><p>\u5728\u8FD9\u79CB\u51AC\u4EA4\u66FF\u4E4B\u9645\uFF0C\u4E5F\u8BB8\u662F\u6DF1\u79CB\u6700\u540E\u4E00\u53EA\u4E27\u5FC3\u75C5\u72C2\u7684\u868A\u5B50\uFF0C\u7ECF\u8FC7\u4E00\u756A\u614E\u91CD\u7684\u76D8\u65CB\u4E4B\u540E\uFF0C\u7A33\u7A33\u5730\u964D\u843D\u5728\u6211\u88F8\u9732\u7684\u80F3\u818A\u4E0A\uFF0C\u5373\u5C06\u5BF9\u6211\u5C55\u5F00\u6700\u540E\u4E00\u8F6E\u8F70\u70B8\uFF0C\u5C31\u8C61\u5F53\u5E74\u5C0F\u65E5\u672C\u5077\u88AD\u73CD\u73E0\u6E2F\u4E00\u6837\u5802\u800C\u7687\u4E4B\u5730\u5927\u53E3\u8FDB\u9910\u3002</p><p>\u868A\u5B50\u8BF4\uFF1A\u4EBA\u4E3A\u8D22\u6B7B\uFF0C\u9E1F\u4E3A\u98DF\u4EA1\u7684\u9053\u7406\u6211\u4E0D\u662F\u4E0D\u61C2\uFF0C\u53EA\u662F\u751F\u547D\u8BDA\u53EF\u8D35\uFF0C\u5403\u996D\u4E5F\u5F88\u91CD\u8981\uFF0C\u5403\u4E0D\u9971\u996D\u53EB\u6211\u600E\u4E48\u5EA6\u8FC7\u6F2B\u957F\u800C\u53C8\u5BD2\u51B7\u7684\u51AC\u5929\u3002</p><p>\u72E1\u733E\u7684\u868A\u5B50\u5728\u4E3A\u81EA\u5DF1\u7684\u7F6A\u884C\u52AA\u529B\u7684\u72E1\u8FA9\u7740\uFF0C\u4FD7\u8BDD\u8BF4\u201C\u5341\u4E03\u80FD\u4E0D\u8FC7\u5341\u516B\uFF0C\u95FA\u5973\u7684\u5FC3\u4E8B\u7792\u4E0D\u8FC7\u5988\u201D\u3002\u533A\u533A\u4E00\u53EA\u5C0F\u5C0F\u7684\u868A\u5B50\u5176\u5201\u866B\u5C0F\u6280\u65E9\u5DF2\u88AB\u6211\u770B\u7A7F\uFF0C\u5C31\u8C61\u8521\u82F1\u6587\u4E00\u6837\u4F60\u5C31\u4F5C\u5427\uFF0C\u4E0D\u662F\u4E0D\u62A5\uFF0C\u65F6\u5019\u672A\u5230\u3002</p><p>\u6BDB\u4E3B\u5E2D\u6559\u5BFC\u6211\u4EEC\uFF1A\u201D\u4EBA\u4E0D\u72AF\u6211\uFF0C\u6211\u4E0D\u72AF\u4EBA\u201D\u3002\u868A\u82E5\u72AF\u6211\uFF0C\u6211\u5FC5\u5C06\u5176\u62CD\u4E4B\u800C\u540E\u5FEB\u3002\u6211\u7684\u5904\u4E8B\u539F\u5219\u662F\u4E0D\u9996\u5148\u52A8\u7C97\uFF0C\u770B\u7740\u5B83\u6E10\u6E10\u9F13\u8D77\u7684\u809A\u76AE\uFF0C\u8868\u660E\u4E86\u5B83\u786E\u5B9E\u5728\u6311\u6218\u6211\u7684\u5E95\u7EBF\uFF0C\u6211\u7684\u9009\u62E9\u662F\u4E0D\u5728\u4FDD\u6301\u6C89\u9ED8\uFF0C\u662F\u53EF\u5FCD\u5B70\u4E0D\u53EF\u5FCD\u3002</p><p>\u4E8E\u662F\u547C\u6211\u679C\u65AD\u51FA\u51FB\uFF0C\u201C\u556A\u201D\u7684\u4E00\u58F0\uFF0C\u665A\u79CB\u6700\u540E\u4E00\u53EA\u4E0D\u8BC6\u8DA3\u7684\u868A\u5B50\u88AB\u6211\u62CD\u4E0E\u65E0\u5F62\u4E4B\u4E2D\uFF0C\u5B83\u6781\u4E0D\u60C5\u613F\u5730\u4E3A\u5B83\u75AF\u72C2\u7684\u4E3E\u52A8\u4ED8\u51FA\u4E86\u751F\u547D\u7684\u4EE3\u4EF7\uFF0C\u5C5E\u4E8E\u5B83\u7684\u65F6\u95F4\u6C38\u8FDC\u5B9A\u683C\u5728\u4E86\u5341\u6708\u4E8C\u5341\u4E09\u65E5\u665A\u4E0A\u3002</p><p>\u970E\u90A3\u95F4\u9C9C\u8840\u67D3\u7EA2\u4E86\u6211\u7684\u624B\u638C\u3002\u868A\u5B50\u5728\u6700\u540E\u7684\u5F25\u7559\u4E4B\u9645\uFF0C\u5B83\u54AC\u7259\u5207\u9F7F\u58F0\u6CEA\u4FF1\u4E0B\u5730\u8BF4\uFF0C\u4F60\u8FD9\u4E2A\u5C0F\u6C14\u9B3C\uFF0C\u6211\u53EA\u662F\u4E3A\u4E86\u4E00\u987F\u996D\u800C\u5DF2\uFF0C\u4F60\u5374\u8981\u4E86\u6211\u7684\u547D\uFF0C\u672A\u514D\u4E0B\u624B\u4E5F\u592A\u5F88\u4E86\u5427\u3002\u72E1\u733E\u7684\u868A\u5B50\u6700\u540E\u6D0B\u6D0B\u81EA\u5F97\u5730\u8BF4\uFF0C\u4F60\u867D\u7136\u62CD\u6B7B\u4E86\u6211\u7684\u8EAB\uFF0C\u4F46\u4F60\u624B\u638C\u4E0A\u6D41\u7684\u5374\u662F\u4F60\u7684\u8840\uFF0C\u62CD\u6B7B\u4E0D\u8981\u7D27\uFF0C\u5438\u8840\u6211\u8BA4\u771F\uFF0C\u6574\u6B7B\u6211\u4E00\u4E2A\uFF0C\u8FD8\u6709\u540E\u6765\u868A\u3002</p><p>\u868A\u5B50\u8BF4\u5730\u6211\u7ADE\u7136\u4E00\u65F6\u65E0\u8BED\uFF0C\u60F3\u60F3\u868A\u5B50\u4E5F\u662F\u633A\u4E0D\u5BB9\u6613\u7684\uFF0C\u5B83\u4EEC\u4E5F\u5E76\u975E\u4E00\u65E0\u662F\u5904\uFF0C\u5B83\u4EEC\u4ECE\u5C0F\u5C31\u53D7\u5230\u4E86\u826F\u597D\u7684\u6559\u80B2\uFF0C\u868A\u5B50\u5988\u5988\u8981\u5B83\u4EEC\u505A\u5230\u201C\u98DF\u4E0D\u8A00\uFF0C\u5BDD\u4E0D\u8BED\u201D\uFF0C\u6240\u4EE5\u868A\u5B50\u5403\u996D\u7684\u65F6\u5019\u4E00\u822C\u90FD\u662F\u9ED8\u9ED8\u65E0\u95FB\u7684\uFF0C\u6CA1\u6709\u5927\u58F0\u55A7\u54D7\u7684\uFF0C\u8FD9\u4E00\u70B9\u8FD8\u662F\u503C\u5F97\u6211\u4EEC\u4EBA\u7C7B\u5B66\u4E60\u7684\u3002</p><p>\u591A\u4E48\u987D\u56FA\u7684\u5C0F\u751F\u547D\u554A\uFF01\u5BF9\u4EBA\u4EEC\u867D\u7136\u6709\u6240\u6C42\uFF0C\u4F46\u662F\u7ED9\u4EBA\u4EEC\u7684\u5374\u662F\u5F53\u4E0B\u4EBA\u4EEC\u90FD\u60F3\u62A2\u5230\u7684\u4E1C\u897F\u2014\u2014\u7EA2\u5305\uFF0C\u4F60\u53EA\u8981\u7B54\u5E94\u7BA1\u5B83\u4E00\u987F\u996D\uFF0C\u800C\u4E14\u8BA9\u5B83\u5403\u7684\u8D8A\u8DD1\uFF0C\u5B83\u9001\u7ED9\u4F60\u7684\u7EA2\u5305\u4E5F\u5C31\u8D8A\u5927\uFF0C\u5F88\u591A\u6CA1\u6709\u8010\u6027\u7684\u4EBA\u4EEC\u5F80\u5F80\u63D0\u524D\u7ED3\u675F\u4E86\u5B83\u7684\u751F\u547D\uFF0C\u56E0\u6B64\u5F97\u5230\u7684\u7EA2\u5305\u4E5F\u4E0D\u4F1A\u592A\u5927\u3002</p><p>\u770B\u7740\u80F3\u818A\u4E0A\u8D77\u7684\u7EA2\u5305\uFF0C\u6211\u6697\u81EA\u53D1\u7B11\uFF0C\u539F\u6765\u6211\u662F\u4E2A\u6709\u8010\u6027\u7684\u4EBA\u3002</p><p>\u868A\u5B50\u5440\u868A\u5B50\uFF0C\u6320\u7740\u4F60\u9001\u6211\u7684\u5DF2\u7ECF\u6709\u70B9\u75D2\u7684\u7EA2\u5305\uFF0C\u6211\u662F\u8BE5\u611F\u8C22\u4F60\u91CC\u8FD8\u662F\u8BC5\u5492\u4F60\u91CC\u3002</p><p>\u868A\u5B50\u8BF4\u4F60\u4E0D\u7528\u611F\u8C22\u6211\uFF0C\u4F60\u7684\u8010\u5FC3\u8FD8\u4E0D\u591F\uFF0C\u5728\u6211\u5373\u5C06\u9001\u4F60\u4E94\u767E\u4E07\u7EA2\u5305\u7684\u65F6\u5019\uFF0C\u4F60\u6253\u6B7B\u4E86\u6211\uFF0C\u6240\u4EE5\u4F60\u662F\u4E2A\u53EA\u77E5\u9053\u6253\u5DE5\uFF0C\u5F53\u4E0D\u4E86\u8001\u677F\uFF0C\u6C38\u8FDC\u4E5F\u53D1\u4E0D\u4E86\u8D22\u7684\u4EBA\uFF0C\u4F60\u5C31\u8BA4\u547D\u5427\u3002</p><p>\u4E00\u8BED\u60CA\u9192\u68A6\u4E2D\u4EBA\uFF0C\u6211\u540E\u6094\u6211\u62CD\u6B7B\u4E86\u4E00\u53EA\u868A\u5B50\u3002</p></blockquote><p><img src="http://t.eryajf.net/imgs/2021/09/00ff133018e13c8c.jpg" alt="image-20210717181202962"></p>',3);function o(r,a){return c}var d=e(t,[["render",o]]);export{d as default};
