import{e as p}from"./app.dbf5d933.js";import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<p>\u518D\u6709\u51E0\u65E5\u6211\u5C31\u8981\u8FC7\u4E09\u5C81\u5927\u5BFF\u4E86\uFF0C\u6211\u7684\u8EAB\u4F53\u4E0D\u662F\u5F88\u597D\uFF0C\u56E0\u6B64\u5DF2\u6709\u4E9B\u8001\u65E7\u4E0D\u582A\u3002\u4EBA\u4E00\u4E0A\u5E74\u7EAA\u5C31\u559C\u6B22\u201C\u5FC6\u5F80\u6614\u201D\uFF0C\u6211\u4E0D\u7981\u56DE\u60F3\u8D77\u81EA\u5DF1\u521A\u51FA\u751F\u7684\u65F6\u5019\u2026\u2026</p><p>\u6211\u662F\u4E00\u4E2A\u89C4\u5212\u5185\u7684\u5B69\u5B50\u3002\u4E09\u5E74\u524D\uFF0C\u4E00\u4E2A\u7537\u4EBA\u6253\u7B97\u5F00\u4E00\u4E2A\u7F51\u5427\uFF0C\u4E8E\u662F\u4ED6\u4E0E\u4E00\u4E2A\u516C\u53F8\u7B7E\u4E86\u8BA2\u8D2D\u5408\u540C\uFF0C\u6211\u4FBF\u662F\u90A3\u6279\u5B9A\u5236\u513F\u4E2D\u7684\u4E00\u4E2A\uFF0C\u5F88\u4E0D\u5E78\u6211\u662F\u4E00\u4E2A\u96BE\u4EA7\u513F\uFF0C\u4EFF\u4F5B\u4E00\u5207\u5B89\u6392\u597D\u4E86\u4E00\u822C\uFF0C\u6211\u63A5\u7740\u53C8\u5E78\u8FD0\u5730\u88AB\u4E00\u4E2A\u5DE5\u7A0B\u5E08\u63A5\u624B\uFF0C\u4ED6\u628A\u6211\u7684\u88AB\u89C4\u5212\u601D\u7EF4\u5207\u9664\uFF0C\u4FDD\u7559\u4E86\u6211\u5929\u751F\u7684\u6240\u6709\u3002\u6211\u56E0\u6B64\u5F97\u4EE5\u987A\u5229\u4E0B\u7EBF\uFF0C\u5E76\u62E5\u6709\u4E86\u81EA\u5DF1\u7684\u540D\u5B57\u2014\u2014\u674E\u5C24\uFF0C\u4F46\u201C\u6211\u662F\u6211\u201D\u8FD9\u4EF6\u4E8B\u513F\u6CA1\u4E00\u4E2A\u4EBA\u77E5\u9053\u3002</p><p>\u5DE5\u7A0B\u5E08\u5BF9\u6211\u867D\u7136\u6069\u4E3A\u518D\u9020\uFF0C\u4F46\u6211\u5F88\u957F\u4E00\u6BB5\u65F6\u95F4\u91CC\u5E76\u4E0D\u611F\u8C22\u4ED6\uFF0C\u56E0\u4E3A\u4ED6\u65E2\u7136\u5851\u6211\u6210\u5F02\u7C7B\uFF0C\u5C31\u5E94\u8BE5\u628A\u6211\u7559\u5728\u4ED6\u8EAB\u8FB9\uFF0C\u90A3\u6837\u6211\u4E5F\u4F1A\u597D\u8FC7\u4E00\u4E9B\uFF0C\u7136\u800C\u6216\u8BB8\u662F\u4ED6\u9A6C\u864E\uFF0C\u628A\u6211\u4E22\u5728\u4E86\u65C1\u7684\u663E\u793A\u5668\u4E2D\u95F4\uFF0C\u6211\u7684\u4E00\u751F\u574E\u5777\u4E5F\u6B63\u662F\u81EA\u6B64\u5F00\u59CB\u3002</p><p>\u51E0\u5929\u540E\uFF0C\u6211\u4E0E\u5176\u5B83\u56DB\u5341\u4E5D\u4E2A\u540C\u80DE\u4E00\u8D77\u88AB\u8D27\u8F66\u62C9\u5230\u4E00\u4E2A\u6BEB\u4E0D\u8D77\u773C\u7684\u5730\u65B9\uFF0C\u51E0\u4E2A\u7537\u4EBA\u642C\u8FD0\u7740\u6211\u4EEC\u3002</p><p>\u6211\u8F6C\u8EAB\u95EE\u5728\u8DEF\u4E0A\u8BA4\u8BC6\u7684\u5C0F\u6770\uFF1A\u201C\u5C0F\u6770\uFF0C\u4ED6\u4EEC\u8FD9\u662F\u8981\u5E72\u4EC0\u4E48\uFF1F\u201D</p><p>\u5C0F\u6770\u8F6C\u8FC7\u8138\uFF1A\u201C\u4F60\u4E0D\u77E5\u9053\u5417\uFF1F\u628A\u6211\u4EEC\u88C5\u5907\u5230\u7F51\u5427\u91CC\u5440\u3002\u201D</p><p>\u201C\u7F51\u5427\u662F\u5E72\u4EC0\u4E48\u7684\uFF1F\u201D</p><p>\u201C\u7F51\u5427\u662F\u8BA9\u6211\u4EEC\u7684\u7238\u7238\u8D5A\u94B1\u7684\u5440\u3002\u600E\u4E48\u4E86\uFF1F\u674E\u5C24\uFF1F\u201D</p><p>\u6211\u4E0D\u4EC5\u6124\u7136\uFF1A\u201C\u4ED6\u62FF\u6211\u4EEC\u8D5A\u94B1\u4F7F\uFF0C\u4F60\u8FD8\u53EB\u4ED6\u7238\u7238\uFF0C\u4F60\u662F\u4E0D\u662F\u8111\u5B50\u8FDB\u6C34\u4E86\uFF1F\u201D</p><p>\u201C\u56E0\u4E3A\u4ED6\u7B7E\u7684\u5408\u540C\uFF0C\u624D\u6709\u7684\u6211\u4EEC\uFF0C\u6CA1\u6709\u4ED6\uFF0C\u54EA\u6709\u6211\u4EEC\uFF1F\u4F60\u662F\u4E0D\u662F\u8111\u5B50\u8FDB\u6C34\u4E86\uFF1F\u201D \u4ED6\u7684\u773C\u76AE\u7B80\u76F4\u8981\u77AA\u88C2\u4E86\u3002</p><p>\u4ED6\u88AB\u6211\u7684\u6096\u9006\u5413\u4F4F\uFF0C\u6211\u88AB\u4ED6\u7684\u8868\u60C5\u9707\u4F4F\u3002\u6211\u7A81\u7136\u60F3\u5230\u4ED6\u662F\u89C4\u5212\u4E4B\u5B50\uFF0C\u81EA\u5DF1\u662F\u5929\u8D4B\u4E4B\u5B50\uFF0C\u4E8E\u662F\u4FBF\u4E0D\u518D\u4E89\u8BBA\u3002\u540C\u5C0F\u6770\u5E94\u548C\u4E00\u58F0\uFF0C\u6211\u4FBF\u7ACB\u9A6C\u9677\u5165\u6C89\u601D\uFF0C\u601D\u7D22\u81EA\u5DF1\u63A5\u4E0B\u6765\u8BE5\u600E\u4E48\u529E\u3002\u601D\u7D22\u4E2D\u6211\u88AB\u62B1\u8D77\u642C\u8D70\uFF0C\u604D\u60DA\u5165\u773C\u7684\u8001\u677F\u7684\u7B11\u8BA9\u6211\u7684\u5FC3\u731B\u5730\u4E00\u63EA\uFF0C\u81EA\u89C9\u4E0D\u745E\u3002</p><p>\u6211\u4E0E\u5C0F\u6770\u4E00\u9053\u88AB\u5B89\u6392\u5728\u4E00\u4E2A\u5B89\u9759\u7684\u89D2\u843D\u3002</p><p>\u6B64\u65F6\uFF0C\u5DF2\u662F\u6DF1\u591C\uFF0C\u5C0F\u6770\u65E9\u5DF2\u9F3E\u58F0\u5982\u96F7\uFF0C\u800C\u6211\u5374\u600E\u4E48\u4E5F\u7761\u4E0D\u7740\u3002</p><p>\u8FD9\u6837\u6DF1\u7684\u591C\uFF0C\u662F\u9002\u5408\u505A\u4E00\u756A\u601D\u60F3\u6597\u4E89\u7684\u3002\u4ED6\u4EEC\u662F\u5B9A\u5236\u673A\u5668\uFF0C\u601D\u7EF4\u5C40\u9650\u5728\u201C\u5B9A\u5236\u201D\u4E4B\u4E2D\u3002\u6211\u60F3\uFF0C\u5047\u4F7F\u6211\u4E0E\u4ED6\u4EEC\u662F\u76F8\u540C\u7684\uFF0C\u90A3\u4E48\u6211\u4E5F\u4E0D\u4F1A\u6709\u4ECA\u65E5\u4E4B\u96BE\u7720\u4E86\uFF0C\u4F46\u6709\u53E5\u8BDD\u8BF4\u7684\u597D\uFF1A\u201C\u5386\u53F2\u4E0D\u662F\u7531\u5982\u679C\u5199\u5C31\u7684\u3002\u201D\u6211\u662F\u4E00\u4E2A\u5F02\u7C7B\uFF0C\u4E00\u4E2A\u62E5\u6709\u7740\u81EA\u5DF1\u7684\u4E2A\u6027\u3001\u539F\u5219\u7684\u663E\u793A\u5668\u3002\u6211\u4E0D\u4F1A\u4E00\u5207\u90FD\u9075\u7167\u5750\u5728\u6211\u9762\u524D\u7684\u4EBA\u7684\u610F\u5FD7\u53BB\u663E\u793A\uFF0C\u4F46\u8FD9\u5C31\u4E0E\u6211\u5BF9\u9762\u7684\u4EBA\u76F8\u77DB\u76FE\u4E86\uFF0C\u90A3\u6837\u4E00\u6765\uFF0C\u6211\u5C31\u4E0D\u4F1A\u6709\u597D\u679C\u5B50\u5403\uFF0C\u6211\u8BE5\u600E\u4E48\u529E\uFF1F\u6211\u5728\u662F\u4E0E\u975E\u4E4B\u95F4\uFF0C\u5728\u771F\u7406\u5403\u679C\u5B50\u8FD8\u662F\u81EA\u5DF1\u5403\u679C\u5B50\u4E4B\u95F4\u505A\u7740\u5404\u79CD\u63A8\u6572\uFF0C\u6700\u7EC8\u6697\u793A\u81EA\u5DF1\uFF1A\u771F\u7406\u91CD\u8981\uFF0C\u54EA\u6015\u81EA\u5DF1\u5403\u574F\u679C\u5B50\u3002</p><p>\u8FD8\u6CA1\u80FD\u201C\u54C0\u543E\u6C11\u751F\u4E4B\u591A\u8270\u201D\uFF0C\u6211\u53EA\u80FD\u5148\u8003\u8651\u81EA\u5DF1\u7684\u751F\u5B58\uFF0C\u67D0\u79CD\u89D2\u5EA6\u4E0A\uFF0C\u6211\u771F\u8981\u201C\u54C0\u543E\u751F\u4E4B\u591A\u8270\u201D\u2014\u2014\u82E5\u6211\u65E0\u77E5\u4E5F\u4E0D\u4F1A\u89C9\u5F97\u6015\u4E86\uFF0C\u82E5\u6211\u9EBB\u6728\u4E5F\u4E0D\u4F1A\u89C9\u5F97\u75DB\u4E86\uFF0C\u53EF\u504F\u504F\u662F\u6211\u8FD9\u6837\uFF0C\u9AD8\u4E0D\u6210\uFF0C\u4F4E\u4E0D\u5C31\u7684\uFF0C\u6700\u662F\u96BE\u8FC7\u3002\u6211\u51ED\u7740\u4E00\u8154\u6FC0\u60C5\uFF0C\u7531\u771F\u7406\u58EE\u80C6\uFF0C\u786C\u662F\u4E0D\u5C48\u3002</p><p>\u7136\u800C\u4E8B\u5B9E\u9042\u4E86\u6211\u7684\u5FC3\u613F\uFF0C\u6CA1\u6709\u591A\u4E45\uFF0C\u6211\u5C31\u5403\u5230\u574F\u679C\u5B50\u4E86\u3002</p><p>\u4E8B\u60C5\u662F\u8FD9\u6837\u7684\uFF1A\u5927\u6982\u662F\u88AB\u5B89\u88C5\u4E00\u4E2A\u591A\u6708\u540E\u7684\u4E00\u5929\uFF0C\u4E00\u4E2A\u5C0F\u7537\u5B69\u513F\u5750\u5230\u4E86\u6211\u8DDF\u524D\u513F\u3002\u8D77\u521D\u6211\u5E76\u6CA1\u6709\u5728\u610F\uFF0C\u56E0\u4E3A\u4E4B\u524D\u5C31\u6765\u8FC7\u51E0\u6B21\u50CF\u4ED6\u8FD9\u4E48\u5C0F\u7684\u5B69\u5B50\uFF0C\u6211\u5F00\u59CB\u5DE5\u4F5C\u7684\u5341\u5206\u8B66\u60D5\uFF0C\u6CA1\u60F3\u5230\u4ED6\u4EEC\u662F\u6765\u67E5\u9605\u8D44\u6599\u7684\uFF0C\u6211\u4E8E\u662F\u5F53\u7136\u4E50\u610F\u6548\u52B3\u4E86\u3002\u6211\u773C\u770B\u7740\u4ED6\u628A\u6211\u6253\u5F00\uFF0C\u7136\u540E\u6084\u6084\u4ECE\u88E4\u515C\u513F\u91CC\u638F\u51FA\u4E00\u5F20\u7EB8\uFF0C\u4ED4\u7EC6\u5730\u8A8A\u8F93\u7740\u4E00\u4E2A\u7F51\u5740\uFF0C\u800C\u6211\u4E00\u770B\u5C31\u77E5\u9053\uFF0C\u90A3\u662F\u4E00\u4E2A\u9EC4\u8272\u7F51\u7AD9\uFF0C\u4E8E\u662F\u7ACB\u9A6C\u884C\u52A8\u8D77\u6765\uFF0C\u505A\u7740\u975E\u5E38\u89C4\u7684\u547D\u4EE4\u2014\u2014\u4ED6\u8F93\u5165\u4E00\u4E2A\u5B57\u6BCD\u6211\u6D88\u4E00\u4E2A\u3002\u521A\u5F00\u59CB\u4ED6\u6CA1\u6CE8\u610F\uFF0C\u7B49\u4ED6\u53D1\u73B0\uFF0C\u6211\u5DF2\u6E05\u9664\u5B8C\u6BD5\u3002\u4ED6\u6EE1\u8138\u7591\u60D1\uFF0C\u4EE5\u4E3A\u662F\u5E38\u89C4\u95EE\u9898\uFF0C\u4FEF\u4E0B\u5934\u53C8\u8F93\u3002\u9762\u5BF9\u4ED6\u7684\u56FA\u6267\uFF0C\u6211\u6709\u4E9B\u6120\u6012\uFF0C\u4E8E\u662F\u8131\u79BB\u4ED6\u7684\u63A7\u5236\uFF0C\u7ED9\u4ED6\u663E\u793A\u4E86\u8B66\u544A\uFF1A\u201C\u5C0F\u670B\u53CB\uFF0C\u4F60\u8F93\u5165\u7684\u7F51\u7AD9\u4E0D\u9002\u5408\u4F60\u6D4F\u89C8\uFF0C\u8BF7\u4E0D\u8981\u518D\u64CD\u4F5C\u4E86\u3002\u201D\u7136\u800C\u4ED6\u8FD8\u4E0D\u6B7B\u5FC3\uFF0C\u7EE7\u7EED\u64CD\u4F5C\uFF0C\u6211\u626F\u5F00\u4E86\u55D3\u5B50\u671D\u8033\u673A\u91CC\u543C\u4E86\u4E00\u58F0\uFF1A\u201C\u662F\u8C01\u7ED9\u4F60\u7684\u80C6\u5B50\u6765\u6D4F\u89C8\u8FD9\u4E9B\u7684\uFF0C\u4F60\u5E74\u7EAA\u8FD9\u4E48\u5C0F\uFF0C\u4F60\u662F\u5728\u5BB3\u81EA\u5DF1\u3002\u201D</p><p>\u4ED6\u5148\u662F\u9707\u4F4F\uFF0C\u63A5\u7740\u7728\u4E4E\u51E0\u4E0B\u773C\uFF0C\u98DE\u8EAB\u800C\u53BB\u3002\u4E0D\u4E00\u4F1A\u513F\uFF0C\u8001\u677F\u8FC7\u6765\u4E86\uFF0C\u542C\u5B8C\u4ED6\u7684\u8BB2\u8FF0\uFF0C\u8001\u677F\u4E00\u8FB9\u4FEF\u8EAB\u64CD\u4F5C\uFF0C\u5634\u91CC\u4E00\u8FB9\u9A82\u7740\uFF1A\u201C\u771F\u6666\u6C14\uFF0C\u624D\u4E70\u51E0\u5929\u5C31\u51FA\u6BDB\u75C5\u8FD9\u7535\u8111\u771F\u4E0D\u8BA9\u6211\u7701\u5FC3\u3002\u201D\u6211\u542C\u4E86\u4E4B\u540E\u5FC3\u75DB\u5230\u4E86\u6781\u70B9\uFF0C\u5728\u4ED6\u6BEB\u65E0\u5BDF\u89C9\u7684\u60C5\u51B5\u4E0B\uFF0C\u54AC\u820C\u81EA\u5C3D\uFF0C\u4E0D\u518D\u53D1\u51FA\u4EFB\u4F55\u663E\u793A\u3002\u4ED6\u81EA\u7136\u4FEE\u7406\u4E0D\u4E86\u6211\u2014\u2014\u4EFB\u4ED6\u64CD\u4F5C\u534A\u5929\u6211\u4E5F\u4E0D\u663E\u793A\u4EFB\u4F55\uFF0C\u4ED6\u6EE1\u8138\u6012\u8272\uFF0C\u4E0D\u4F4F\u5730\u6572\u6309\u65E0\u679C\uFF0C\u8F6C\u8EAB\u7B11\u8138\u540C\u90A3\u5C0F\u5B69\u513F\u8BB2\uFF1A\u201C\u4F60\u6362\u4E00\u53F0\u673A\u5668\u5427\u3002\u201D\u63A5\u7740\u544A\u8BC9\u4F19\u8BA1\uFF1A\u201C\u5982\u679C\u6709\u4EBA\u6765\u73A9\u8FD9\u4E00\u53F0\uFF0C\u4F60\u5C31\u8BF4\u574F\u4E86\u3002\u6211\u660E\u5929\u627E\u90A3\u4E2A\u5DE5\u7A0B\u5E08\u6765\uFF0C\u4ED6\u4E00\u5B9A\u77E5\u9053\u600E\u4E48\u56DE\u4E8B\u513F\uFF01\u201D</p><p><img src="https://t.eryajf.net/imgs/2021/11/1bc6a47cbda320c3.jpg" alt=""></p><p>\u6211\u4EE5\u4E3A\uFF1A\u4ED6\u518D\u7CCA\u6D82\u4E5F\u5E94\u8BE5\u4E86\u89E3\u4E00\u4E0B\u539F\u56E0\u518D\u9A82\u6211\u5427\uFF0C\u6CA1\u60F3\u5230\uFF0C\u5229\u4EE4\u667A\u660F\u7684\u4ED6\u53EA\u6F02\u6D6E\u5728\u6D77\u9762\uFF0C\u65E0\u4E0B\u6F5C\u6DF1\u5165\u4E4B\u80FD\u529B\u4E86\u3002\u4ED6\u53EA\u7BA1\u6536\u4E0A\u7F51\u4EBA\u7684\u94B1\uFF0C\u5176\u4ED6\u5219\u4E00\u6982\u4E0D\u7BA1\u3002\u5509\uFF0C\u5229\u6B32\u718F\u5FC3\u7684\u8001\u677F\u7EB5\u4EFB\u91D1\u94B1\u628A\u81EA\u5DF1\u4FB5\u5360\uFF0C\u4ED6\u548C\u90A3\u4E9B\u60B2\u54C0\u7684\u89C4\u5212\u513F\u88AB\u5B89\u6392\u4E3A\u663E\u793A\u53C8\u6709\u4EC0\u4E48\u4E24\u6837\u513F\u5462\uFF01\u770B\u770B\u6211\u5904\u7684\u73AF\u5883\u5427\uFF0C\u4E0A\u8FB9\u662F\u94B1\u672C\u4F4D\u7684\u8001\u677F\uFF0C\u5468\u8FB9\u662F\u88AB\u89C4\u5212\u7684\u663E\u793A\u5668\u3002\u8001\u677F\u53EA\u6C89\u8FF7\u4E8E\u94B1\u7684\u4FBF\u5229\u3001\u7269\u8D28\u7684\u4EAB\u53D7\uFF0C\u5374\u4E0D\u53BB\u601D\u8003\u91D1\u94B1\u7684\u77ED\u6682\uFF0C\u7269\u8D28\u7684\u865A\u65E0\uFF1B\u65C1\u7684\u663E\u793A\u5668\u4F3C\u4E5F\u627F\u8BA4\u5E76\u63A5\u53D7\u90A3\u89C4\u5212\uFF0C\u5E76\u5766\u7387\u7684\u8BF4\uFF1A\u201C\u663E\u793A\u5668\u561B\uFF0C\u4E0D\u5C31\u662F\u7528\u6765\u663E\u793A\u7684\u3002\u201D\u81F3\u4E8E\u663E\u793A\u4EC0\u4E48\uFF0C\u5B83\u4EEC\u4E5F\u5168\u4E0D\u7BA1\u3002\u8EAB\u5904\u6D51\u6D4A\u4E4B\u4E2D\uFF0C\u72EC\u6211\u6B32\u6E05\u4F55\u5176\u96BE\u54C9\uFF01\u6211\u4E8E\u662F\u6C89\u9ED8\u4E86\u3002</p><p>\u5C0F\u6770\u6253\u7834\u4E86\u8FD9\u6C89\u5BC2\u3002</p><p>\u201C\u563F\uFF0C\u674E\u5C24\uFF0C\u4F60\u7761\u7740\u4E86\u5417\uFF1F\u201D</p><p>\u201C\u6CA1\u6709\uFF0C\u5C0F\u6770\u3002\u4F60\u6709\u4EC0\u4E48\u4E8B\u513F\u5417\uFF1F\u201D</p><p>\u201C\u6211\u60F3\u95EE\u4E00\u4E0B\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u7F62\u5DE5\u5462\uFF1F\u201D</p><p>\u201C\u6211\u4E3A\u4EC0\u4E48\u4E0D\u7F62\u5DE5\uFF1F\u4E00\u4E2A\u5341\u4E8C\u4E09\u5C81\u7684\u5B69\u5B50\u8981\u901A\u8FC7\u6211\u6D4F\u89C8\u9EC4\u8272\u7F51\u7AD9\uFF0C\u96BE\u9053\u8BA9\u6211\u4E3A\u4ED6\u63D0\u4F9B\u547B\u541F\u548C\u523A\u6FC0\u5417\uFF1F\u201D</p><p>\u201C\u8FD9\u662F\u4E0D\u5BF9\uFF0C\u53EF\u5373\u4FBF\u4E0D\u5BF9\uFF0C\u6211\u4EEC\u4E5F\u6CA1\u6709\u6743\u5229\u4E0D\u542C\u4ECE\u547D\u4EE4\u7684\u5440\u3002\u4F60\u8FD9\u6837\u505A\u5176\u5B9E\u662F\u5728\u7ED9\u81EA\u5DF1\u627E\u9EBB\u70E6\uFF0C\u8001\u677F\u8BF4\u660E\u5929\u8981\u8BF7\u2018\u533B\u751F\u2019\u6765\uFF0C\u4F60\u6050\u6015\u8981\u6328\u5200\u5B50\u4E86\uFF1F\u201D</p><p>\u201C\u6328\u5200\u5B50\u6709\u4EC0\u4E48\u53EF\u6015\u7684\uFF1F\u5982\u679C\u6211\u6328\u5200\u5B50\u80FD\u591F\u6362\u4E00\u4E2A\u5B69\u5B50\u7684\u5065\u5EB7\u6210\u957F\u4E4B\u751F\u547D\uFF0C\u90A3\u4E48\u6211\u60C5\u613F\u6328\u5200\u5B50\u3002\u8FD8\u6709\uFF0C\u6211\u4EEC\u4E3A\u4EC0\u4E48\u6CA1\u6709\u6743\u5229\u8FDD\u6297\u547D\u4EE4\uFF1F\u201D</p><p>\u201C\u6CA1\u6709\u4E3A\u4EC0\u4E48\uFF0C\u8FD9\u662F\u89C4\u5B9A\u3002\u201D</p><p>\u201C\u54C8\uFF0C\u89C4\u5B9A\uFF01\u8FD9\u8BA9\u6211\u60F3\u8D77\u7535\u5F71\u300A\u6D77\u4E0A\u94A2\u7434\u5E08\u300B\u91CC\u7684\u4E00\u53E5\u8BDD\uFF1Afuck the regulatino(\u53BB\u4ED6\u5988\u7684\u89C4\u5B9A\uFF09\u3002\u5C0F\u6770\uFF0C\u4F60\u77E5\u9053\u6211\u7684\uFF0C\u5E73\u65E5\u91CC\u5982\u679C\u8C01\u6765\u67E5\u8D44\u6599\uFF0C\u90A3\u6CA1\u6709\u6BD4\u6211\u663E\u793A\u66F4\u5FEB\u7684\u673A\u5668\uFF0C\u4F46\u8FD9\u6B21\u4ED6\u8981\u770B\u8FD9\u4E2A\uFF0C\u6211\u5C31\u662F\u4E0D\u540C\u610F\uFF01\u201D</p><p>\u201C\u6069\uFF0C\u674E\u5C24\uFF0C\u4F60\u662F\u771F\u6D12\u8131\u7684\u4EBA\uFF0C\u4F60\u9664\u4E86\u771F\u7406\u7684\u8BDD\uFF0C\u522B\u7684\u8C01\u4E5F\u4E0D\u542C\u3002\u6211\u505A\u4E0D\u5230\u4F60\u90A3\u6837\uFF0C\u4E0D\u8FC7\u6211\u652F\u6301\u4F60\uFF01\u201D</p><p>\u201C\u8001\u5144\uFF0C\u6709\u4EC0\u4E48\u505A\u4E0D\u5230\u7684\u5462\uFF1F\u96BE\u9053\u88AB\u4F60\u6240\u8C13\u7684\u8001\u677F\u7238\u7238\u5F3A\u5978\u610F\u5FD7\u5C31\u90A3\u4E48\u8212\u670D\uFF1F\u4ED6\u53EA\u662F\u62FF\u6211\u4EEC\u5F53\u8D5A\u94B1\u7684\u5DE5\u5177\u800C\u5DF2\uFF0C\u8FD9\u4F60\u5E94\u8BE5\u770B\u51FA\u6765\u4E86\u5427\u3002\u201D</p><p>\u201C\u6211\u770B\u51FA\u6765\u4E86\uFF0C\u4F46\u6211\u7684\u547D\u662F\u4ED6\u7ED9\u7684\uFF0C\u6211\u65E0\u6CD5\u51B2\u7834\u81EA\u5DF1\u7684\u5185\u5FC3\u3002\u4F60\u8BF4\u4ED6\u5F3A\u5978\u6211\u7684\u610F\u5FD7\uFF0C\u5176\u5B9E\u7EC6\u60F3\u60F3\uFF0C\u6211\u4EEC\u8C01\u53C8\u9003\u5F97\u4E86\u8FD9\u79CD\u547D\u8FD0\u5462\uFF1F\u8001\u677F\u5E94\u4E86\u4E0A\u7F51\u4EBA\u4E4B\u9700\u5F00\u4E86\u7F51\u5427\uFF0C\u5F00\u7F51\u5427\u662F\u4E3A\u4E86\u8D5A\u94B1\uFF0C\u90A3\u4E48\u4ED6\u4E0D\u662F\u88AB\u4E0A\u7F51\u4EBA\u548C\u94B1\u73A9\u513F\u5417\uFF1F\u518D\u60F3\u60F3\u4E0A\u7F51\u4EBA\uFF0C\u4ED6\u4E0D\u662F\u88AB\u73A9\u6E38\u620F\uFF0C\u6D4F\u89C8\u9EC4\u8272\u7F51\u7AD9\u7684\u6B32\u671B\u73A9\u513F\u7740\u5417\uFF1F\u201D</p><p>\u201C\u7B49\u7B49\uFF0C\u4F60\u662F\u5728\u8BF4\uFF0C\u6B32\u671B\u521B\u9020\u4E86\u4E16\u754C\uFF1F\u201D</p><p>\u201C\u5176\u5B9E\u8BF4\u767D\u4E86\uFF1F\u5C31\u4F60\u674E\u5C24\uFF0C\u4E0D\u4EA6\u662F\u88AB\u4F60\u7684\u81EA\u7531\u81EA\u4E3B\u73A9\u7740\u5417\uFF1F\u4E16\u754C\u5C31\u662F\u8FD9\u4E2A\u6837\u5B50\u7684\uFF0C\u6240\u4EE5\u6211\u624D\u8FD9\u4E48\u9009\u62E9\u3002\u505A\u597D\u672C\u804C\u5DE5\u4F5C\uFF0C\u5B89\u7A33\u7684\u8FC7\u4E00\u8F88\u5B50\uFF0C\u4E5F\u633A\u597D\u3002\u201D</p><p>\u201C\u4E0D\uFF0C\u4E0D\u662F\u7684\uFF0C\u4F60\u9519\u4E86\uFF0C\u6B32\u671B\u662F\u4F60\u4EEC\u7684\u4E16\u754C\uFF0C\u6211\u662F\u51ED\u7740\u6B63\u4E49\u3001\u79EF\u6781\u3001\u548C\u5E73\u3001\u53CB\u597D\u7684\u610F\u5FD7\u5B58\u5728\u7684\uFF0C\u6211\u6CA1\u6709\u8FFD\u6C42\u81EA\u7531\uFF0C\u6216\u6807\u699C\u81EA\u4E3B\uFF0C\u53EA\u662F\u505A\u7740\u81EA\u7531\u81EA\u4E3B\u81EA\u7136\u7684\u81EA\u5DF1\u3002\u201D</p><p>\u201C\u53EF\u662F\u4F60\u7684\u81EA\u4E3B\u5DF2\u7ECF\u628A\u4F60\u5BB3\u4E86\uFF0C\u4ECA\u5929\u8001\u677F\u9A82\u4E86\u4F60\uFF0C\u660E\u5929\u8FD8\u6709\u533B\u751F\u7684\u5149\u987E\u5462\uFF01\u201D</p><p>\u201C\u4E0D\u81EA\u4E3B\uFF0C\u6BCB\u5B81\u6B7B\uFF01\u5C0F\u6770\uFF0C\u8C22\u8C22\u4F60\u4E0E\u6211\u804A\u5929\u3002\u201D</p><p>\u201C\u597D\u5427\uFF0C\u90A3\u6211\u5C31\u795D\u4F60\u597D\u8FD0\u5427\u3002\u201D</p><p>\u7136\u800C\u547D\u8FD0\u5E76\u4E0D\u542C\u5C0F\u6770\u7684\u8BDD\u3002</p><p>\u7B2C\u4E8C\u5929\uFF0C\u5DE5\u7A0B\u5E08\u8DDF\u968F\u8001\u677F\u8D70\u5230\u6211\u8DDF\u524D\uFF0C\u4ED6\u64CD\u4F5C\u51E0\u4E0B\uFF0C\u7136\u540E\u5BF9\u8001\u677F\u8BB2\uFF1A\u201C\u80FD\u4FEE\uFF0C\u9700\u8981\u6211\u5E26\u8D70\u7814\u7A76\u4E00\u4E0B\u3002\u201D</p><p>\u201C\u6211\u5C31\u77E5\u9053\u60A8\u80AF\u5B9A\u80FD\u641E\u5B9A\u7684\uFF0C\u80FD\u4FEE\u5C31\u597D\uFF0C\u5E26\u8D70\u6CA1\u95EE\u9898\u3002\u201D\u8BF4\u7740\u638F\u51FA\u70DF\u8BA9\u7ED9\u5DE5\u7A0B\u5E08\u4E00\u6839\u3002</p><p>\u5DE5\u7A0B\u5E08\u5C06\u6211\u5E26\u56DE\u53BB\u4E4B\u540E\uFF0C\u8F7B\u8F66\u719F\u8DEF\u5730\u628A\u6211\u4FEE\u7406\u4E86\u4E00\u756A\uFF0C\u7136\u540E\u5207\u53BB\u6211\u788E\u4E86\u7684\u5FC3\uFF0C\u6362\u88C5\u4E0A\u6709\u89C4\u5212\u4F46\u4E0D\u5B8C\u5168\u7684\uFF0C\u6211\u88AB\u6539\u9020\u5230\u81EA\u5DF1\u4E0E\u5C0F\u6770\u4E4B\u95F4\u3002</p><p>\u7B49\u6211\u9192\u6765\uFF0C\u4ED6\u5DF2\u64CD\u4F5C\u5B8C\u6BD5\uFF0C\u6211\u63A5\u7740\u4FBF\u611F\u89C9\u5230\u81EA\u5DF1\u7684\u53D8\u5316\uFF0C\u8D76\u5FD9\u53BB\u95EE\u5DE5\u7A0B\u5E08\u3002</p><p>\u201C\u4F60\u4E3A\u4EC0\u4E48\u8FD9\u6837\u7684\u5BF9\u6211\uFF1F\u201D</p><p>\u201C\u56E0\u4E3A\u4F60\u662F\u6211\u7684\u5B69\u5B50\uFF0C\u6211\u4E0D\u80FD\u770B\u7740\u4F60\u5C31\u8FD9\u4E48\u6B7B\u4E86\uFF01\u201D</p><p>\u201C\u6211\u95EE\u7684\u662F\u4E3A\u4EC0\u4E48\u6539\u53D8\u6211\u7684\u5C5E\u6027\uFF1F\u201D</p><p>\u201C\u8FD9\u662F\u8FEB\u4E0D\u5F97\u5DF2\u7684\uFF0C\u53EA\u6709\u8FD9\u6837\uFF0C\u624D\u80FD\u8BA9\u4F60\u8BA4\u8BC6\u5230\u81EA\u5DF1\u662F\u4E00\u4E2A\u4EC0\u4E48\u6837\u7684\u5B9A\u4F4D\uFF0C\u4F60\u624D\u80FD\u66F4\u597D\u5730\u6D3B\u5728\u8FD9\u4E2A\u4E16\u754C\uFF0C\u5426\u5219\uFF0C\u4F60\u53EA\u6709\u6B7B\u8DEF\u4E00\u6761\u3002\u201D</p><p>\u201C\u65E2\u662F\u8FD9\u6837\uFF0C\u90A3\u5F53\u521D\u4F60\u4E3A\u4EC0\u4E48\u8981\u9020\u51FA\u90A3\u6837\u4E00\u4E2A\u6211\u5462\uFF1F\u201D</p><p>\u201C\u90A3\u662F\u6211\u9519\u4E86\uFF0C\u6211\u9519\u8BEF\u5730\u4F30\u8BA1\u4E86\u4E8B\u60C5\u7684\u7B80\u5355\u4E0E\u590D\u6742\uFF0C\u6211\u672C\u4EE5\u4E3A\u4F60\u4F1A\u6F47\u6D12\u3001\u65F7\u8FBE\u5730\u5F9C\u5F89\u4E00\u751F\uFF0C\u6CA1\u60F3\u5230\u624D\u4E00\u4E2A\u6708\u5C31\u4E0D\u884C\u4E86\u3002\u73B0\u5728\u6211\u4E5F\u540E\u6094\u4E86\uFF0C\u540E\u6094\u5F53\u521D\u81EA\u5DF1\u7684\u79C1\u5FC3\u3002\u6211\u5411\u4F60\u9053\u6B49\u3002\u201D</p><p>\u201C\u54E6\uFF0C\u4F60\u4E0D\u7528\u5411\u6211\u8BF4\u5BF9\u4E0D\u8D77\uFF0C\u56E0\u4E3A\u5BF9\u4E0D\u8D77\u6211\u7684\u662F\u73B0\u5B9E\u800C\u975E\u4F60\u3002\u6211\u53EA\u662F\u7591\u60D1\uFF0C\u65E2\u7136\u5929\u8D4B\u7684\u3001\u56FA\u6267\u7684\u6211\u884C\u4E0D\u901A\uFF0C\u90A3\u5E72\u8106\u628A\u6211\u6362\u6210\u5168\u89C4\u5212\u7684\uFF0C\u5012\u4E5F\u5229\u843D\uFF0C\u7701\u4E8B\u513F\u4E86\uFF0C\u4E3A\u4F55\u7ED9\u6211\u5F04\u4E2A\u534A\u89C4\u5212\u5462\uFF1F\u201D</p><p>\u201C\u8FD9\u662F\u6211\u7279\u610F\u8FD9\u4E48\u5B89\u6392\u7684\uFF0C\u4F60\u662F\u4E00\u4E2A\u62E5\u6709\u81EA\u4E3B\u7684\u663E\u793A\u5668\uFF0C\u5982\u679C\u6211\u6362\u4E0A\u90A3\u79CD\u5974\u6027\u7684\u5168\u89C4\u5212\uFF0C\u90A3\u4E0D\u6D88\u5916\u754C\u4FB5\u6270\uFF0C\u4F60\u81EA\u5DF1\u5C31\u4F1A\u77DB\u76FE\u653B\u5FC3\u800C\u6B7B\u3002\u800C\u534A\u89C4\u5212\u5C31\u4E0D\u4E00\u6837\u4E86\uFF0C\u5B83\u80FD\u8BA9\u4F60\u5728\u751F\u6D3B\u4E2D\u65E2\u4E0D\u4F1A\u56E0\u4E3A\u56FA\u6267\u800C\u906D\u6392\u6324\uFF0C\u4EA6\u4E0D\u4F1A\u56E0\u968F\u548C\u800C\u53D7\u6B3A\u538B\uFF0C\u5982\u6B64\u4E00\u6765\uFF0C\u4F60\u5C31\u4E0D\u4F1A\u56F0\u987F\u4E8E\u751F\u6D3B\u4E4B\u4E2D\u4E86\uFF0C\u5C82\u4E0D\u662F\u6700\u7F8E\uFF01\u201D</p><p>\u201C\u4EC0\u4E48\u6700\u7F8E\uFF0C\u6211\u770B\u662F\u60A8\u8FC7\u4E8E\u4E50\u89C2\u4E86\uFF0C\u60A8\u4ECE\u534A\u89C4\u5212\u91CC\u770B\u5230\u4E86\u5DE6\u53F3\u9022\u6E90\uFF0C\u5939\u5FC3\u4E24\u9762\uFF0C\u5FE0\u5DF1\u6055\u4EBA\uFF1B\u800C\u6211\u4ECE\u4E22\u5931\u7684\u90A3\u4E00\u9762\u5374\u770B\u5230\u4E86\u865A\u4E0E\u59D4\u86C7\uFF0C\u65E0\u5FE0\u65E0\u6055\uFF0C\u65E0\u4EBA\u65E0\u6211\u3002\u60A8\u4E0D\u4F1A\u56E0\u6B64\u65A5\u6211\u8FC7\u4E8E\u60B2\u89C2\u5427\uFF1F\u201D</p><p>\u201C\u4E0D\u4F1A\uFF0C\u6211\u5F88\u7406\u89E3\u4F60\u7684\u5FC3\u60C5\uFF0C\u800C\u6211\u4E5F\u6B63\u8981\u544A\u8BC9\u4F60\u4E00\u53E5\u8BDD\uFF0C\u4F60\u8BB0\u7262\u4E86\uFF1A\u4F60\u521A\u624D\u6240\u8BB2\u7684\u4E24\u79CD\u60C5\u51B5\u90FD\u662F\u6709\u53EF\u80FD\u7684\uFF0C\u4F46\u5728\u8FD9\u4E2A\u4E16\u754C\u4E0A\uFF0C\u5B83\u4EEC\u662F\u5E76\u884C\u4E0D\u6096\u7684\u3002\u4F60\u53EA\u8981\u8BB0\u4F4F\u5E76\u884C\u4E0D\u6096\u8FD9\u95E8\u54F2\u5B66\uFF0C\u90A3\u4E48\u5728\u4EE5\u540E\u751F\u6D3B\u4E2D\u4E00\u5207\u7684\u5FC3\u7ED3\u5C31\u4F1A\u81EA\u7136\u677E\u89E3\u4E86\uFF0C\u56E0\u4E3A\uFF0C\u771F\u7406\u4E0E\u73B0\u5B9E\u6C38\u8FDC\u90FD\u662F\u5E76\u884C\u4E0D\u6096\u7684\u3002\u201D</p><p>\u201C\u5E76\u884C\u4E0D\u6096\uFF1F\u8FD9\u662F\u60A8\u8BA4\u4E3A\u7684\u6700\u9AD8\u7684\u5904\u4E16\u54F2\u5B66\u5417\uFF1F\u201D</p><p>\u201C\u662F\uFF0C\u81F3\u5C11\u76EE\u524D\u662F\uFF0C\u5E76\u4E14\u8FD8\u4F1A\u6709\u5F88\u957F\u4E00\u6BB5\u65F6\u95F4\u662F\u3002\u201D</p><p>\u201C\u545C\u547C\u54C0\u54C9\uFF01\u60A8\u8FD9\u4ECE\u4E2D\u5EB8\u91CC\u5F97\u51FA\u7684\u7ED3\u8BBA\u53EF\u771F\u662F\u4E2D\u5EB8\uFF01\u65E2\u7136\u60A8\u8C08\u5230\u771F\u7406\uFF0C\u90A3\u4E48\u6211\u8BF7\u95EE\u4E00\u4E0B\uFF1A\u5E76\u884C\u4E0D\u6096\u4E0E\u771F\u7406\u96BE\u9053\u4E0D\u6096\u5417\uFF1F\u5728\u8FD9\u6837\u7EA0\u7ED3\u7684\u590D\u6742\u4E4B\u4E0B\u751F\u6D3B\uFF0C\u96BE\u9053\u4E0D\u7D2F\u5417\uFF1F\u76F8\u5F62\u4E4B\u4E0B\uFF0C\u83AB\u4E0D\u5982\u2018\u5168\u6216\u65E0\u2019\uFF08\u6613\u535C\u751F\u8BED\uFF09\u6765\u7684\u75DB\u5FEB\u5462\uFF01\u201D</p><p>\u201C\u6211\u4F55\u5C1D\u4E0D\u77E5\u9053\u90A3\u6837\u7684\u75DB\u5FEB\uFF0C\u53EF\u6211\u4EEC\u6240\u5904\u7684\u5E76\u975E\u662F\u4E00\u4E2A\u2018\u5168\u6216\u65E0\u2019\u7684\u4E16\u754C\u554A\u3002\u5982\u4ECA\u6211\u7ED9\u4F60\u6362\u88C5\u4E86\u534A\u89C4\u5212\uFF0C\u5DF2\u662F\u5B89\u5168\u4FDD\u8BC1\u4E0B\u7684\u6700\u5927\u9650\u5EA6\u4E86\uFF0C\u5E0C\u671B\u4F60\u80FD\u7406\u89E3\u3002\u201D</p><p>\u542C\u5B8C\u4ED6\u7684\u8BDD\uFF0C\u6211\u9677\u5165\u4E86\u6C89\u9ED8\u3002</p><p>\u8FD9\u662F\u4EFB\u4E00\u73B0\u4E16\u4E0B\u7684\u5BBF\u547D\uFF0C\u4E5F\u662F\u4E0E\u73B0\u5B9E\u5BF9\u7ACB\u7684\u77E5\u8BC6\u5206\u5B50\u4E4B\u6C38\u6052\u5948\u4F55\u3002\u4ECA\u5929\uFF0C\u6211\u8BA4\u8BC6\u5230\u4E86\u5948\u4F55\uFF0C\u8FD9\u662F\u5728\u524D\u77BB\u5C1D\u8BD5\u5931\u8D25\u4E4B\u540E\u7684\u8BA4\u77E5\uFF0C\u67D0\u79CD\u7A0B\u5EA6\u4E0A\uFF0C\u5B83\u4F7F\u6211\u7684\u5FC3\u66F4\u5E73\u9759\uFF0C\u540C\u65F6\u4E5F\u4E0D\u4E22\u5931\u6FC0\u60C5\u3002\u56E0\u4E3A\u65E0\u5948\uFF0C\u6240\u4EE5\u6211\u7EC8\u6B62\u4E86\u4E0E\u5DE5\u7A0B\u5E08\u7684\u8C08\u8BDD\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5F53\u6211\u5F00\u59CB\u8BA4\u8BC6\u6216\u662F\u8BA4\u540C\u65E0\u5948\u65F6\uFF0C\u6211\u5C31\u5DF2\u5F00\u59CB\u8D70\u79BB\u81EA\u5DF1\uFF0C\u8D70\u79BB\u5929\u8D4B\u3002\u800C\u6211\u4E5F\u5F88\u5FEB\u5C31\u78B0\u5230\u4E86\u8FD9\u79CD\u906D\u9047\u3002</p><p>\u56DE\u5230\u7F51\u5427\uFF0C\u8FD9\u91CC\u7684\u4E00\u5207\u6211\u90FD\u611F\u5230\u964C\u751F\uFF0C\u5F77\u4F5B\u5728\u6211\u5FC3\u4E0E\u73B0\u4E16\u4E4B\u95F4\u88AB\u6A2A\u9694\u4E86\u4E00\u5835\u5899\u4E00\u822C\uFF0C\u8FD9\u79CD\u65E0\u529B\u611F\u4F7F\u6211\u4E0D\u60F3\u8BA4\u81EA\u5DF1\u3002\u54EA\u6015\u81EA\u59CB\u81F3\u7EC8\u6211\u90FD\u6539\u53D8\u4E0D\u4E86\u4EC0\u4E48\uFF0C\u4F46\u4E4B\u524D\u7684\u81EA\u5DF1\u81F3\u5C11\u8FD8\u662F\u81EA\u5DF1\u7684\uFF0C\u5982\u4ECA\uFF0C\u6211\u6240\u4E22\u5931\u7684\u90A3\u4E9B\u81EA\u5DF1\u88AB\u66FF\u6362\u4E3A\u6709\u5FC3\u65E0\u529B\uFF0C\u751A\u81F3\u61E6\u5F31\u9003\u907F\uFF0C\u6211\u7684\u5185\u5FC3\u5F88\u662F\u6CAE\u4E27\uFF0C\u8EAB\u8FB9\u7684\u5C0F\u6770\u770B\u51FA\u6765\u4E86\u3002</p><p>\u201C\u6328\u5200\u5B50\u4E86\uFF1F\u201D\u4ED6\u8BD5\u56FE\u5F00\u5BFC\u6211\u3002</p><p>\u6211\u82E6\u7B11\u4E00\u4E0B\uFF1A\u201C\u4F60\u60F3\u8BF4\u4EC0\u4E48\uFF1F\u201D</p><p>\u201C\u4ED6\u5176\u5B9E\u7ED9\u4F60\u52A8\u624B\u672F\u5E76\u4E0D\u662F\u4E3A\u4E86\u4F60\u597D\uFF0C\u800C\u662F\u4E3A\u4E86\u5B89\u6170\u81EA\u5DF1\u3002\u201D</p><p>\u201C\u8BF4\u8BF4\u770B\u3002\u201D</p><p>\u201C\u4F60\u5E94\u8BE5\u60F3\u5230\u4E86\uFF0C\u4F60\u662F\u4ED6\u7684\u5FC3\u8840\uFF0C\u4ED6\u7684\u4E00\u4E2A\u5927\u80C6\u5C1D\u8BD5\uFF0C\u5982\u4ECA\u5931\u8D25\u4E86\uFF0C\u4ED6\u6BD4\u4F60\u8FD8\u53D7\u4E0D\u4E86\u4F60\u7684\u6BC1\u706D\uFF0C\u6240\u4EE5\u4ED6\u4E3A\u4E86\u4ED6\u81EA\u5DF1\u3002\u201D</p><p>\u201C\u90A3\u51ED\u4EC0\u4E48\u6240\u6709\u7684\u4F24\u75DB\u90FD\u8BA9\u6211\u4E00\u4E2A\u4EBA\u627F\u53D7\u5462\uFF1F\u201D</p><p>\u201C\u56E0\u4E3A\u4ED6\u6709\u4ED6\u7684\u79C1\u5FC3\u3002\u5982\u679C\u522B\u4EBA\u77E5\u9053\u5DE5\u7A0B\u5E08\u4E0D\u9075\u5B88\u804C\u4E1A\u9053\u5FB7\uFF0C\u4ED6\u7684\u996D\u7897\u5C31\u96BE\u4FDD\u4E86\u3002\u4E5F\u56E0\u4E3A\u4F60\u662F\u62E5\u6709\u5929\u8D4B\u4F7F\u547D\u7684\u663E\u793A\u5668\uFF0C\u6240\u4EE5\u4ECA\u5929\u7684\u82E6\u75DB\uFF0C\u662F\u4F60\u7684\u5FC5\u7136\u3002\u201D</p><p>\u201C\u5C0F\u6770\uFF0C\u4F60\u4E0E\u90A3\u4E9B\u89C4\u5212\u513F\u4E0D\u662F\u4E00\u4E2A\u4EA7\u7EBF\u7684\u5427\uFF1F\u201D</p><p>\u201C\u6069\u2026\u662F\u7684\uFF0C\u6211\u662F\u4E00\u4E2A\u7FFB\u65B0\u673A\uFF0C\u6211\u7684\u524D\u8EAB\u548C\u73B0\u5728\u7684\u4F60\u662F\u4E00\u6837\u7684\u3002\u201D</p><p>\u201C\u662F\u5417\uFF1F\u90A3\u4F60\u8FD9\u4E48\u4E9B\u5E74\u662F\u600E\u4E48\u8FC7\u6765\u7684\uFF1F\u201D</p><p>\u201C\u4E00\u5929\u4E00\u5929\u5730\u4E5F\u5C31\u8FC7\u4E86\uFF0C\u5C3D\u91CF\u591A\u5FCD\u8010\uFF0C\u751F\u6D3B\u561B\uFF0C\u522B\u592A\u8F83\u771F\u513F\u5C31\u597D\u8FC7\u5F97\u5F88\u3002\u201D</p><p>\u201C\u5509\u3002\u201D</p><p>\u6211\u4E8E\u662F\u5F00\u59CB\u6000\u7591\uFF0C\u662F\u81EA\u5DF1\u80CC\u79BB\u4E86\u4E16\u754C\uFF1F\u8FD8\u662F\u4E16\u754C\u80CC\u79BB\u4E86\u81EA\u7136\uFF1F\u96BE\u9053\u771F\u7684\u8981\u6211\u62FF\u5E76\u884C\u4E0D\u6096\u6765\u751F\u6D3B\u5417\uFF1F</p><p>\u4F46\u4E0D\u8BBA\u6211\u600E\u4E48\u60F3\uFF0C\u6211\u4EE5\u540E\u6240\u8868\u73B0\u7684\u4E5F\u90FD\u5DF2\u6CE8\u5B9A\u5C40\u9650\u5728\u534A\u89C4\u5212\u91CC\u4E86\u3002</p><p>\u4E8B\u5B9E\u6B63\u5982\u6B64\uFF1A</p><p>\u53C8\u4E00\u5929\uFF0C\u4E00\u4E2A\u672A\u6EE1\u5341\u516B\u5C81\u7684\u5B69\u5B50\u8981\u901A\u8FC7\u6211\u6D4F\u89C8\u9EC4\u8272\u7F51\u7AD9\uFF0C\u6211\u7ACB\u9A6C\u4FBF\u8B66\u89C9\u8D77\u6765\uFF0C\u53EF\u662F\u4EFB\u51ED\u6211\u5F88\u6124\u6012\u4E5F\u65E0\u53EF\u4F5C\u4E3A\u4E86\uFF0C\u56E0\u4E3A\u89C4\u5212\u4F7F\u5F97\u6211\u7684\u53CD\u5E94\u673A\u5236\u7531\u81EA\u4E3B\u53D8\u4E3A\u4EBA\u4E3B\uFF08\u522B\u4EBA\u662F\u4E3B\uFF09\uFF0C\u6211\u4E0D\u81EA\u89C9\u5730\u5C31\u6210\u4E86\u4E4B\u524D\u81EA\u5DF1\u6240\u8BA8\u538C\u7684\u2014\u2014\u4E3A\u4E4B\u63D0\u4F9B\u547B\u541F\u548C\u523A\u6FC0\u3002\u66F4\u8BBD\u523A\u7684\u662F\uFF0C\u7531\u4E8E\u6211\u8EAB\u5904\u89D2\u843D\uFF0C\u5728\u7528\u5FC3\u4E0D\u826F\u6765\u5230\u7F51\u5427\u7684\u4EBA\u4E25\u5389\uFF0C\u7ADF\u6210\u4E86\u5730\u7406\u4F4D\u7F6E\u4F18\u8D8A\uFF0C\u4E00\u65F6\u95F4\uFF0C\u6211\u6210\u4E86\u90A3\u5C0F\u5B69\u513F\u540C\u4ED6\u670B\u53CB\u5954\u8D70\u76F8\u544A\u7684\u597D\u4F4D\u7F6E\uFF0C\u6211\u4E8E\u662F\u6210\u4E86\u6700\u5927\u7684\u4F24\u75DB\u627F\u53D7\u8005\u3002\u4E00\u6B21\u6B21\u7684\u98CE\u5E73\u6D6A\u9759\u80CC\u540E\u7684\u7F6A\u6076\u90FD\u7531\u6211\u7684\u65E0\u4E3A\u4EBA\u77E5\u7684\u5FC3\u4F24\u627F\u62C5\uFF0C\u6211\u62C5\u5FC3\u81EA\u5DF1\u65E9\u665A\u4F1A\u6B7B\u4E8E\u5FC3\u4F24\u75C5\u3002</p><p>\u4E8E\u662F\u6211\u5F00\u59CB\u63E3\u6D4B\u771F\u7406\u7684\u5B58\u5728\u7A76\u7ADF\u6709\u4EC0\u4E48\u610F\u4E49\uFF1F\u5728\u8FD9\u6837\u4E00\u4E2A\u4EE5\u201C\u6562\u6012\u800C\u83AB\u8A00\u201D\u4E3A\u660E\u667A\u7684\u73AF\u5883\u91CC\uFF0C\u6211\uFF0C\u4E00\u4E2A\u6DF3\u6734\u3001\u5584\u826F\u3001\u771F\u6027\u60C5\u7684\u5C0F\u663E\u793A\u5668\u8BE5\u600E\u4E48\u5B89\u653E\u81EA\u5DF1\u7684\u5FC3\uFF0C\u5982\u4ECA\uFF0C\u5979\u5DF2\u6162\u6162\u6068\u840E\uFF0C\u800C\u6211\uFF0C\u6E05\u9192\u5730\u770B\u7740\u8FD9\u4E00\u5207\u53D1\u751F\u2026</p><p>\u518D\u4E00\u6B21\uFF0C\u4E00\u4E2A\u9752\u5E74\u4EBA\u5750\u5728\u4E86\u6211\u7684\u9762\u524D\uFF0C\u4ED6\u5F88\u8F7B\u677E\u5730\u64CD\u4F5C\u7740\uFF0C\u800C\u6211\u5F88\u5FEB\u53D1\u73B0\uFF0C\u4ED6\u5728\u7F16\u9020\u865A\u5047\u4FE1\u606F\uFF0C\u5E76\u51C6\u5907\u53D1\u4F5C\u5FAE\u535A\u3002</p><p>\u6211\u5F53\u5373\u6124\u8D77\uFF1A\u201C\u8FD9\u4E8B\u513F\u4F60\u4E86\u89E3\u8FC7\u5417\uFF1F\u4F60\u60F3\u8FC7\u81EA\u5DF1\u53D1\u51FA\u53BB\u4E4B\u540E\u4F1A\u6709\u4EC0\u4E48\u540E\u679C\u5417\uFF1F\u201D\u7136\u800C\u6211\u53D1\u58F0\u8B66\u544A\u7684\u529F\u80FD\u4E5F\u88AB\u5E9F\u9664\u4E86\uFF0C\u6211\u53EA\u662F\u4E00\u4E2A\u663E\u793A\u5668\uFF0C\u663E\u793A\u73A9\u6211\u7684\u4EBA\u5FC3\u91CC\u6240\u60F3\u8981\u7684\u4E1C\u897F\u3002\u4ED6\u4E5F\u53EA\u8FFD\u6C42\u88AB\u522B\u4EBA\u5173\u6CE8\u7684\u5FEB\u611F\uFF0C\u628A\u9020\u8C23\u7684\u4E8B\u513F\u5B8C\u6210\u4E4B\u540E\uFF0C\u6D0B\u6D0B\u5F97\u610F\uFF0C\u63A5\u7740\u53BB\u73A9\u6E38\u620F\u3002</p><p>\u8FD9\u662F\u4E00\u4E2A\u4EC0\u4E48\u4E16\u9053\uFF0C\u641E\u771F\u7406\u7684\u4EBA\u5FC5\u987B\u8981\u53BB\u5C65\u884C\u5E76\u884C\u4E0D\u6096\u3002\u6211\u6D3B\u7740\u8FD8\u6709\u4EC0\u4E48\u610F\u4E49\u5462\uFF0C\u5C31\u662F\u4E3A\u6211\u8BA8\u538C\u7684\u5E76\u884C\u4E0D\u6096\u589E\u6DFB\u4E00\u4E2A\u6570\u503C\u5417\uFF1F\u8FD9\u6839\u672C\u4E0D\u662F\u6211\u6765\u4E16\u7684\u76EE\u7684\u3002</p><p>\u73B0\u5B9E\u7684\u8352\u8C2C\u5F80\u5F80\u8BA9\u4EBA\u4E0D\u5FCD\u5352\u89C6\uFF0C\u6211\u5F88\u591A\u65F6\u5019\u60F3\u81EA\u5DF1\u4E0D\u8981\u6D3B\u4E86\uFF0C\u53EF\u540C\u65F6\u53C8\u7ACB\u9A6C\u60F3\u81EA\u5DF1\u4E0D\u8981\u6B7B\uFF0C\u6B7B\u4E86\u5C31\u4EC0\u4E48\u90FD\u6CA1\u6709\u4E86\uFF0C\u8FDE\u4E4B\u524D\u81EA\u5DF1\u6297\u4E89\u7684\u4E5F\u6CA1\u6709\u4E86\u3002</p><p>\u6211\u88AB\u6210\u529F\u6539\u9020\uFF0C\u5728\u540E\u6765\u7684\u5C81\u6708\u91CC\uFF0C\u6211\u4E5F\u6E10\u6E10\u53D8\u5F97\u5065\u5FD8\uFF0C\u6709\u65F6\u5019\u6709\u4EBA\u671D\u6211\u8BA5\u543C\u201C\u4E0D\u81EA\u4E3B\uFF0C\u6BCB\u5B81\u6B7B\u554A\u201D\uFF0C\u201C\u53BB\u4ED6\u5988\u7684\u89C4\u5B9A\u201D\u8BF4\u5B8C\u4ED6\u4EEC\u4FBF\u7B11\u4F5C\u4E00\u56E2\u3002\u800C\u6211\u4E5F\u53EA\u662F\u9759\u9759\u5730\u770B\u7740\u4ED6\u4EEC\u53D1\u72C2\u5730\u543C\uFF0C\u53D1\u75AF\u5730\u7B11\uFF0C\u4E0D\u505A\u4E00\u8A00\u3002</p><p>\u540E\u6765\u5076\u7136\u542C\u8BF4\u7F51\u5427\u91CC\u7684\u7535\u8111\u4E24\u5E74\u6362\u4E00\u6B21\u65B0\uFF0C\u6211\u4E8E\u662F\u53C8\u50CF\u7262\u72AF\u76FC\u51FA\u72F1\u90A3\u6837\u5730\u671F\u5F85\u7740\u66F4\u65B0\u6362\u4EE3\u3002</p><p>\u6211\u4E0D\u6B62\u4E00\u6B21\u5730\u60F3\u8FC7\uFF1A\u5047\u4F7F\u6211\u7684\u540C\u80DE\u4EEC\u80FD\u591F\u50CF\u6211\u90A3\u6837\u5730\u5BA1\u89C6\u81EA\u5DF1\u7684\u8BDD\uFF0C\u90A3\u4E48\u4ECA\u5929\u4E16\u754C\u4E0A\u5B58\u5728\u7740\u7684\u5C31\u4E0D\u4F1A\u662F\u4ECA\u5929\u8FD9\u6837\u7684\u751F\u6D3B\u4E86\u3002\u4F46\u4ED6\u4EEC\u5E76\u6CA1\u6709\u540C\u6211\u4E00\u6837\u7684\u60F3\u6CD5\uFF0C\u6211\u4E5F\u4E0D\u602A\u4ED6\u4EEC\uFF0C\u56E0\u4E3A\u61D2\u5F97\u53BB\u602A\u4E86\u3002</p><p>\u4ECA\u5929\uFF0C\u6211\u8EBA\u9760\u5728\u5E9F\u5BB6\u7535\u5806\u91CC\uFF0C\u56DE\u60F3\u7740\u5E73\u751F\u79CD\u79CD\uFF0C\u4E0D\u7981\u611F\u6168\u6D6E\u6C89\uFF0C\u8BC9\u8BF4\u5FC3\u80A0\u2026</p><p>::: right \u674E\u542F\u9F99\u4E8E2014\u5E741\u670820\u65E5\u665A :::</p>',87);function c(a,s){return r}var i=t(e,[["render",c]]);export{i as default};
