import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const t={},c=p('<p>\u6574\u7406\u81EA\u7236\u4EB2\u53D1\u7684\u5FAE\u4FE1\u670B\u53CB\u5708\uFF0C\u5206\u4EAB\u65F6\u95F4\u4E3A\uFF1A<code>2\u670825\u65E5</code></p><blockquote><p>\u5FC6\u7236\u4EB2\u2014\u2014\u82E6\u96BE\u4EBA\u751F(\u4E00)</p><p>\u7236\u4EB2\u751F\u4E8E\u4E00\u4E5D\u4E09\u4E09\u5E74\uFF0C\u75C5\u901D\u4E8E\u4E8C\u96F6\u4E00\u516B\u5E74\u51AC\uFF0C\u4EAB\u5E74\u516B\u5341\u516D\u5C81\u3002</p><p>\u7236\u4EB2\u662F\u5730\u5730\u9053\u9053\u7684\u519C\u6C11\uFF0C\u6211\u987A\u7406\u6210\u7AE0\u5730\u4FBF\u662F\u519C\u6C11\u7684\u513F\u5B50\uFF0C\u8840\u6DB2\u91CC\u81EA\u7136\u800C\u7136\u5730\u6D41\u8D9F\u7740\u519C\u6C11\u90A3\u7279\u6709\u7684\u52E4\u52B3\u6734\u5B9E\u61A8\u539A\u5014\u5F3A\u7684\u6027\u683C\uFF0C\u7236\u4EB2\u5F1F\u5144\u4E09\u4E2A\uFF0C\u6392\u884C\u8001\u4E09\u6211\u4FBF\u53EB\u4ED6\u53D4\u3002\u5728\u4E0A\u4E16\u7EAA\u516D\u4E03\u5341\u5E74\u4EE3\u7684\u519C\u6751\u5F88\u5C11\u6709\u53EB\u7238\u7684\uFF0C\u4EE5\u81F3\u4E8E\u4E5D\u5341\u5E74\u81EA\u5DF1\u7684\u513F\u5B50\u7BA1\u6211\u53EB\u7238\u8FD8\u6709\u70B9\u4E0D\u4E60\u60EF\uFF0C\u9AA8\u5B50\u91CC\u4F9D\u7136\u6839\u6DF1\u8482\u56FA\u5730\u6CBF\u88AD\u7740\u53E4\u4EBA\u201C\u4F2F\u3001\u4EF2\u3001\u53D4\u3001\u5B63\u201D\b\uFF0C\u548C\u89E3\u653E\u540E\u201C\u4F2F\u3001\u7239\u3001\u53D4\u3001\u5927\u201D\u7684\u79F0\u8C13\u3002</p><p>\u7236\u4EB2\u5C0F\u65F6\u5019\u8BFB\u8FC7\u79C1\u719F\uFF0C\u4F1A\u73E0\u7B97\u3002\u80FD\u8BC6\u5B57\uFF0C\u4F1A\u7B97\u5E10\u5DF2\u8D85\u8D8A\u4E86\u4ED6\u4EEC\u90A3\u4E2A\u5E74\u4EE3\u7684\u540C\u9F84\u4EBA\uFF0C\u662F\u5F53\u65F6\u5F88\u591A\u4EBA\u6240\u671B\u5C18\u83AB\u53CA\u7684\uFF0C\u4EE5\u81F3\u4E8E\u540E\u6765\u6BCF\u5F53\u5BB6\u91CC\u5356\u7CAE\u98DF\u7684\u65F6\u5019\uFF0C\u4ED6\u603B\u662F\u62FF\u51FA\u4ED6\u7684\u7B97\u76D8\u70AB\u6280\uFF0C\u867D\u7136\u7B97\u6B63\u786E\u7684\u65F6\u5019\u4E0D\u591A\uFF0C\u4F46\u4ED6\u5374\u4E50\u6B64\u4E0D\u5F7C\uFF0C\u5F15\u4EE5\u4E3A\u8363\u3002</p><p>\u7236\u4EB2\u7684\u4E00\u751F\u662F\u574E\u5777\u7684\u3001\u56F0\u96BE\u7684\u3001\u6349\u895F\u89C1\u8098\u7684\u4E00\u751F\u3002\u89E3\u653E\u540E\u571F\u5730\u6240\u6709\u6743\u5F52\u96C6\u4F53\u6240\u6709\uFF0C\u7236\u4EB2\u5C31\u662F\u90A3\u4E2A\u5E74\u4EE3\u9760\u79CD\u5730\u8C0B\u751F\u7684\u519C\u6C11\uFF0C\u5F53\u65F6\u751F\u4EA7\u961F\u51ED\u5DE5\u5206\u5403\u996D\uFF0C\u7531\u4E8E\u5144\u5F1F\u59D0\u59B9\u591A\uFF0C\u6BCD\u4EB2\u9700\u8981\u7167\u987E\u6211\u4EEC\uFF0C\u6240\u4EE5\u53EA\u80FD\u9760\u4ED6\u4E00\u4EBA\u6323\u5DE5\u5206\uFF0C\u5403\u4E86\u4E0A\u987F\u6CA1\u6709\u4E0B\u987F\u90A3\u662F\u5E38\u6709\u7684\u4E8B\u3002</p><p>\u7236\u4EB2\u90A3\u4E00\u8F88\u6240\u5403\u7684\u82E6\u4E0D\u662F\u6211\u4EEC\u8FD9\u4E00\u4EE3\u6240\u80FD\u4F53\u4F1A\u5230\u7684\u3002\u5F53\u5E74\u8F70\u8F70\u70C8\u70C8\u7684\u5927\u751F\u4EA7\u8FD0\u52A8\u4E2D\u57FA\u672C\u4E0A\u6CA1\u6709\u5316\u80A5\uFF0C\u79CD\u5E84\u7A3C\u5168\u9760\u571F\u7CAA\uFF0C\u7531\u4E8E\u7F3A\u5C11\u8F66\u8F86\uFF0C\u5173\u952E\u65F6\u523B\u8FD8\u5F97\u9760\u4EBA\u5DE5\u5F80\u5730\u91CC\u6311\u7CAA\uFF0C\u751F\u4EA7\u961F\u957F\u5C31\u662F\u571F\u7687\u5E1D\uFF0C\u6709\u7740\u7EDD\u5BF9\u7684\u6743\u5A01\uFF0C\u4ED6\u89C4\u5B9A\u5F80\u5730\u91CC\u6311\u4E00\u62C5\u4E8C\u5398\uFF0C\u5C11\u4E00\u62C5\u6263\u4E8C\u5206\uFF0C\u800C\u4E14\u662F\u6309\u4EBA\u5934\u5747\u644A\u7684\uFF0C\u8C61\u6211\u5BB6\u5F20\u5634\u7684\u591A\u5E72\u6D3B\u7684\u5C11\u5C31\u53EA\u80FD\u54D1\u5DF4\u5403\u9EC4\u8FDE\uFF0C\u767D\u5929\u5E72\u4E00\u5929\u6D3B\u7684\u7236\u4EB2\u5E38\u5E38\u8D81\u7740\u6708\u8272\u5F80\u5730\u91CC\u6311\u7CAA\uFF0C\u5C31\u8FD9\u6837\u4E00\u5929\u4E0B\u6765\u8FD8\u662F\u88AB\u6263\u4E86\u5206\u3002\u5E72\u4E00\u5929\u6D3B\u4E0B\u6765\u4E0D\u4EC5\u6CA1\u6709\u6323\u5230\u5DE5\u5206\uFF0C\u800C\u4E14\u8FD8\u88AB\u6263\u4E86\u5206\uFF0C\u5565\u6982\u5FF5\u4E5F\u8BB8\u4F60\u4E0D\u4F1A\u6709\u6DF1\u523B\u7684\u8BA4\u77E5\uFF0C\u5C31\u6BD4\u5982\u73B0\u5728\u7684\u5B66\u751F\u5206\u6570\u5C31\u662F\u547D\u6839\uFF0C\u5206\u6570\u4F4E\u5F53\u7136\u5C31\u8BFB\u4E0D\u4E86\u91CD\u70B9\u6216\u540D\u724C\u5927\u5B66\u3002\u519C\u6C11\u9760\u5DE5\u5206\u5403\u996D\uFF0C\u5206\u6570\u5C11\u5230\u5E74\u5E95\u81EA\u7136\u5C31\u5206\u4E0D\u5230\u94B1\u548C\u7CAE\u98DF\uFF0C\u62FF\u4EC0\u4E48\u517B\u5BB6\u7CCA\u53E3\u3002</p><p>\u7236\u4EB2\u662F\u8C6A\u65E0\u4E89\u8BAE\u5730\u662F\u4E00\u5BB6\u4E4B\u4E3B\uFF0C\u5E76\u7528\u4ED6\u90A3\u7626\u5F31\u7684\u80A9\u8180\u6311\u8D77\u4E86\u6211\u4EEC\u5168\u5BB6\u7684\u751F\u6D3B\u91CD\u62C5\u3002\u7531\u4E8E\u751F\u6D3B\u62EE\u636E\u3001\u82E6\u65E5\u5B50\u96BE\u71AC\uFF0C\u7236\u4EB2\u7684\u813E\u6C14\u53D8\u5F97\u6709\u70B9\u53E4\u602A\u548C\u66B4\u8E81\uFF0C\u6211\u4EEC\u90FD\u5F88\u6015\u4ED6\uFF0C\u56E0\u4E3A\u6DD8\u6C14\u548C\u4E0D\u542C\u8BDD\uFF0C\u4ED6\u53EF\u6CA1\u5C11\u628A\u6211\u4EEC\u6253\uFF0C\u6709\u65F6\u5019\u4E0D\u660E\u7F18\u7531\u5730\u4E5F\u4F1A\u6328\u4E00\u987F\u63CD\uFF0C\u6253\u4E0E\u4E0D\u6253\u5168\u51ED\u4ED6\u5FC3\u60C5\u7684\u597D\u574F\uFF0C\u6BCD\u4EB2\u4E5F\u662F\u8C6A\u65E0\u529E\u6CD5\u5730\u5E38\u5E38\u966A\u6211\u4E00\u8D77\u62B9\u773C\u6CEA\u3002 \u7236\u4EB2\u7684\u4E00\u751F\u662F\u52E4\u52E4\u80AF\u80AF\uFF0C\u4E0D\u8F9E\u52B3\u82E6\uFF0C\u4EFB\u52B3\u4EFB\u6028\u7684\u4E00\u751F\u3002\u5728\u5BB6\u4E61\u90A3\u7247\u8D2B\u7620\u7684\u9EC4\u571F\u5730\u4E0A\uFF0C\u4E00\u5E74\u53C8\u4E00\u5E74\u5730\u6536\u83B7\u7740\u5FAE\u8584\u7684\u5E0C\u671B\uFF0C\u5403\u7684\u662F\u4E94\u8C37\u7CAE\uFF0C\u559D\u7684\u662F\u90A3\u67AF\u4E95\u6C34\uFF0C\u7A7F\u7684\u662F\u7C97\u5E03\u8863\uFF0C\u4F4F\u7684\u662F\u6CE5\u5DF4\u623F\uFF0C\u5178\u578B\u7684\u571F\u91CC\u5228\u751F\u6D3B\uFF0C\u6536\u83B7\u7740\u82E6\u4E2D\u4E50\u3002</p><p>\u7236\u4EB2\u7684\u9AA8\u5B50\u91CC\u6709\u79CD\u575A\u97E7\u4E0D\u62E8\u7684\u8010\u529B\uFF0C\u4ED6\u662F\u90A3\u79CD\u6C38\u8FDC\u4E0D\u77E5\u75B2\u5026\u5E76\u4E14\u95F2\u4E0D\u4E0B\u6765\u7684\u4EBA\u3002\u6211\u4EEC\u519C\u6751\u6709\u53E5\u4FD7\u8BED\u53EB\u201D\u5C0F\u8F66\u4E0D\u5012\u53EA\u7BA1\u63A8\u201D\uFF0C\u4E5F\u5C31\u662F\u201D\u751F\u547D\u4E0D\u6B62\uFF0C\u8FD0\u52A8\u4E0D\u606F\u201D\u3002\u5C31\u5728\u4ED6\u751F\u547D\u7684\u5F25\u7559\u4E4B\u9645\uFF0C\u4F9D\u7136\u4E0D\u5FD8\u5631\u5490\u6211\u4EEC\u660E\u5E74\u5E84\u7A3C\u600E\u4E48\u79CD\u600E\u4E48\u6536\u3002</p><p>\u7236\u4EB2\u90A3\u4E00\u8F88\u4EBA\u5BF9\u571F\u5730\u662F\u6709\u611F\u60C5\u7684\u3002\u4ECE\u89E3\u653E\u540E\u7684\u5927\u96C6\u4F53\u751F\u6D3B\uFF0C\u5230\u540E\u6765\u7684\u8054\u4EA7\u627F\u5305\u8D23\u4EFB\u5236(\u571F\u5730\u5305\u4EA7\u5230\u6237)\uFF0C\u4ED6\u4EEC\u5BF9\u571F\u5730\u7684\u60C5\u7ED3\u662F\u65E0\u6CD5\u60F3\u8C61\u7684\uFF0C\u4ED6\u4EEC\u65E0\u6028\u65E0\u6094\u5730\u628A\u6BD5\u751F\u7684\u7CBE\u529B\u90FD\u732E\u7ED9\u4E86\u751F\u4ED6\u517B\u4ED6\u7684\u90A3\u7247\u9EC4\u571F\u5730\u4E86\u3002\u81EA\u4ECE\u56FD\u5BB6\u514D\u9664\u519C\u4E1A\u7A0E\u4E4B\u540E\uFF0C\u4ED6\u9022\u4EBA\u4FBF\u5938\u9047\u4E0A\u597D\u7687\u5E1D\u4E86\uFF0C\u72EC\u6253\u72EC\u5403\uFF0C\u518D\u4E5F\u4E0D\u7528\u4EA4\u7687\u7CAE\u4E86\uFF0C\u4ED6\u5E38\u5631\u5490\u6211\u4EEC\u5171\u4EA7\u515A\u597D\uFF0C\u6BDB\u4E3B\u5E2D\u597D\uFF0C\u9886\u5BFC\u4EBA\u6C11\u7FFB\u8EAB\u5F97\u89E3\u653E\uFF0C\u8981\u542C\u515A\u8BDD\u3001\u8DDF\u515A\u8D70\uFF0C\u73CD\u60DC\u773C\u524D\u8FD9\u6765\u4E4B\u4E0D\u6613\u7684\u5E78\u798F\u751F\u6D3B\u3002</p><p>\u814A\u6708\u5BD2\u51AC\uFF0C\u4E34\u8FD1\u6625\u8282\uFF0C\u5F97\u77E5\u7236\u4EB2\u75C5\u91CD\u7684\u6D88\u606F\uFF0C\u6211\u4FBF\u8D76\u7D27\u653E\u4E0B\u624B\u4E2D\u7684\u6D3B\u6025\u5306\u5306\u5730\u5F80\u5BB6\u8D76\u3002\u770B\u5230\u7684\u7236\u4EB2\u662F\u7CBE\u795E\u6643\u5FFD\u3001\u4E8C\u76EE\u65E0\u795E\u3001\u817F\u80BF\u811A\u80BF\u3001\u884C\u52A8\u8FDF\u7F13\uFF0C\u996E\u98DF\u9AA4\u51CF\u3001\u9AA8\u7626\u5982\u67F4\uFF0C\u6211\u5C31\u77E5\u9053\u7236\u4EB2\u5927\u9650\u4E4B\u65E5\u4E0D\u8FD0\u4E86\uFF0C\u6211\u518D\u4E5F\u65E0\u6CD5\u63A7\u5236\u6127\u759A\u4E0E\u5FC3\u75DB\u7684\u6CEA\u6C34\uFF0C\u513F\u4E0D\u5B5D\u554A\uFF01</p><p>\u7236\u4EB2\u6839\u672C\u4E0D\u77E5\u9053\u4ED6\u75C5\u60C5\u7684\u4E25\u91CD\u7A0B\u5EA6\u3002\u5F53\u6211\u8FD8\u6CA1\u6709\u8D76\u5230\u5BB6\u65F6\uFF0C\u4ED6\u5BF9\u6211\u5AB3\u5987\u8BF4\uFF1A\u201C\u7B49\u6211\u5A03\u56DE\u6765\u4E86\uFF0C\u62C9\u6211\u53BB\u5927\u57CE\u5E02\u770B\u75C5\uFF0C\u867D\u8BF4\u54B1\u4E61\u91CC\u4EBA\u547D\u6E85\uFF0C\u4F46\u9547\u91CC\u7684\u6C34\u5E73\u4E0D\u6CBE\u201D\u3002\u7136\u800C\u57CE\u91CC\u7684\u533B\u751F\u5374\u544A\u8BC9\u6211\u4EEC\u8001\u7237\u5B50\u5DF2\u751F\u547D\u5782\u5371\uFF0C\u6765\u65E5\u65E0\u591A\uFF0C\u4F60\u4EEC\u80FD\u5C3D\u5B5D\u5C31\u591A\u5C3D\u70B9\u5B5D\u5427\u3002\u62FF\u7740\u533B\u751F\u5F00\u7684\u75C5\u5371\u901A\u77E5\u4E66\uFF0C\u6211\u4E0D\u77E5\u9053\u7B7E\u4E86\u591A\u5C11\u4E2A\u540D\u5B57\uFF0C\u6211\u53EA\u77E5\u9053\u6211\u53EF\u80FD\u5931\u53BB\u7236\u7231\uFF0C\u6211\u4EEC\u4E5F\u53EA\u80FD\u542C\u4ECE\u533B\u751F\u7684\u5EFA\u8BAE\u4E00\u4E1D\u4E0D\u62D8\u5730\u505A\u7740\u6211\u4EEC\u5E94\u8BE5\u505A\u7684\u4E00\u5207\uFF0C\u671F\u76FC\u7740\u5947\u8FF9\u80FD\u591F\u53D1\u751F\u3002</p><p>\u814A\u6708\u7684\u53BF\u57CE\uFF0C\u6CA1\u6709\u4E00\u4E1D\u7EFF\u610F\uFF0C\u5929\u603B\u662F\u7070\u8499\u8499\u9634\u6C89\u6C89\u7684\uFF0C\u6839\u672C\u89C1\u4E0D\u5230\u4E00\u7F15\u9633\u5149\uFF0C\u5076\u5C14\u522E\u8FC7\u4E00\u9635\u98CE\u4E5F\u662F\u51B7\u55D6\u55D6\u7684\uFF0C\u8BA9\u4EBA\u540E\u80CC\u53D1\u51C9\u3002\u534A\u591C\u7684\u533B\u9662\u6B7B\u4E00\u6837\u7684\u6C89\u5BC2\uFF0C\u53EA\u6709\u6211\u4E00\u4E2A\u4EBA\u6162\u6B65\u5728\u4F4F\u9662\u90E8\u72ED\u957F\u7684\u8D70\u5ECA\u91CC\uFF0C\u5750\u5728\u5927\u5385\u51B0\u51B7\u7684\u957F\u6905\u4E0A\u9677\u5165\u4E86\u6DF1\u601D\uFF0C\u5E73\u5E38\u4E0D\u548B\u62BD\u70DF\u7684\u6211\u70B9\u71C3\u4E00\u652F\u9999\u70DF\uFF0C\u5728\u70DF\u96FE\u98D8\u7F08\u4E4B\u4E2D\u56DE\u60F3\u7236\u4EB2\u751F\u5E73\u7684\u70B9\u70B9\u6EF4\u6EF4\u3002</p><p>\u770B\u7740\u75C5\u5E8A\u4E0A\u773C\u7A9D\u6DF1\u9677\uFF0C\u65E5\u6E10\u6D88\u7626\uFF0C\u547C\u5438\u8D8A\u6765\u8D8A\u56F0\u96BE\u7684\u7236\u4EB2\uFF0C\u6211\u5FC3\u5982\u5200\u5272\uFF0C\u5E38\u4EE5\u6CEA\u6D17\u9762\u3002\u5728\u7236\u4EB2\u5076\u5C14\u9192\u6765\u4E4B\u65F6\u6211\u8F7B\u58F0\u5BF9\u4ED6\u5BBD\u6170\u9053\uFF1A\u201C\u4F60\u8981\u575A\u5F3A\u8D77\u6765\uFF0C\u7B49\u6371\u8FC7\u8FD9\u4E2A\u51AC\u5929\uFF0C\u6765\u5E74\u6625\u6696\u82B1\u5F00\u9633\u6C14\u4E0A\u5347\u4E4B\u65F6\uFF0C\u4F60\u7684\u75C5\u5C31\u597D\u4E86\u201D\u3002\u7236\u4EB2\u65E0\u529B\u5730\u70B9\u70B9\u5934\uFF0C\u773C\u6674\u91CC\u4F3C\u4E4E\u4E5F\u6D41\u9732\u51FA\u4E86\u4E9B\u8BB8\u7684\u5411\u5F80\uFF0C\u660E\u77E5\u9053\u53BB\u65E5\u65E0\u591A\u7684\u7236\u4EB2\u6211\u5374\u7528\u5584\u610F\u7684\u8C0E\u8A00\u9F13\u52B1\u7740\u4ED6\uFF0C\u540C\u65F6\u4E5F\u662F\u5728\u5B89\u6170\u7740\u6211\u81EA\u5DF1\u3002</p><p>\u968F\u7740\u7236\u4EB2\u9F84\u7684\u8870\u8001\uFF0C\u8EAB\u4F53\u9010\u5E74\u7684\u5728\u8D70\u4E0B\u5761\u8DEF\uFF0C\u8FD1\u51E0\u5E74\u6BCF\u5E74\u51AC\u5929\u90FD\u8981\u751F\u4E00\u6B21\u75C5\uFF0C\u800C\u4E14\u6CBB\u6108\u7684\u65F6\u95F4\u4E5F\u4F1A\u8D8A\u6765\u8D8A\u957F\u3002\u4ECA\u5E74\u79CB\u6536\u51AC\u79CD\u4E4B\u540E\uFF0C\u7236\u4EB2\u53C8\u751F\u75C5\u5012,\u7ECF\u8FC7\u4E00\u4E2A\u591A\u6708\u7684\u6CBB\u7597\u4ED6\u53C8\u8C61\u5F80\u5E74\u4E00\u6837\u5EB7\u590D\u4E86\uFF0C\u800C\u4E14\u751F\u6D3B\u8D77\u5C45\u4F9D\u7136\u6709\u6761\u4E0D\u7D0A\u3002</p></blockquote><p><img src="http://t.eryajf.net/imgs/2021/09/05b08fdd16c41456.jpg" alt="image-20210717180133839"></p>',3);function o(r,d){return c}var _=e(t,[["render",o]]);export{_ as default};
