import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const t={},c=p('<p>\u6574\u7406\u81EA\u7236\u4EB2\u53D1\u7684\u5FAE\u4FE1\u670B\u53CB\u5708\uFF0C\u5206\u4EAB\u65F6\u95F4\u4E3A\uFF1A<code>12\u67084\u65E5</code></p><blockquote><p>\u79C1\u4EBA\u8BA2\u5236</p><p>\u4EBA\u65E0\u8FDC\u8651\uFF0C\u5FC5\u6709\u8FD1\u5FE7</p><p>\u8FDC\u8651\u5728\u6B64\u6211\u4EEC\u53EF\u4EE5\u7406\u89E3\u4E3A\u4EBA\u4EEC\u90A3\u4E30\u6EE1\u7684\u7406\u60F3\uFF0C\u8FD1\u5FE7\u53EF\u4EE5\u6253\u5305\u4E3A\u9AA8\u611F\u7684\u73B0\u5B9E\u3002</p><p>\u62FF\u7834\u4ED1\u66FE\u7ECF\u8BF4\u8FC7\uFF0C\u201C\u4E0D\u60F3\u5F53\u5C06\u519B\u7684\u58EB\u5175\u4E0D\u662F\u4E00\u4E2A\u662F\u597D\u58EB\u5175\u201D\u3002</p><p>\u51AF\u5DE9\u4E5F\u66FE\u7ECF\u8BF4\u8FC7\uFF0C\u201D\u4E0D\u60F3\u5F53\u53A8\u5B50\u7684\u88C1\u7F1D\u4E0D\u662F\u4E00\u4E2A\u597D\u53F8\u673A\u201D\u3002</p><p>\u4EBA\u6D3B\u4E00\u4E16\uFF0C\u4E0D\u7BA1\u80FD\u4E0D\u80FD\u5B9E\u73B0\uFF0C\u6216\u5927\u6216\u5C0F\u90FD\u4F1A\u6709\u4E00\u4E2A\u68A6\u60F3\uFF0C\u5C3D\u7BA1\u5B9E\u73B0\u5B83\u7684\u8FC7\u7A0B\u975E\u5E38\u8270\u96BE\uFF0C\u751A\u81F3\u6839\u672C\u5B9E\u73B0\u4E0D\u4E86\uFF0C\u4F46\u6211\u4EEC\u5FC5\u987B\u4E3A\u6B64\u800C\u52AA\u529B\u8FC7\u3002</p><p>\u771F\u5B9E\u7684\u73B0\u5B9E\u751F\u6D3B\u4E2D\uFF0C\u5E76\u4E0D\u662F\u6BCF\u4E00\u4F4D\u60F3\u6210\u4E3A\u5C06\u519B\u7684\u58EB\u5175\u90FD\u80FD\u6210\u4E3A\u5C06\u519B\uFF0C\u4E5F\u4E0D\u662F\u6BCF\u4E00\u4F4D\u88C1\u7F1D\u65E2\u80FD\u5F00\u8F66\u53C8\u4F1A\u505A\u996D\u3002</p><p>\u5F53\u6211\u4EEC\u7684\u4EBA\u751F\u8C61\u8001\u9A74\u62C9\u78E8\u6C38\u8FDC\u4E5F\u8D70\u4E0D\u51FA\u90A3\u4E2A\u5708\u9047\u5230\u74F6\u9888\u7684\u65F6\u5019</p><p>\u5F53\u6211\u4EEC\u8FC7\u4E8E\u4E30\u6EE1\u7684\u7406\u60F3\u5728\u6B8B\u9177\u7684\u73B0\u5B9E\u4E2D\u65E0\u6CD5\u5F97\u4EE5\u5B9E\u73B0\u7684\u65F6\u5019</p><p>\u5F53\u6211\u4EEC\u5728\u8FFD\u9010\u7406\u60F3\u7684\u8DEF\u4E0A\u4E00\u6B21\u53C8\u4E00\u6B21\u88AB\u649E\u5F97\u5934\u7834\u8840\u6D41\u7684\u65F6\u5019</p><p>\u6B64\u65F6\u6B64\u523B\u6211\u4EEC\u5E94\u8BE5\u6C42\u52A9\u4E8E\u79C1\u4EBA\u8BA2\u5236\u3002</p><p>\u5F88\u591A\u65F6\u5019\uFF0C\u6211\u4EEC\u4F1A\u60F3\u4E0D\u5F00\u4E5F\u653E\u4E0D\u4E0B\uFF0C\u4E30\u6EE1\u7684\u7406\u60F3\u4E3A\u4EC0\u4E48\u603B\u662F\u96BE\u4EE5\u5B9E\u73B0\uFF0C\u5728\u7ECF\u8FC7\u4E00\u756A\u56F0\u82E6\u4E0E\u78E8\u96BE\u4E4B\u540E\uFF0C\u5F88\u591A\u4EBA\u4F1A\u7948\u6C42\u4E0A\u5E1D\u4EA6\u6216\u662F\u4F5B\u7956\u7684\u4FDD\u4F51\uFF0C\u5176\u65F6\u4ED6\u4E0D\u662F\u6545\u610F\u7684\uFF0C\u4E5F\u4E0D\u662F\u65E0\u610F\u7684\u4E0D\u5E2E\u52A9\u6211\u4EEC\uFF0C\u56E0\u4E3A\u4E0A\u5E1D\u6839\u672C\u4E0D\u77E5\u9053\u6211\u4EEC\u7684\u5B58\u5728\u3002</p><p>\u8FD9\u4E2A\u65F6\u5019\u4E0D\u5982\u6765\u4E00\u573A\u79C1\u4EBA\u8BA2\u5236\u4F1A\u8BA9\u4F60\u626C\u7709\u5410\u6C14\uFF0C\u7B11\u50B2\u6C5F\u6E56\u7684\u3002</p><p>\u5018\u82E5\u6709\u4EBA\u95EE\u6211\uFF0C\u5982\u679C\u7ED9\u4F60\u4E00\u6B21\u81EA\u4E3B\u9009\u62E9\u673A\u4F1A\u7684\u8BDD\uFF0C\u4F60\u9996\u5148\u60F3\u5230\u7684\u4F1A\u662F\u4EC0\u4E48\uFF0C\u6211\u4F1A\u6BEB\u4E0D\u72B9\u8C6B\u5730\u9009\u62E9\u8BA9\u6211\u62CD\u6B7B\u7684\u90A3\u53EA\u868A\u5B50\u4E00\u6B21\u5403\u4E2A\u591F\uFF0C\u56E0\u4E3A\u6211\u60F3\u201C\u6709\u94B1\u201C\uFF0C\u66F4\u60F3\u4EFB\u6027\u3002</p><p>\u867D\u7136\u8FD9\u4E2A\u60F3\u6CD5\u6709\u70B9\u5EB8\u4FD7\uFF0C\u5145\u6EE1\u94DC\u81ED\u5473\uFF0C\u4F46\u6211\u60F3\u8FD9\u6050\u6015\u662F\u6BCF\u4E00\u4F4D\u4FD7\u4EBA\u5185\u5FC3\u60F3\u6CD5\u7684\u771F\u60C5\u6D41\u9732\u3002</p><p>\u5982\u679C\u975E\u8981\u6211\u9009\u62E9\u4E00\u6B3E\u79C1\u4EBA\u8BA2\u5236\u7684\u8BDD\uFF0C\u6211\u4F1A\u4E0D\u52A0\u601D\u7D22\u5730\u8BF4\u201C\u6709\u94B1\u201D\u5427\uFF01</p><p>\u6709\u4E00\u79CD\u201C\u6709\u94B1\u201D\u662F\u505A\u201C\u6307\u5C71\u5356\u78E8\u201D\u7684\u767D\u65E5\u68A6\uFF0C\u8FD9\u79CD\u68A6\u6211\u505A\u8FC7\uFF0C\u603B\u611F\u89C9\u6CA1\u7740\u6CA1\u843D\u7684\u8BA9\u4EBA\u4E0D\u8E0F\u5B9E\u3002</p><p>\u4E0E\u5176\u90A3\u6837\u8FD8\u4E0D\u5982\u868A\u5B50\u9001\u6211\u7684\u201C\u7EA2\u5305\u201D\u6765\u7684\u5B9E\u518D\uFF0C\u81EA\u4ECE\u62CD\u6B7B\u868A\u5B50\u4E4B\u540E\uFF0C\u6211\u5E38\u5904\u5728\u6DF1\u6DF1\u7684\u81EA\u8D23\u4E4B\u4E2D\uFF0C\u56E0\u4E3A\u868A\u5B50\u6700\u540E\u7684\u771F\u60C5\u544A\u767D\u4F7F\u6211\u8305\u585E\u987F\u5F00\uFF0C\u540E\u6094\u4E0D\u5DF2\u3002</p><p>\u868A\u5B50\u5F88\u771F\u57CE\u5E76\u4E14\u4E0D\u65E0\u60CB\u60DC\u5730\u544A\u8BEB\u6211\u8BF4\uFF1A\u201C\u5728\u6211\u770B\u6765\uFF0C\u6539\u53D8\u4EBA\u547D\u8FD0\u7684\u65B9\u5F0F\u4E0D\u5916\u547C\u6709\u4E09\u79CD\u201D\u3002</p><p>\u7B2C\u4E00\u79CD\u5C31\u662F\u9760\u77E5\u8BC6\u6539\u53D8\u547D\u8FD0\uFF0C\u770B\u770B\u4F60\u62CD\u6B7B\u6211\u90A3\u53EA\u5E03\u6EE1\u8001\u8327\u7684\u624B\u638C\u4EE5\u53CA\u82E6\u5927\u4EC7\u6DF1\u7684\u5BB9\u989C\uFF0C\u663E\u7136\u4F60\u662F\u5931\u8D25\u7684\u3002</p><p>\u7B2C\u4E8C\u79CD\u5C31\u662F\u4E70\u53CC\u8272\u7403\u4E5F\u53EF\u4EE5\u6539\u53D8\u547D\u8FD0\uFF0C\u518D\u770B\u770B\u4F60\u56E0\u7F3A\u94B1\u800C\u5F71\u54CD\u4E86\u7684\u667A\u5546\uFF0C\u5230\u5E95\u662F\u732A\u649E\u6811\u4E0A\u4E86\u8FD8\u662F\u6811\u649E\u732A\u4E0A\u4E86\u4F60\u90FD\u50BB\u50BB\u5206\u4E0D\u6E05\u695A\uFF0C\u5E78\u8FD0\u7684\u4E94\u767E\u4E07\u3001\u4E00\u5343\u4E07\u2026\u2026\u600E\u4E48\u53EF\u80FD\u4F1A\u7838\u5230\u4F60\u5934\u4E0A\u3002</p><p>\u7B2C\u4E09\u79CD\u4E5F\u662F\u6700\u5FEB\u6377\u7684\u4E00\u79CD\u65B9\u5F0F\u90A3\u5C31\u662F\u9760\u6211\u76F4\u63A5\u9001\u4F60\u7684\u5927\u5927\u7684\u201C\u7EA2\u5305\u201D\uFF0C\u7136\u800C\u5374\u88AB\u4F60\u65E0\u60C5\u7684\u62D2\u7EDD\u4E86\u3002\u5509\uFF01\u6211\u770B\u4F60\u8FD9\u8F88\u5B50\u4E5F\u5C31\u4E24\u6837\u4E0D\u884C\uFF0C\u8FD9\u4E5F\u4E0D\u884C\uFF0C\u90A3\u4E5F\u4E0D\u884C\uFF0C\u4F60\u62D2\u7EDD\u4E86\u53EA\u6709\u6211\u624D\u80FD\u7ED9\u4F60\u4EFB\u6027\u7684\u673A\u4F1A\uFF0C\u90A3\u4F60\u5C31\u53EA\u6709\u8BA4\u547D\u4E86\u3002</p><p>\u662F\u5440\uFF0C\u597D\u8C61\u662F\u8FD9\u4E48\u4E2A\u7406\uFF0C\u868A\u5B50\u7684\u5634\u662F\u4E0D\u662F\u5F00\u8FC7\u5149\uFF0C\u8BF4\u7684\u7B80\u76F4\u592A\u51C6\u4E86\uFF0C\u771F\u662F\u542C\u868A\u4E00\u5E2D\u8BDD\uFF0C\u767D\u8BFB\u5341\u5E74\u4E66\u3002\u6211\u540E\u6094\u5440\uFF01\u6211\u662F\u771F\u540E\u6094\uFF01\u6211\u540E\u6094\u6211\u62CD\u6B7B\u4E86\u4E00\u53EA\u5DEE\u70B9\u6539\u53D8\u6211\u547D\u8FD0\u5E76\u4E14\u53EF\u4EE5\u4F7F\u6211\u4EFB\u6027\u7684\u4E00\u53EA\u868A\u5B50\uFF01</p><p>\u4E8E\u662F\u6211\u5C31\u8C61\u9C81\u8FC5\u7B14\u4E0B\u201C\u513F\u5B50\u963F\u6BDB\u88AB\u72FC\u5201\u8D70\u7684\u7965\u6797\u5AC2\u201D\u4E00\u6837\uFF0C\u4E0D\u505C\u5730\u5FF5\u53E8\u201C\u6211\u771F\u50BB\uFF0C\u771F\u7684\uFF0C\u6211\u5355\u77E5\u9053\u868A\u5B50\u54AC\u4EBA\u7684\u65F6\u5019\u6709\u70B9\u75DB\u6709\u70B9\u75D2\uFF0C\u6211\u5374\u4E0D\u77E5\u9053\u5B83\u662F\u6700\u540E\u4E00\u79CD\u6539\u53D8\u6211\u547D\u8FD0\u7684\u6551\u547D\u7A3B\u8349\u3002\u6211\u771F\u50BB\uFF0C\u771F\u7684\u2026\u2026</p><p>\u6211\u4E0D\u77E5\u9053\u6211\u7A76\u7ADF\u662F\u88AB\u868A\u5B50\u5FFD\u60A0\u4E86\uFF0C\u8FD8\u662F\u771F\u7684\u8C61\u5B83\u8BF4\u7684\u90A3\u6837\uFF0C\u7F3A\u94B1\u5F71\u54CD\u4E86\u6211\u7684\u667A\u5546\uFF0C\u6211\u771F\u50BB\uFF0C\u771F\u7684\uFF0C\u6211\u4E0D\u8BE5\u62CD\u6B7B\u4E86\u5DEE\u70B9\u6539\u53D8\u6211\u4EBA\u751F\u5386\u53F2\u7684\u868A\u5B50\u3002</p><p>\u6211\u771F\u50BB\uFF0C\u771F\u7684\u3002</p><p>\u868A\u5B50\u5C31\u8C61\u6211\u7684\u5FC3\u7075\u89C4\u5316\u5E08\u4E00\u6837\uFF0C\u7528\u5B83\u90A3\u72EC\u7279\u7684\u9EBB\u9189\u65B9\u5F0F\uFF0C\u542E\u5438\u7740\u6211\u7684\u8089\u4F53\uFF0C\u5FFD\u60A0\u7740\u6211\u7684\u7075\u9B42\uFF0C\u6EE1\u8DB3\u4E86\u6211\u7684\u75F4\u5FC3\u5984\u60F3\uFF0C\u8BA9\u6211\u7684\u4EBA\u751F\u4ECE\u6B64\u4E0D\u7559\u9057\u61BE\u3002</p><p>\u770B\u5BA2\u670B\u53CB\u4EEC\uFF0C\u5403\u74DC\u7FA4\u4F17\u4EEC\uFF0C\u7EA6\u8D77\u6765\u5427\uFF01\u56E0\u4E3A\u603B\u6709\u4E00\u6B3E\u79C1\u4EBA\u8BA2\u5236\u4F1A\u8BA9\u4F60\u7684\u767D\u65E5\u68A6\uFF0C\u5728\u65E0\u6CD5\u5B9E\u73B0\u7684\u73B0\u5B9E\u4E2D\u5F97\u5230\u6EE1\u8DB3\uFF0C\u8BA9\u4F60\u7684\u7406\u60F3\u5728\u865A\u65E0\u6F02\u6E3A\u7684\u592A\u7A7A\u4E2D\u8D77\u98DE\u8FDC\u822A\u3002</p><p>\u7A77\u4EBA\u81EA\u4E50\uFF0C\u5C4C\u4E1D\u81EA\u5632</p><p>\u591C\u5DF2\u6DF1\uFF0C\u4EBA\u4EA6\u56F0\uFF0C\u6401\u7B14\u7761\u89C9</p></blockquote><p><img src="http://t.eryajf.net/imgs/2021/09/15d6771f1b44c503.jpg" alt="image-20210717181258049"></p>',3);function o(r,a){return c}var _=e(t,[["render",o]]);export{_ as default};
