import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const t={},c=p('<p>\u6574\u7406\u81EA\u7236\u4EB2\u53D1\u7684\u5FAE\u4FE1\u670B\u53CB\u5708\uFF0C\u5206\u4EAB\u65F6\u95F4\u4E3A\uFF1A<code>5\u670827\u65E5</code></p><blockquote><p>\u6211\u4E3A\u8682\u8681\u70B9\u4E2A\u8D5E</p><p>\u5E73\u5E38\u6CA1\u4E8B\u7684\u65F6\u5019\u6211\u559C\u6B22\u770B\u8682\u8681\u4E0A\u6811\u3002</p><p>\u67D0\u65E5\u6674\uFF0C\u6C14\u6E29\u9002\u5B9C\uFF0C\u4E00\u5927\u65E9\u4E00\u7FA4\u9965\u80A0\u8F98\u8F98\u7684\u8682\u8681\u4FBF\u503E\u5DE2\u800C\u51FA\uFF0C\u4E1C\u5954\u897F\u8D70\uFF0C\u5357\u6765\u5317\u5F80\uFF0C\u5230\u5904\u5BFB\u627E\u7740\u751F\u547D\u4E2D\u7684\u5076\u9047\uFF0C\u7948\u7977\u7740\u4E0B\u4E00\u79D2\u88F9\u8179\u5145\u9965\u7684\u730E\u7269\u5C31\u80FD\u591F\u51FA\u73B0\u3002</p><p>\u4E00\u53EA\u53D7\u4F24\u7684\u82CD\u8747\u8270\u96BE\u5730\u5728\u5730\u4E0A\u722C\u7740\uFF0C\u4F3C\u4E4E\u5DF2\u4E0D\u80FD\u8D77\u98DE\uFF0C\u5F88\u5FEB\u5B83\u4FBF\u95EF\u5165\u4E86\u8682\u8681\u7684\u89C6\u91CE\u3002</p><p>\u5BFB\u89C5\u591A\u65F6\u7684\u8682\u8681\u5C31\u8C61\u54E5\u4F26\u5E03\u53D1\u73B0\u4E86\u65B0\u5927\u9646\u4E00\u6837\uFF0C\u89C1\u5230\u5982\u6B64\u7F8E\u5473\u81EA\u7136\u4E0D\u80AF\u8F7B\u6613\u653E\u8FC7\uFF0C\u5B83\u4EEC\u5C31\u8C61\u6253\u4E86\u9E21\u8840\u4E00\u6837\uFF0C\u4EB3\u4E0D\u5BA2\u6C14\u5730\u4E0A\u53BB\u5C31\u54AC\uFF0C\u82CD\u8747\u89C1\u906D\u5230\u88AD\u51FB\uFF0C\u54EA\u91CC\u80AF\u8F7B\u6613\u5C31\u8303\uFF0C\u4E0D\u65AD\u5730\u62CD\u6253\u7740\u7FC5\u8180\uFF0C\u594B\u529B\u75BE\u8D70\uFF0C\u62FC\u547D\u6323\u624E\u3002</p><p>\u4E00\u53EA\u8682\u8681\u89C1\u4E00\u65F6\u96BE\u4EE5\u5236\u670D\uFF0C\u7A81\u7136\u95F4\u5F03\u4E4B\u4E0D\u987E\uFF0C\u6025\u5306\u5306\u75BE\u9A70\u800C\u53BB\uFF0C\u4E0D\u65AD\u5730\u5954\u8D70\u76F8\u544A\uFF0C\u7528\u89E6\u89D2\u4EA4\u6362\u4FE1\u606F\uFF0C\u4E00\u4F20\u5341\u3001\u5341\u4F20\u767E\uFF0C\u88AB\u544A\u77E5\u7684\u8682\u8681\u6309\u7167\u524D\u54E8\u5175\u53D1\u7684\u5B9A\u4F4D\uFF0C\u5C31\u8C61\u5317\u6597\u5BFC\u822A\u4E00\u6837\u7CBE\u51C6\uFF0C\u57FA\u672C\u4E0A\u90FD\u662F\u5C31\u8FD1\u4E0D\u7ED5\u8FDC\uFF0C\u5174\u5306\u5306\u7684\u4ECE\u56DB\u9762\u516B\u65B9\u8D76\u6765\u53C2\u52A0\u6218\u6597\u3002</p><p>\u6709\u7684\u62B1\u817F\uFF0C\u6709\u7684\u6402\u8170\uFF0C\u4E0D\u5927\u529F\u592B\u5B83\u4EEC\u4FBF\u5B89\u629A\u4F4F\u4E86\u521A\u624D\u60C5\u7EEA\u8FD8\u6709\u70B9\u6025\u8E81\u7684\u82CD\u8747\u3002\u82CD\u8747\u4F3C\u4E4E\u4E5F\u611F\u89C9\u5230\u4E86\u60C5\u5F62\u6709\u70B9\u4E0D\u5BF9\u52B2\uFF0C\u5728\u6B64\u751F\u6B7B\u5B58\u4EA1\u4E4B\u9645\u6B7B\u6B7B\u5730\u6293\u4F4F\u5730\u677F\uFF0C\u5C31\u8C61\u751F\u6C14\u7684\u4E8C\u695E\u5B50\u4E89\u7ADF\u5E2D\u4F4D\uFF0C\u62B1\u7740\u6811\u4E0D\u80AF\u6492\u624B\uFF0C\u4E00\u65F6\u8FD8\u662F\u771F\u96BE\u4EE5\u8BF7\u5B83\u5165\u5E2D\uFF0C\u5C40\u9762\u9650\u5165\u4E86\u50F5\u6301\u9636\u6BB5\u3002</p><p>\u5F88\u5FEB\u4E0D\u8FDC\u5904\u53C8\u6765\u4E86\u4E24\u4E2A\u4F53\u578B\u7A0D\u5927\u7684\u8682\u8681\uFF0C\u4E00\u770B\u5C31\u662F\u7EC3\u5BB6\uFF0C\u8EAB\u6750\u9B41\u68A7\u3001\u9F7F\u989A\u80A5\u5927\u6709\u516D\u5757\u8179\u808C\u7684\u90A3\u79CD\u8682\u8681\u3002\u5B83\u4EEC\u6765\u7684\u76EE\u7684\u8F6C\u773C\u95F4\u6211\u5C31\u5F04\u6E05\u695A\u4E86\uFF0C\u5C31\u8C61\u7535\u89C6\u4E0A\u5C0F\u59D1\u5A18\u5E7F\u544A\u70B9\u8BFB\u673A\u7684\u5E7F\u544A\u8BED\u90A3\u6837\uFF0C\u54EA\u91CC\u52A8\u4E86\u54AC\u54EA\u91CC\uFF0C\u5E76\u4E14\u5BF9\u5B83\u4FE9\u6765\u8BF4\u770B\u8D77\u6765\u7ADF\u662F\u5982\u6B64\u5BB9\u6613\u3002</p><p>\u9996\u5148\u80A2\u89E3\u4E86\u90A3\u4E24\u53EA\u4E0D\u5B89\u4EFD\u7684\u7FC5\u8180\uFF0C\u63A5\u7740\u54AC\u65AD\u4E86&quot;\u62B1\u7740\u6811\u4E0D\u80AF\u6492\u624B\u2033\u7684\u624B\u811A\uFF0C\u6B64\u523B\u7684\u82CD\u8747\u5C31\u8C61\u5F85\u5BB0\u7684\u7F94\u7F8A\uFF0C\u88AB\u4E24\u53EA\u5F3A\u58EE\u7684\u8682\u8681\u5206\u5272\u5F97\u652F\u79BB\u7834\u788E\uFF0C\u773C\u7741\u7741\u5730\u770B\u7740\u81EA\u5DF1\u7684\u9A71\u4F53\u88AB\u8682\u8681\u62AC\u8D70\u800C\u65E0\u80FD\u4E3A\u529B\uFF0C\u6781\u4E0D\u60C5\u613F\u5730\u628A\u81EA\u5DF1\u6367\u4E0A\u4E86\u8682\u8681\u7684\u9910\u684C\u3002</p><p>\u4E5F\u8BB8\u82CD\u8747\u548B\u5929\u8FD8\u5728\u60F3\uFF0C\u5230\u5E95\u660E\u5929\u548C\u610F\u5916\u7A76\u7ADF\u54EA\u4E2A\u5148\u6765\u3002\u5176\u5B9E\u5230\u6B7B\u5B83\u4E5F\u4E0D\u4F1A\u60F3\u5230\uFF0C\u4ECA\u5929\u548C\u8682\u8681\u7684\u7F8E\u4E3D\u9082\u9005\uFF0C\u7ADF\u6210\u4E86\u5B83\u4ECA\u751F\u518D\u4E5F\u65E0\u6CD5\u5F25\u8865\u7684\u610F\u5916\u3002</p><p>\u591A\u4E48\u53EF\u7231\u7684\u5C0F\u751F\u7075\u554A\uFF01\u8682\u8681\u9762\u5BF9\u5982\u6B64\u5E9E\u7136\u5927\u7269\uFF0C\u5C31\u8C61\u300A\u9ED4\u4E4B\u9A74\u300B\u4E2D\u7684\u8001\u864E\uFF0C\u4ECE\u521D\u89C1\u9A74\u65F6\u7684\u53C8\u8E22\u53C8\u54AC\uFF0C\u5230\u6700\u540E\u7684\u65AD\u5176\u5589\uFF0C\u5C3D\u5176\u8089\u3002\u663E\u73B0\u51FA\u7684\u4E0D\u4EC5\u4EC5\u662F\u8682\u8681\u5543\u78BE\u76D8\u7684\u5634\u4E0A\u529F\u592B\uFF0C\u4E5F\u8868\u73B0\u4E86\u5B83\u4EEC\u9F50\u5FC3\u534F\u529B\u3001\u5206\u5DE5\u5408\u4F5C\u3001\u9532\u800C\u4E0D\u820D\u7684\u56E2\u961F\u7CBE\u795E\u3002</p><p>\u5E73\u5E38\u6211\u559C\u6B22\u770B\u8682\u8681\u4E0A\u6811\uFF0C\u5B83\u4EEC\u5C31\u8C61\u4E00\u5E2E\u6F14\u51FA\u8D39\u4E0D\u9AD8\u7684\u7FA4\u4F17\u6F14\u5458\uFF0C\u6709\u65F6\u6BD4\u4E3B\u6F14\u6F14\u7684\u8FD8\u8981\u7CBE\u5F69\u3002</p><p>\u745E\u751F\u4E8E\u4E00\u4E2A\u7F8E\u4E3D\u7684\u65E9\u6668\uFF0C\u4E0E\u4E00\u7FA4\u8682\u8681\u4E0D\u671F\u800C\u9047\uFF0C\u5168\u7A0B\u89C2\u770B\u4E86\u8FD9\u60CA\u5FC3\u52A8\u9B44\u7684\u573A\u9762\u5E76\u8BB0\u5F55\u4E86\u4E0B\u6765\u3002</p></blockquote><p><img src="http://t.eryajf.net/imgs/2021/09/8bdce93395da2f2d.jpg" alt="image-20210717182830405"></p>',3);function o(r,d){return c}var _=e(t,[["render",o]]);export{_ as default};
