import{e as p}from"./app.dbf5d933.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const t={},c=p('<p>\u6574\u7406\u81EA\u7236\u4EB2\u53D1\u7684\u5FAE\u4FE1\u670B\u53CB\u5708\uFF0C\u5206\u4EAB\u65F6\u95F4\u4E3A\uFF1A<code>7\u670819\u65E5</code></p><blockquote><p>\u751F\u7684\u5E73\u5EB8\u3001\u6D3B\u7684\u618B\u5C48\u3001\u6B7B\u7684\u7A9D\u56CA</p><p>\u4EBA\u7684\u4E00\u751F\uFF0C\u8BF4\u957F\u4E0D\u957F\uFF0C\u8BF4\u77ED\u4E0D\u77ED\uFF0C\u5C31\u5982\u540C\u4E58\u5750\u4E00\u5217\u706B\u8F66\uFF0C\u4E2D\u95F4\u6709\u5F88\u591A\u7AD9\u70B9\uFF0C\u65E9\u4E0B\u4E5F\u662F\u4E0B\uFF0C\u665A\u4E0B\u4E5F\u662F\u4E0B\uFF0C\u5173\u952E\u662F\u4F60\u4E0B\u8F66\u7684\u5730\u65B9\u662F\u5426\u4E3A\u4F60\u7684\u4EBA\u751F\u753B\u4E0A\u4E86\u5B8C\u7F8E\u7684\u53E5\u53F7\u3002</p><p>\u5C5E\u4E8E\u4EBA\u7684\u751F\u547D\u53EA\u6709\u4E00\u6B21\uFF0C\u4E0D\u7BA1\u4F60\u662F\u660E\u661F\u5927\u4F3D\uFF0C\u8FD8\u662F\u5E73\u6C11\u767E\u59D3\uFF0C\u6211\u4EEC\u90FD\u4E0D\u80FD\u8C61\u300A\u8D85\u7EA7\u739B\u4E3D\u300B\u4E00\u6837\u9876\u4E00\u767E\u6B21\u7816\u5934\u5C31\u53EF\u4EE5\u83B7\u5F97\u4E00\u6B21\u65B0\u751F\u7684\u673A\u4F1A\u3002</p><p>\u4EBA\u6709\u60B2\u6B22\u79BB\u5408\uFF0C\u6708\u6709\u9634\u6674\u5706\u7F3A\u3002\u5176\u65F6\u6B7B\u5E76\u4E0D\u53EF\u6015\uFF0C\u6709\u91CD\u4E8E\u6CF0\u5C71\u7684\uFF0C\u6709\u8F7B\u4E8E\u9E3F\u6BDB\u7684\uFF0C\u5E73\u5E38\u4EBA\u6B7B\u7684\u4F1F\u4E0D\u4F1F\u5927\u5E76\u4E0D\u91CD\u8981\uFF0C\u5173\u952E\u662F\u4ED6\u6B7B\u7684\u662F\u4E0D\u662F\u5408\u65F6\u5B9C\uFF0C\u662F\u4E0D\u662F\u6B7B\u5F97\u5176\u6240\u3002\u524D\u51E0\u5929\u5728\u6211\u7684\u4EBA\u751F\u65C5\u9014\u4E2D\u5C31\u5DEE\u4E00\u70B9\u4E2D\u9014\u4E0B\u8F66\uFF0C\u8981\u662F\u6B7B\u4E86\u4E5F\u662F\u5C5E\u4E8E\u6B7B\u7684\u7A9D\u56CA\u7684\u90A3\u4E00\u79CD\u3002</p><p>\u9752\u5C71\u542B\u60C5\uFF0C\u7EFF\u6C34\u5E26\u7B11\uFF0C\u7EFF\u8349\u82CD\u82CD\uFF0C\u767D\u96FE\u832B\u832B\uFF0C\u6709\u53E3\u7AA8\u4E95\uFF0C\u5728\u6C34\u4E00\u65B9\uFF0C\u5C31\u662F\u8FD9\u53E3\u7AA8\u4E95\u5DEE\u70B9\u8BA9\u6211\u9B42\u5F52\u6545\u91CC\uFF0C\u68A6\u65AD\u6D59\u6C5F\u3002</p><p>\u6211\u4EEC\u6BCF\u4E2A\u4EBA\u90FD\u5728\u770B\u8FD9\u4E2A\u4E16\u754C\uFF0C\u6709\u7684\u4EBA\u770B\u89C1\u7684\u662F\u4E00\u68F5\u6811\uFF0C\u6709\u7684\u4EBA\u770B\u89C1\u7684\u662F\u4E00\u7247\u68EE\u6797\uFF0C\u6709\u7684\u4EBA\u770B\u89C1\u7684\u662F\u6CE2\u6D9B\u6C79\u6D8C\u7684\u5927\u6D77\uFF0C\u6709\u7684\u4EBA\u5374\u5BF9\u773C\u524D\u5371\u673A\u56DB\u4F0F\u5145\u6EE1\u51F6\u9669\u7684\u7AA8\u4E95\u89C6\u800C\u4E0D\u89C1\uFF0C\u800C\u6211\u5C31\u662F\u90A3\u4E2A\u5DEE\u70B9\u6389\u4E0B\u53BB\u548C\u5927\u5BB6\u8BF4\u201C\u4E09\u53CB\u62FF\u62C9\u201D\u201C\u59D1\u7684\u62DC\u201C\u7684\u4EBA\u3002</p><p>\u5728\u4E00\u4E2A\u6708\u9ED1\u98CE\u9AD8\u591C\u665A\uFF0C\u4F38\u624B\u4E0D\u89C1\u4E94\u6307\uFF0C\u5929\u7A7A\u4E2D\u6DC5\u6DC5\u6CA5\u6CA5\u5730\u4E0B\u7740\u7EC6\u96E8\uFF0C\u6709\u65F6\u5927\uFF0C\u6709\u65F6\u5C0F\uFF0C\u8001\u5929\u7237\u597D\u8C61\u5728\u4E3A\u6211\u5373\u5C06\u4E0A\u6F14\u7684\u60B2\u5267\u800C\u54ED\u6CE3\u3002</p><p>\u7ECF\u8FC7\u4E00\u8DEF\u5954\u6CE2\uFF0C\u6211\u9A71\u8F66\u6765\u5230\u7ECD\u5174\uFF0C\u5DF2\u662F\u665A\u4E0A\u5341\u4E00\u70B9\u5DE6\u53F3\uFF0C\u5230\u76EE\u7684\u5730\u540E\u51C6\u5907\u6CCA\u8F66\u7761\u89C9\uFF0C\u660E\u5929\u4E00\u65E9\u5378\u8D27\u3002 \u7136\u800C\u5C31\u5728\u6211\u4E0B\u8F66\u5C0F\u89E3\u65F6\u5DEE\u70B9\u8981\u4E86\u6211\u7684\u547D\u3002\u5728\u6211\u8FD8\u7B97\u5112\u96C5\u7684\u89C2\u5FF5\u4E2D\uFF0C\u59CB\u7EC8\u6709\u201C\u4E0A\u524D\u4E00\u5C0F\u6B65\uFF0C\u6587\u660E\u4E00\u5927\u6B65\u201D\u7684\u6982\u5FF5\uFF0C\u5F53\u6211\u8DE8\u8FC7\u9053\u4F22\uFF0C\u8E0F\u5165\u8349\u576A\uFF0C\u6211\u6E05\u695A\u7684\u8BB0\u5F97\u5F80\u524D\u8D70\u7B2C\u4E09\u6B65\u7684\u65F6\u5019\uFF0C\u5DE6\u811A\u8E0F\u7A7A\u4E86\uFF0C\u8FD9\u4E5F\u8BB8\u5C31\u662F\u4EBA\u4EEC\u5E38\u8BF4\u7684\u9A6C\u5931\u524D\u8E44\u5427\uFF0C\u771F\u662F\u4EBA\u751F\u65E0\u5E38\uFF0C\u9669\u6076\u65E0\u5904\u4E0D\u5728\uFF0C\u5230\u5904\u90FD\u662F\u9677\u9631\uFF0C\u5728\u4E0D\u7ECF\u610F\u95F4\u4F60\u4E00\u811A\u8E0F\u5165\u7684\u53EF\u80FD\u5C31\u662F\u9B3C\u95E8\u5173\u3002\u970E\u65F6\u95F4\u6574\u4E2A\u8EAB\u5B50\u5411\u5728\u5012\u53BB\uFF0C\u5E78\u4E8F\u5B83\u7684\u76D6\u5B50\u662F\u534A\u906E\u534A\u63A9\uFF0C\u6211\u624D\u6709\u5E78\u6CA1\u6709\u6389\u4E0B\u53BB\uFF0C\u4F46\u662F\u5C0F\u817F\u548C\u5927\u817F\u90FD\u5148\u540E\u6302\u5F69\uFF0C\u5DEE\u4E00\u70B9\u5C31\u6210\u4E86\u771F\u5B9E\u7248\u7684\u4E00\u5931\u8DB3\u800C\u6210\u5343\u53E4\u6068\u3002\u670B\u53CB\u4EEC\u4E0D\u5FC5\u6000\u7591\u5B83\u7684\u771F\u5B9E\u6027\uFF0C\u6709\u56FE\u6709\u771F\u8C61\uFF0C\u5B9E\u4E8B\u5C31\u6446\u5728\u90A3\u91CC\uFF0C\u7EDD\u975E\u51ED\u7A7A\u675C\u64B0\uFF0C\u8C01\u4E5F\u4E0D\u4F1A\u62FF\u5C4E\u76C6\u5B50\u5F80\u81EA\u5DF1\u5934\u4E0A\u6263\u3002</p><p>\u8FD9\u548B\u4E00\u770B\u4E5F\u5C31\u7A00\u677E\u5E73\u5E38\uFF0C\u4E5F\u6CA1\u4EC0\u4E48\u60CA\u5929\u5730\u6CE3\u9B3C\u795E\u7684\uFF0C\u4F46\u4F60\u7EC6\u60F3\u4E4B\u540E\u4E5F\u8BB8\u4F1A\u548C\u6211\u4E00\u6837\u60CA\u51FA\u4E00\u8EAB\u51B7\u6C57\u3002\u65F6\u95F4\u662F\u534A\u591C\uFF0C\u5730\u70B9\u662F\u504F\u50FB\u7684\u516C\u8DEF\uFF0C\u8DEF\u4E0A\u5DF2\u57FA\u672C\u4E0A\u6CA1\u6709\u884C\u4EBA\uFF0C\u5076\u5C14\u8FC7\u4E00\u8F86\u5C0F\u8F66\u4E5F\u662F\u75BE\u9A70\u800C\u8FC7\uFF0C\u5047\u5982\u771F\u6389\u4E0B\u53BB\u4EC5\u51ED\u4E00\u5DF1\u4E4B\u529B\u662F\u51FA\u4E0D\u6765\u7684\uFF0C\u56E0\u4E3A\u7B2C\u4E8C\u5929\u62CD\u7167\u7559\u5FF5\u65F6\uFF0C\u636E\u76EE\u6D4B\u4E95\u6DF1\u6709\u4E8C\u4E08\u5F00\u5916\uFF0C\u4E03\u516B\u7C73\u6DF1\u6CA1\u4EBA\u6551\u662F\u7EDD\u5BF9\u4E0A\u4E0D\u6765\u7684\uFF0C\u90A3\u611F\u89C9\u5C31\u548C\u4E95\u5E95\u4E4B\u86D9\u5750\u4E95\u89C2\u5929\u662F\u4E00\u4E2A\u611F\u89C9\uFF0C\u558A\u6551\u547D\u6709\u4EBA\u542C\u5230\u7684\u6982\u7387\u57FA\u672C\u4E3A\u96F6\uFF0C\u5C31\u8FD9\u6837\u4ECE\u5F00\u59CB\u7684\u5927\u58F0\u75BE\u547C\u3001\u5230\u868A\u866B\u9489\u54AC\u3001\u9A71\u4F53\u75BC\u75DB\u3001\u7531\u5931\u671B\u53D8\u7EDD\u671B\uFF0C\u6700\u540E\u6709\u53EF\u80FD\u5728\u7F3A\u6C27\u7684\u72B6\u6001\u4E0B\u7A92\u606F\u800C\u4EA1\u3002</p><p>\u7B2C\u4E8C\u5929\u4E00\u65E9\u5730\u65B9\u5C0F\u62A5\u6216\u7535\u53F0\u65B0\u95FB\u4E2D\u6709\u53EF\u80FD\u62A5\u5BFC\uFF1A\u67D0\u7537\u6628\u665A\u8BEF\u5165\u7AA8\u4E95\uFF0C\u56E0\u7F3A\u6C27\u800C\u7A92\u606F\u8EAB\u4EA1\uFF0C\u5E0C\u671B\u5E7F\u5927\u5E02\u6C11\u4E0D\u8981\u5149\u987E\u7740\u62AC\u5934\u770B\u5929\uFF0C\u800C\u4E14\u4E5F\u8981\u8BB0\u5F97\u4F4E\u5934\u770B\u8DEF\uFF0C\u8BA9\u6211\u4EEC\u4E0D\u8981\u518D\u91CD\u590D\u90A3\u6628\u5929\u7684\u6545\u4E8B\u3002\u8FD9\u4E5F\u8BB8\u5C31\u662F\u6211\u80FD\u4E3A\u4EBA\u4EEC\u505A\u7684\u6700\u540E\u4E00\u70B9\u8D21\u732E\u5427\uFF01</p><p>\u5C31\u8FD9\u6837\u5728\u6211\u4EBA\u751F\u7684\u5EB7\u5E84\u5927\u9053\u4E0A\u7A33\u6B65\u524D\u884C\u7684\u65F6\u5019\uFF0C\u600E\u4E48\u4E5F\u4E0D\u4F1A\u60F3\u5230\u6709\u5982\u6B64\u4E00\u52AB\uFF0C\u5DEE\u70B9\u5760\u5165\u4E07\u52AB\u4E0D\u590D\u7684\u6DF1\u6E0A\uFF0C\u5047\u5982\u5C31\u6B64\u8C22\u4E16\uFF0C\u5C82\u4E0D\u662F\u5728\u5E73\u5EB8\u7684\u751F\u6D3B\u4E2D\u80CC\u4E0A\u4E86\u7A9D\u56CA\u7684\u7F6A\u540D\u3002</p><p>\u4EBA\u5728\u5012\u7709\u7684\u65F6\u5019\uFF0C\u559D\u53E3\u51C9\u6C34\u4E5F\u4F1A\u585E\u7259\uFF0C\u6211\u4EEC\u867D\u7136\u751F\u4E8E\u5E73\u5EB8\uFF0C\u6D3B\u7684\u4E5F\u4E0D\u6F47\u6D12\uFF0C\u4F46\u6211\u4EEC\u5FC5\u987B\u62D2\u7EDD\u6B7B\u7684\u7A9D\u56CA\u3002\u6B64\u60C5\u6B64\u666F\u4E0D\u7531\u5730\u8BA9\u6211\u60F3\u8D77\u4E00\u9996\u8BD7\uFF1A</p><p>\u76F2\u4EBA\u9A91\u778E\u9A6C</p><p>\u591C\u534A\u4E34\u6DF1\u6C60</p><p>\u6D0B\u6D0B\u81EA\u5F97\u610F</p><p>\u6D51\u7136\u4E0D\u89C9\u9669</p><p>\u5657\u901A\u4E00\u58F0\u54CD</p><p>\u6251\u817E\u51E0\u6251\u817E</p><p>\u8D39\u5C3D\u5468\u6298\u82E6</p><p>\u4EBA\u9A6C\u547D\u76F8\u540C</p><p>\u4F17\u91CC\u5BFB\u4ED6\u5343\u767E\u5EA6</p><p>\u6C34\u6DF1\u4E0D\u77E5\u5728\u4F55\u5904</p><p>\u4E0D\u7ECF\u5386\u98CE\u96E8\uFF0C\u600E\u4E48\u53EF\u80FD\u89C1\u5230\u7F8E\u4E3D\u7684\u5F69\u8679\u3002\u4EBA\u5462\u6709\u65F6\u5019\u751F\u4E00\u573A\u5927\u75C5\u6216\u8005\u7ECF\u5386\u4E00\u6B21\u547D\u8FD0\u4E3A\u4F60\u5F00\u7684\u73A9\u7B11\uFF0C\u4F60\u4E5F\u8BB8\u4F1A\u770B\u5230\u4E0D\u4E00\u6837\u7684\u98CE\u666F\uFF0C\u6700\u5927\u7684\u5962\u671B\u5C31\u662F\u6D3B\u7740\u771F\u597D\u3002</p><p>\u56E0\u4E3A\u5065\u5EB7\u7684\u65F6\u5019\u4E0D\u4F1A\u60F3\u5230\u75C5\u75DB\u7684\u6298\u78E8\uFF0C\u6D3B\u7740\u7684\u65F6\u5019\u4E0D\u4F1A\u60F3\u5230\u6FD2\u4E34\u6B7B\u4EA1\u7684\u7EDD\u671B\uFF0C\u5927\u96BE\u4E0D\u6B7B\u6211\u4EEC\u5FC5\u7136\u4F1A\u611F\u6069\u751F\u6D3B\uFF0C\u4F1A\u89C9\u5F97\u751F\u6D3B\u5C31\u662F\u518D\u82E6\u518D\u7D2F\u90FD\u503C\u5F97\u6D3B\u4E0B\u53BB\uFF0C\u518D\u4E5F\u4E0D\u4F1A\u5ACC\u5F03\u5E73\u51E1\u7684\u81EA\u5DF1\u548C\u5E73\u51E1\u7684\u751F\u6D3B\uFF0C\u518D\u4E5F\u4E0D\u4F1A\u60F3\u7740\u94B1\u6CA1\u8D5A\u591F\uFF0C\u518D\u4E5F\u4E0D\u4F1A\u6F3E\u6155\u522B\u4EBA\u6BD4\u6211\u5403\u7684\u597D\uFF0C\u6211\u6CA1\u522B\u4EBA\u7A7F\u7684\u597D\uFF0C\u4EC0\u4E48\u8C6A\u8F66\u522B\u5885\u4E00\u5207\u90FD\u662F\u6D6E\u4E91\uFF0C\u5F53\u6211\u4EEC\u7ECF\u8FC7\u4E00\u573A\u751F\u4E0E\u6B7B\u7684\u8003\u9A8C\u65F6\uFF0C\u4E5F\u8BB8\u4F60\u5C31\u6D3B\u901A\u900F\u4E86\u3002</p><p>\u6211\u5199\u8FD9\u7BC7\u65E5\u8BB0\u7684\u76EE\u7684\u5E76\u4E0D\u662F\u60F3\u8981\u663E\u6446\u4EC0\u4E48\uFF0C\u4E5F\u4E0D\u662F\u8BF4\u6211\u662F\u4E2A\u8305\u623F\u62C9\u5C4E\u8138\u671D\u5916\u7684\u6C49\u5B50\u2014\u2014\u81ED\u4E0D\u8981\u8138\uFF0C\u6211\u53EA\u662F\u60F3\u6210\u5168\u522B\u4EBA\u6076\u5FC3\u81EA\u5DF1\uFF0C\u4EE5\u6211\u4E3A\u9274\uFF0C\u7136\u540E\u7167\u4EAE\u670B\u53CB\u4EEC\u7684\u524D\u884C\u4E4B\u8DEF\uFF0C\u767D\u5929\u4E0D\u61C2\u591C\u7684\u9ED1\uFF0C\u6478\u8FC7\u9ED1\u8DEF\u7684\u4EBA\u624D\u77E5\u9053\u9633\u5149\u7684\u53EF\u8D35\u3002</p><p>\u6211\u662F\u4ECE\u5317\u8D70\u5230\u5357\uFF0C\u53C8\u4ECE\u767D\u5929\u8D70\u5230\u9ED1\uFF0C\u6211\u60F3\u8BA9\u670B\u53CB\u4EEC\u8BB0\u4F4F\u6211\uFF0C\u5374\u4E0D\u77E5\u9053\u6211\u662F\u8C01\uFF0C\u5047\u5982\u4F60\u770B\u5230\u4E86\u6211\u906D\u7684\u7F6A\uFF0C\u5C31\u8BF7\u4F60\u7ED9\u5012\u7897\u6C34\uFF0C\u56E0\u4E3A\u7AA8\u4E95\u91CC\u786E\u5B9E\u6709\u70B9\u6E34\u4E5F\u6709\u70B9\u7D2F\u3002</p><p>\u670B\u53CB\u4EEC\uFF0C\u6709\u65F6\u5019\u6362\u4E00\u79CD\u6253\u5F00\u65B9\u5F0F\uFF0C\u4E5F\u8BB8\u6211\u4EEC\u7684\u4EBA\u751F\u5C31\u53D8\u5F97\u6674\u6717\u4E86\u3002</p><p>\u57CE\u91CC\u7AA8\u4E95\u6DF1\uFF0C\u8D70\u6709\u987B\u5C0F\u5FC3\uFF0C\u8BA9\u6211\u4EEC\u73CD\u7231\u751F\u547D\uFF0C\u8FDC\u79BB\u7AA8\u4E95\uFF0C\u4ECE\u6211\u505A\u8D77\uFF0C\u5411\u7AA8\u4E95\u8BF4NO\u3002</p><p>\u4E0E\u670B\u53CB\u5708\u91CC\u7684\u670B\u53CB\u4EEC\u5171\u52C9\uFF01</p></blockquote><p><img src="http://t.eryajf.net/imgs/2021/09/55146c966101d354.jpg" alt="image-20210717180710520"></p>',3);function o(r,a){return c}var _=e(t,[["render",o]]);export{_ as default};
